insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (26,'ELF','<b>ELF</b>','<b>Leucovorin calcium</b> 300 mg/m<sup>2</sup>/d IV over 10 minutes, <b>FOLLOWED BY</b><br> <b>Etoposide</b> 120 mg/m<sup>2</sup>/d IV over 50 minutes,<br> <b>THEN</b><br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup>/d IV  bolus<br> Give all agents on days 1-3; repeat every 22 days','Low','Vanhoefer U, Rougier P, Wilke H, et al. Final results of a randomized phase III trial of sequential high-dose methotrexate, fluorouracil, and doxorubicin versus etoposide, leucovorin, and fluorouracil versus infusional flurorouracil and cisplatin in advanced gastric cancer: a trial of the European Organization for Research and Treatment of Cancer Gasttrointestinal Tract Cancer Cooperative Group. $J Clin Oncol. 2000;18(41):2648-2657$','<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fluorouracil:<br>No dose reduction </li></ul>','Etoposide : VP-16<br>Fluorouracil : 5-FU');
