insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (227,'CODOX-M/IVAC','<b>CODOX-M/IVAC</b>','<u>Cycles 1 AND 3 (CODOX-M)</u><br> <b>Cyclophosphamide</b> 800 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 600 mg/m<sup>2</sup>/d IV, days 2-5<br> <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV up to a maximum dose of 2 mg, days 1,8 for cycle 1, days 1,8, 15 for cycle 2<br> <b>Methotrexate</b> 1,200 mg/m<sup>2</sup> IV over 1 hour, day 10,          <b>THEN</b> 240 mg/m<sup>2</sup>/h CIVI for the next 23 hours<br> <b>Leucovorin calcium </b>192 mg/m<sup>2</sup> IV, starting at hour 36  <b>THEN</b> 12 mg/m<sup>2</sup> every 6 hours until Methotrexate level is <5*10<sup>-8</sup> M<br> <b>Filgrastim</b>5 mcg/kg/d SQ until ANC >1,000/mm<sup>3</sup><br> <b>Methotrexate</b> 12mg IT, day 15<br> <b>Leucovorin calcium</b> 15 mg PO 24 hours after Methotrexate, day 16<br>                                                              <u>CNS PROPHYLAXIS</u><br> <b>Cytarabine</b> 70 mg IT, days 1,3,5<br> <b>Methotrexate</b> 12 mg IT, day 12 Cycles 2 AND 4 (IVAC)<br> <b>Ifosfamide</b> 1,500mg/m<sup>2</sup> IV over 1 hour, days 1-5<br> <b>Mesna</b> 360 mg/m<sup>2</sup> IV over 1 hour <b>THEN</b> 360 mg/m<sup>2</sup> IV every 3 hours for a maximum of 7 doses  per 24 hour period, days 1-5<br> <b>Cytarabine</b>  2,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours, days 1 and 2<br> <b>Methotrexate</b> 12 mg IT, day 5<br> <b>Leucovorin calcium</b> 15 mg PO 24 hours after Methotrexate, day 6<br> <b>Filgrastim</b> 5 mcg/kg/d SQ until ANC > 1,000/mm<sup>3</sup>                                                 ','Moderate','Mead GM, Sydes MR, Walewski J, et al. An international evaluation of CODOX-M and CODOX-M alternating with IVAC in adult Burkitt''s lymphoma: results of United Kingdom Lymphoma Group LY06 Study. $Ann Oncol. 2002, 13(8): 1254-1274.$','<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>','Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR');
insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (228,'R - CVP','<b>R - CVP</b>','<b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> up to a maximum dose of 2 mg IV, day 1<br> <b>Prednisone</b> 40 mg PO, days 1-5<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for a maximum off 8 cycles','Moderate','Marcus R, Imrie K, Belch A, et al. CVP chemotherapy plus rituximab compared with CVP as first-line treatment for advanced follicular lymphoma. $Blood, 105(4): 1417-1423.$<br> Marcus R, Imrie K, Solal-Celigny P, et al. Phase III study of R-CVP compared with cyclophosphamide, vincristine and prednisone alone in patients with previously utreated advanced follicular lymphoma. $J Clin Oncol. 2008;26(28):4578-4589.$','<b>Hepatic Impairment</b><ul><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>','Prednisone : various<br>Rituximab : Rituxan<br>Vincristine : Oncovin, VCR');
insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (229,'DHAP','<b>DHAP</b>','<b>Dexamethasone</b> 40 mg/d PO or IV over 24 hours, days 1-4<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> CIVI, day 1<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours for 2 doses, day 2<br> Repeat every 21-28 days','High','Velasquez WS, Cabanillas F, Salvador P, et al. Effective salvage therapy for lymphoma with cisplatin in combination with high-dose Ara-C and dexamethasone (DHAP). $Blood. 1988;71(1):117-122.$','<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>','Cisplatin : CDDP<br>Cytarabine : Ara-C<br>Dexamethasone : various');
insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (230,'ESHAP','<b>ESHAP</b>','<b>Etoposide</b> 40 mg/m<sup>2</sup>/d IV, days 1-4<br> <b>Methylprednisolone</b> 500 mg/d IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 2-3 hours,             day 5<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>OR</b><br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d IV, days 1-4<br> <b>Methylprednisolone</b> 500 mg/d IV, days 1-4<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 2 hours, day 5, immediately following completion of cisplatin<br> Repeat every 21-28 days       ','High','Velasquez WS, McLaughlin P, Tucker S, et al. ESHAP- an effective chemotherapy regimen in refractory and relapsing lymphoma: a 4-year follow-up study. $J Clin Oncol. 1994; 12(6): 1169-1176.$<br> Rodriguez MA, Cabanillas FC, Velasquez W, et al. Results of a salvage treatment program for relapsing lymphoma: MINE consolidated with ESHAP. $J Clin Oncol. 1995; 13(7): 1734-1741.$','<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>','Cisplatin : CDDP<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Methylprednisolone : Depo-Medrol, Solu-Medrol, various');
insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (231,'Hyper-CVAD/MTX-Ara-C','<b>Hyper-CVAD/MTX-Ara-C</b>','<u>COURSE 1</u><br> <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> over 3 hours every 12 hours  for 6 doses , days 1-3<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> over 48 hours after cyclophosphamide, days 4-5<br> <b>Vincristine</b> 2 mg administered 12 hours  after last cyclophosphamide dose, repeated on day 11<br> <b>Dexamethasone</b> 40 mg/d IV or PO,days 1-4,11-14<br> <b>Filgrastim</b> 5 mcg/kg IV or SQ beginning 24 hours after doxorubicin is complete.<br> <u>COURSE 2 <br>(BEGIN AFTER CLINICAL AND HEMATOLOGIC RECOVERY FROM COURSE 1)</u><br> <b>Methotrexate</b> 200 mg/m<sup>2</sup> IV bolus, day 1<br> <b>FOLLOWED BY</b><br> <b>Methotrexate</b> 800mg/m<sup>2</sup>over 24 hours day1 <b>Followed by</b><br><b>Cytarabine</b>3000 mg/m<sup>2</sup> IV every 12 hours for 4 doses, days 2-3 (dose reduced to 1,000 mg/m<sup>2</sup> in patients >60 years or with serum creatinine >1.5 mg/dL)<br> <b>Leucovorin calcium</b> 50 mg PO 24 hours after end of Methotrexate infusion<br> <b>FOLLOWED 6 HOURS LATER BY</b><br> <b>Leucovorin calcium</b> 15 mg PO every 6 hours for 8 doses (dose adjusted for Methotrexate concentration at 24 and 48 hours after completion of Methotrexate infusion)<br> <b>Filgrastim</b> 5mcg/kg IV or SQ begining 24 hours after cytarabine.<br> Repeat courses every 21 days.           ','High','Khouri IF, Romaguera J, Kantrajian H,et al. Hyper-CVAD and high -dose methotrexate/ cytarabine followed by stem-cell tans-plantation: an active regimen for aggressive mantle-cell lymphoma. $J Clin Oncol. 1998; 16(12): 3803-3809$.','<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>','Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Vincristine : Oncovin, VCR');
insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (232,'R-EPOCH','<b>R-EPOCH</b>','<b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> <b>Etoposide</b> 65 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Doxorubicin</b> 15 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Vincristine</b> 0.5 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 5<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-14<br> Repeat every 21 days for 4 -6 cycles','Moderate','Jermann M, jost M, Taverna C, et al. Rituximab EPOCH, an effective salvage therapy for relapsed, refractory or transformed B-cell lymphomas: results of a phase II study. $Ann Oncol. 2004; 15(3):511-516$.','<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>'