insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (233,'RICE','<b>RICE</b>','<b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1 every 14 days<br> <b>Ifosfamide</b> 5,000 mg/m<sup>2</sup> with mesna 5,000 mg/m<sup>2</sup> CIVI over 24 hours, day 4<br> <b>Carboplatin</b> AUC of 5 (maximum 800 mg) IV, day 4<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d CIVI, days 3-5<br> Repeat every 14 days','Moderate','Kewalramani T, Zelenetz AD, Nimer SD, Nimer SD, et al. Rituximab and ICE as second-line therapy before autologous stem cell transplantation for relapsed or primary refractory diffuse large B-cell lymphoma. $Blood. 2004; 103(10):3684-3688.$','<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>','Carboplatin : Paraplatin<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Rituximab : Rituxan');
