insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (247,'CDE','<b>CDE</b>','<b>Cyclophosphamide</b> 200mg/m<sup>2</sup>/d CIVI for 4 days<br><b>Doxorubicin</b> 12.5 mg/m<sup>2</sup>/d CIVI for 4 days<br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d CIVI for 4 days<br> Repeat every 28 days for a maximum of 8 cycles','Moderate','Sparano JA,Lee S, Chen MG, et al. Phase II trial of   infusional, cyclophosphamide, doxorubicin, and etoposide in patients with HIV-associated non-Hodgkins lymphoma: an Eastern Cooperative Oncology Group Trial (E1491). $J Clin Oncol. 2004; 22(8): 1491-1500.$','<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>','Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16');
insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (248,'CHOP','<b>CHOP</b>','<b>Cyclophosphamide</b> 750mg/m<sup>2</sup> IV, day 1<br> Doxorubicin 50 mg/m<sup>2</sup> IV, day 1<br><b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 1<br> <b>Prednisone</b> 100mg/m<sup>2</sup> PO, days 1-5<br> Repeat every 21 days.','Moderate','Weiss R, Mitrou P, Arasteh K, et.al. Acquired immunodeficiency syndrome-related lymphoma: Simultaneous treatment with combined cyclophosphamide, doxorubicin, Vincristine, and prednisone chemotherapy and highly active antiretroviral therapy is safe and improves survival--results of the German Multicenter Trial. $Cancer. 2006; 106(7): 1560-1568.$','<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>','Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR');
insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (249,'CODOX-M/IVAC','<b>CODOX-M/IVAC</b>','<u>CYCLES 1 and 3 (CODOX-M)</u><br>  <b>Cyclophosphamide</b> 800 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 200 mg/m<sup>2</sup>/d IV, days 2-5<br>  <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 1, 8 for cycle 1,days 1, 8, 15 for cycle 2<br> <b>Methotrexate</b> 1,200 mg/m<sup>2</sup> IV over 1 h, day 10, <b>THEN</b> 240 mg/m2/h CIVI for the next 23 hours<br> <b>Leucovorin calcium</b> 50 mg IV q6h, begining 36 hours from the start of Methotrexate until Methotrexate level <0.05 mcM<br> <b>Filgrastim</b> beginning 24 h from the start of leucovorin until ANC>1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Cytarabine</b> 70 mg IT, days 1, 3<br> <b>Methotrexate<b> 12 mg IT, day 15<br> <u>CYCLES 2 AND 4 (IVAC)</u><br> <b>Ifosofamide</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Mesna</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Etoposide</b> 60 mg/m<sup>2</sup> IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV q 12h, days 1 and 2<br> <b>Filgrastim</b> beginning 24 h after completion of chemotherapy until ANC >1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Methotrexate</b> 12 mg IT, day 15<br> Additional IT Treatment for CNS disease- positive patients:<br> Cycle 1: cytarabine 70 mg IT, days 1,3,5, and Methotrexate 12 mg IT, days 15,17<br> Cycle 3: cytarabine 70 mg IT, days 1,3, and Methotrexate 12 mg IT, day 15                                                                                                                                                              ','Moderate','Wang ES, Strauss DJ, Teruya-Feldstein J, et al. Intensive chemotherapy with cyclophosphosphamide, doxorucibicin, high-dose methotrecate/ ifosfamide, etoposide, and high-dose cytarabine (CODOX-M/IVAC)for human immunodeficiency virus-associated Burkitt lymphoma. 