insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (282,'Bortezomib/Thalidomide/Dexamethasone (VTD)','<b>Bortezomib/Thalidomide/Dexamethasone (VTD)</b>','<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV bolus, days 1, 4, 8, and 11<br> <b>Thalidomide</b> 100 mg PO daily for 14 days and 200 mg daily thereafter<br> <b>Dexamethasone</b> 40 mg PO daily on days 1, 2, 4, 5, 8, 9, 11, and 12; total of 320 mg per cycle','Low','Cazo M, Tacchetti P, Patriarca F, et al. Bortezomib with thalidomide plus dexamethasone compared with thalidomide plus dexamethasone as induction therapy before, and consolidation therapy after, double autologous stem-cell transplantation in newly diagnosed multiple myeloma: a randomised phase 3 study. $Lancet. 2010;18;376(9758):2075-2085.$','<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>','Bortezomib : Velcade<br>Thalidomide : Thalomid');
insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (283,'CyBorD','<b>CyBorD</b>','<b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> PO days 1, 8, 15, 22<br> <b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV on days 1, 4, 8, and 11<br> <b>Dexamethasone</b> 40 mg PO days 1-4, 9-12, and 17-20<br> Repeat every 28 days','Moderate-High','Khan ML, Reeder CB, Kumar SK, et al. A comparison af lenalidomide/dexamethasone versus cyclophosphamide/lenalidomide/dexamethasone versus cyclophosphamide/bortezomib/dexamethansone in newly diagnosed multiple myeloma. $Br J Haematol. 2011 [Epub ahead of print].$','<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li></ul>','Bortezomib : Velcade<br>Cyclophosphamide : Cytoxan<br>Dexamethasone : various');
insert into regimen_detail (id, disp_name, name, schedule, emetogenic_potential,reference, dosage_modifications, brand_names) values (284,'DT-PACE','<b>DT-PACE</b>','<b>Dexamethasone</b> 40 mg PO, days 1-4<br> <b> Thalidomide</b> 400 mg/d PO<br> <b>Cisplatin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Doxorubicin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Etoposide</b> 40 mg/m<sup>2</sup>/d CIVI, days 1-4','High','Lee CK, Barlogie B, Munshi N, et al. DTPACE: an effective, novel combination chemotherapy with thalidomide for previously treated patients with myeloma. $J Clin Oncol. 2003;21(14):2732-2739.$','<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>','Cisplatin : CDDP<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Thalidomide : Thalomid');
