insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1, 'Bevacizumab : Avastin', 'Bevacizumab/Irinotecan', '<b>Hepatic Impairment</b><ul><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Irinotecan:<br>No dose reduction </li></ul>', 'Moderate', '<b>Bevacizumab/Irinotecan</b>', 'Vredenburgh JJ, Desjardins A, Herndon JE, et al. Bevacizumab plus irinotecan in recurrent glioblastoma multiforme. $J Clin Oncol. 2007;25(30):4722-4279.$', null, '<b>Bevacizumab</b> 15 mg/kg IV<br> Repeat Every 21 Days<br> <b>Irinotecan</b> 350 mg/m<sup>2</sup> with enzyme-inducing antiepileptic drugs, days 1, 8, 22, 29<br> <b>OR</b><br> <b>Irinotecan</b> 125 mg/m<sup>2</sup> with non-enzyme-including antiepileptic drugs, IV, days 1, 8, 22, 29<br> Repeat every 6 weeks', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (2, 'Lomustine : CCNU<br>Procarbazine : Matulane<br>Vincristine : Oncovin, VCR', 'PCV', '<b>Hepatic Impairment</b><ul><li>Lomustine:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Lomustine:<br>If CrCl < 60 mL/min, do not use</li><li>Procarbazine:<br>If CrCl < 30 mL/min, do not use</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate-High', '<b>PCV</b>', 'Levin VA, Uhm JH, Jaeckle KA, et al. Phase III randomized study of postradiotherapy chemotherapy with alpha-difluromethylornithine- procarbazine, N-(2-chloroethyl)-N'' -cyclohexyl- N-nitrosuera, vincristine (DFMO-PCV) versus PCV for glioblastoma multiforme. $Clin Cancer Res.2000;6(10):3878-3884.$', null, '<b>Procarbazine</b> 60 mg/m<sup>2</sup>/d PO, days 8-21<br> <b>Lomustine</b> 110 mg/m<sup>2</sup> PO, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, days 8, 29<br> Repeat every 6 weeks upto 7 cycles ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (3, 'Temozolomide : Temodar', 'Temozolomide+RT', '<b>Hepatic Impairment</b><ul><li>Temozolomide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Moderate for IV<br> <=75 mg/m<sup>2</sup> PO is minimal-low;<br> >75 mg/m<sup>2</sup> PO is moderate-high   ', '<b>Temozolomide+RT</b>', 'Stupp R, et al.  Radiotherapy plus Concomitant and Adjuvant Temozolomide for Glioblastoma.$N Engl J Med 2005; 352:987-996$', null, '<b>Temozolomide</b> 75 mg/m<sup>2</sup> daily along with radiation<br> <b>FOLLOWED 28 DAYS LATER BY</b><br><b>Temozolomide<sup>e</sup></b> 150 mg/m<sup>2</sup> daily for 5 days<br>Repeat every 28 days.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (4, 'Carmustine : BCNU', 'BCNU (Carmustine)', '<b>Hepatic Impairment</b><ul><li>Carmustine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carmustine:<br>If CrCl < 60 mL/min, do not use</li></ul>', 'High', '<b>BCNU (Carmustine)</b>', 'Selker RG, Sharipo WR, Burger p, et al. The Brain tumor cooperative group NIH Trial 87-01: a randomized comparision of surgery, external radiotherapy, and carmustine versus surgery, interstital radiotherapy, and carmustine. $Neurosurgery.2002;51(2):343-355; discussion 355-357.$', null, '<b>Carmustine</b> 200 mg/m<sup>2</sup> IV over 30-60 minutes<br> Repeat every 8 weeks ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (5, 'Bevacizumab : Avastin', 'Bevacizumab', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Bevacizumab</b>', 'Kreisl TN, Kim L, Moore K, et al. Phase II trial of single-agent bevacizumab plus irinotecan at tumor progression in recuurent glioblastoma. $J Clin Oncol. 2009;27(5):740-745.$                                                           Raizer JJ, Grimm S, et al. Aphase 2 trial of single-agent bevacizumab plus irinotecan at tumor progression in recurrent high- grade schedule for patients with recurrent high- grade gliomas. $Cancer.2010;116(22):597-5305.$', null, '<b>Bevacizumab</b> 10 mg/kg IV<br> Repeat every 14 days on a 28-day cycle<br><b>OR</b><br><b>Bevacizumab</b> 15 mg/kg IV<br> Repeat every 21 days ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (6, 'Temozolomide : Temodar', 'Temozolomide', '<b>Hepatic Impairment</b><ul><li>Temozolomide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Moderate for IV<br> >75 mg/m<sup>2</sup> PO is moderate-high   ', '<b>Temozolomide</b>', 'Gállego Pérez-Larraya J, et al.Temozolomide in elderly patients with newly diagnosed glioblastoma and poor performance status: an ANOCEF phase II trial.$J Clin Oncol. 2011 Aug 1;29(22):3050-5. doi: 10.1200/JCO.2011.34.8086. Epub 2011 Jun 27$', null, '<b>Temozolomide</b> 150 mg/m<sup>2</sup> every day for 5 days per 28-day cycle<br> ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (7, 'Carboplatin : Paraplatin<br>Cetuximab : Erbitux<br>Fluorouracil : 5-FU', 'Carboplatin/Fluorouracil/Cetuximab', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Moderate', '<b>Carboplatin/Fluorouracil/Cetuximab</b>', 'Vermorken JB, Mesia R, Rivera F, et al. Platinum-based chemotherapy plus cetuximab in head and neck cancer. $N Engl J Med. 2008;359(11):1116-1127.$', null, '<b>Carboplatin</b> AUC of 5, day 1<br> <b>Fluorouracil</b> 1,000 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days for a maximum of 6 cycles<br> <b>Cetuximab</b> 400 mg/m<sup>2</sup> IV load over 4 hours, day 1, cycle 1 <b>THEN</b> 250 mg/m<sup>2</sup> IV over 1 hour, every week.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (8, 'Cisplatin : CDDP', 'CF', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'High', '<b>CF</b>', 'Gibson MK, Li Y, Murphy B, et al. Randomized phase III evaluation of cisplatin plus fluorouracil versus cisplatin plus paclitaxel in advanced head and neck cancer (E1395): an intergroup trial of the Eastern Cooperative Oncology Group. $J Clin Oncol. 2005;23(15):3562-3567.$', null, '<b>Cisplatin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>5-Fluorouracil</b> 1000 mg/m<sup>2</sup>/day CIVI, days 1-4<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (9, 'Cetuximab : Erbitux<br>Cisplatin : CDDP<br>Fluorouracil : 5-FU', 'Cisplatin/Fluorouracil/Cetuximab', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'High', '<b>Cisplatin/Fluorouracil/Cetuximab</b>', 'Vermorken JB, Mesia R, Rivera F, et al. Platinum-based chemotherapy plus cetuximab in head and neck cancer. $N Engl J Med. 2008;359(11):1116-1127.$', null, '<b>Cisplatin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 1,000 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days for a maximuum of 6 cycles<br> <b>Cetuximab</b> 400 mg/m<sup>2</sup> IV load over 4 hours, day 1, cycle 1. <b>THEN</b> 250 mg/m<sup>2</sup> IV over 1 hour, every week', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (138, 'Anastrozole : Arimidex', 'Anastrozole', '<b>Hepatic Impairment</b><ul><li>Anastrozole:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Anastrozole:<br>No dose reduction </li></ul>', 'N/A', '<b>Anastrozole</b>', 'Arimidex [package insert]. $Wilmington, DE: AstraZeneca Pharmaceuticals LP; 2011.$', null, '<b>Anastrozole</b> 1 mg PO  once daily', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (10, 'Cisplatin : CDDP', 'Cisplatin + radiation', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'High', '<b>Cisplatin + radiation</b>', 'Franchin G, Minatel E, Politi D, et al. Postoperative reduced dose of cisplatin concomitant with radiatoin therapy in high-risk head and neck squamous cell carcinoma. $Cancer. 2009;115(11):2464-2471.$', null, '<b>Cisplatin</b> 75 mg/m<sup>2</sup>  IV, days 1,22, 43 concomitantly with radiation', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (11, 'Cisplatin : CDDP<br>Paclitaxel : Taxol', 'CP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'High', '<b>CP</b>', 'Gibson MK, Li Y, Murphy B, et al. Randomized phase III evaluation of cisplatin plus fluorouracil versus cisplatin plus paclitaxel in advanced head and neck cancer (E1395): an intergroup trial of the Eastern Cooperative Oncology Group. $J Clin Oncol. 2005;23(15):3562-3567.$', null, '<b>Cisplatin</b> 75 mg/m<sup>2</sup>, day 1 and<br> <b>Paclitaxel</b> 175 mg/m<sup>2</sup> over 3 hours on day 1<br> Repeat cycle every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (12, 'Cisplatin : CDDP<br>Docetaxel : Taxotere', 'TPF', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction </li></ul>', 'High', '<b>TPF</b>', 'Vermorken JB, Remenar E, Van Herpen C, et al. Cisplatin, fluorouracil, and docetaxel in unresectable head and neck cancer. $N Engl J Med. 2007;357(17):1695-1704.$<br> Posner MR, Hershock DM, Blajman CR, et al. Cisplatin and fluorouracil alone or with docetaxel in head and neck cancer. $N Engl J Med. 2007;357(17):1705-1715.$', null, '<b>Docetaxel<sup>b</sup></b> 75mg/m<sup>2</sup> IV over 1 hour, day1<br> <b>Cisplatin</b> 75mg/m<sup>2</sup> IV over 1 hour, day 1<br> <b>Fluorouraacil</b> 750 mg/m<sup>2</sup>/d, days 1-5<br> Repeat every 21 days<br> <b>OR</b><br> <b>Docetaxel<sup>b</sup></b> 75 mg/m<sup>2</sup> IV, day 1<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 1,000 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days for 3 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (13, 'Cetuximab : Erbitux', 'Cetuximab', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Cetuximab</b>', 'Bonner JA, Harari PM, Giralt J, et al. Radiotherapy plus cetuximab for locoregionally advanced head and neck cancer: 5-year survival datat from a phase 3 randomised trial, and relation between cetuximab-induced rash and surival. $Lancet Oncol.2010;11(1):21-28.$', null, '<b>Cetuximab</b> 400 mg/m<sup>2</sup> loading dose over 120 minutes, <b>THEN</b> 250 mg/m<sup>2</sup> over 60 minutes, every 7 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (14, 'Cisplatin : CDDP<br>Fluorouracil : 5-FU', '5-Fluorouracil + Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'High', '<b>5-Fluorouracil + Cisplatin</b>', '$Kies MS, et al. Cancer 1987;60:2156-2160.$', null, '<b>5-Fluorouracil</b>1,000 mg/m<sup>2</sup>/day IV continuous infusion on days 1-5<br><b>Cisplatin</b>100 mg/m<sup>2</sup> IV on day 1<br>Repeat cycle on weeks 1, 5, 8, and 11.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (15, 'Cisplatin : CDDP', 'Irinotecan + Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Irinotecan:<br>No dose reduction </li></ul>', 'High', '<b>Irinotecan + Cisplatin</b>', '$Ilson DH, et al. J Clin Oncol 1999;17:3270-3275.$', null, '<b>Irinotecan</b>65 mg/m<sup>2</sup> IV weekly for 4 weeks<br><b>Cisplatin</b>30 mg/m<sup>2</sup> IV weekly for 4 weeks<br>Repeat cycle every 6 weeks.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (16, 'Cisplatin : CDDP<br>Paclitaxel : Taxol', 'Paclitaxel + Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'High', '<b>Paclitaxel + Cisplatin</b>', '$Ilson DH, et al. J Clin Oncol 1998;16:1826-1834.$', null, '<b>Paclitaxel</b>200 mg/m<sup>2</sup> IV over 24 hours on day 1<br><b>Cisplatin</b>75 mg/m<sup>2</sup> IV on day 2<br>Repeat cycle every 21 days. G-CSF support is recommended.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (17, 'Capecitabine : Xeloda<br>Oxaliplatin : Eloxatin, Oxaliplatin, various', 'Capecitabine + Oxaliplatin', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Moderate', '<b>Capecitabine + Oxaliplatin</b>', '$Van Meerten E, et al. Br J Cancer 2007;96:1348-1352.$', null, '<b>Capecitabine</b>1,000 mg/m<sup>2</sup> PO bid on days 1-14<br><b>Oxaliplatin</b>130 mg/m<sup>2</sup> IV on day 1<br>Repeat cycle every 21 days.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (18, 'Cisplatin : CDDP<br>Epirubicin : Ellence, various', 'ECF', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'High', '<b>ECF</b>', '$Cunningham D, et al. N Engl J Med 2008;358:36-46.$', null, '<b>Epirubicin</b>50 mg/m<sup>2</sup> IV on day 1<br><b>Cisplatin</b>60 mg/m<sup>2</sup> IV on day 1<br><b>5-Fluorouracil</b>200 mg/m<sup>2</sup>/day IV continuous infusion for 24 weeks.<br>Repeat cycle every 21 days.', null);
