insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (21, 'Capecitabine : Xeloda<br>Epirubicin : Ellence, various<br>Oxaliplatin : Eloxatin, Oxaliplatin, various', 'EOX', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Moderate', '<b>EOX</b>', '$Cunningham D, et al. N Engl J Med 2008;358:36-46.$', null, '<b>Epirubicin</b>50 mg/m<sup>2</sup> IV on day 1<br><b>Oxaliplatin</b>130 mg/m<sup>2</sup> IV on day 1<br><b>Capecitabine</b>625 mg/m<sup>2</sup> PO bid times 21 days<br>Repeat cycle every 21 days.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (22, 'Paclitaxel : Taxol', 'Paclitaxel', '<b>Hepatic Impairment</b><ul><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Paclitaxel</b>', '$Ajani JA, et al. Semin Oncol 1995;22 (Suppl 6):35-40.$', null, '<b>Paclitaxel</b>250 mg/m<sup>2</sup> IV over 24 hours on day 1<br>Repeat cycle every 21 days. G-CSF support is recommended.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (23, 'Cisplatin : CDDP<br>Docetaxel : Taxotere<br>Fluorouracil : 5-FU', 'DCF', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'High', '<b>DCF</b>', 'Van Custem E, Moiseyenko VM, Tjulandin S, et al. Phase III study of docetaxel and cisplatin plus florouracil compared with cisplatin and flouorouracil as first-line therapy for advanced gastric cancer: a report of the V325 Study Group. $J Clin Oncol. 2006;24(31):4991-4997$', null, '<b>Docetaxel</b> 75mg/m<sup>2</sup> IV over 1 hour, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV over 1-3 hours, day 1<br> <b>Fluorouracil</b> 750 mg/m<sup>2</sup>/d CIVI, days 1-5<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (24, 'Cisplatin : CDDP<br>Docetaxel : Taxotere', 'Docetaxel/Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction </li></ul>', 'High', '<b>Docetaxel/Cisplatin</b>', 'Roth AD, Maibach R, Martinelli G, et al. Docetaxel (Taxotere)-cisplatin (TC): an effective drug combination in gastric carcinoma. $Ann Oncol. 2000;11(3):301-306$', null, '<b>Docetaxel</b> 85 mg/m<sup>2</sup> IV over 1 hour, day 1,<br> <b>FOLLOWED BY</b><br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV over 1 hour, day 1<br> Repeat every 21 days for a maximum of 8 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (25, 'Cisplatin : CDDP<br>Epirubicin : Ellence, various', 'ECF', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'High', '<b>ECF</b>', 'Waters JS, Norman A, Cunningham D, et al. Long-term survival afer epirubicin, cisplatin and fluorouracil for gastric cancer: results of a randomized trial. $Br J Cancer. 1999;80(1-2):269-272.$', null, '<b>Epirubicin</b> 50 mg/m<sup>2</sup> IV, day 1<br> <b>Cisplatin</b> 60 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 200 mg/m<sup>2</sup>/d CIVI, for up to 6 months<br> Repeat every 21 days for a maximum of 8 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (26, 'Etoposide : VP-16<br>Fluorouracil : 5-FU', 'ELF', '<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Low', '<b>ELF</b>', 'Vanhoefer U, Rougier P, Wilke H, et al. Final results of a randomized phase III trial of sequential high-dose methotrexate, fluorouracil, and doxorubicin versus etoposide, leucovorin, and fluorouracil versus infusional flurorouracil and cisplatin in advanced gastric cancer: a trial of the European Organization for Research and Treatment of Cancer Gasttrointestinal Tract Cancer Cooperative Group. $J Clin Oncol. 2000;18(41):2648-2657$', null, '<b>Leucovorin calcium</b> 300 mg/m<sup>2</sup>/d IV over 10 minutes, <b>FOLLOWED BY</b><br> <b>Etoposide</b> 120 mg/m<sup>2</sup>/d IV over 50 minutes,<br> <b>THEN</b><br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup>/d IV  bolus<br> Give all agents on days 1-3; repeat every 22 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (27, 'Fluorouracil : 5-FU', '5-FU/LV and Radiation', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Low', '<b>5-FU/LV and Radiation</b>', 'Macdonald JS, Smalley SR, Benedetti J, et al. Chemoradiotherapy afer surgery compared with surgery alone for adenocarcinoma of the stomach or gastroesophageal junction. $N Engl J Med. 2001;345(10):725-730.$', null, '<b>Fluorouracil</b> 425 mg/m<sup>2</sup>/d for 5 days<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d for 5 days,<br> <b>FOLLOWED BY</b><br> <b>RT</b> 4,500 cGy (180cGy/d), 5 days a week for 5 weeks<br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup>/d IV, on the first 4 and last 3 days of RT<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d, on the first 4 and last 3 days of RT,<br> <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 425 mg/m<sup>2</sup>/d for 5 days<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d for 5 days<br>repeat 28 days X 2 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (28, 'Capecitabine : Xeloda<br>Cisplatin : CDDP<br>Fluorouracil : 5-FU<br>Trastuzumab : Herceptin', 'Trastuzumab + Cisplatin + Capecitabine or Fluorouracil', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'High day 1, low for the remaining treatment days', '<b>Trastuzumab + Cisplatin + Capecitabine or Fluorouracil</b>', 'Bang YJ, Van Cutsem E, Feyereislava A, et al. Trastuzumab in combination with chemotherapy versus chemotherapy alone for treatment of HER2-positive advanced gastric or gastro-oesophageal junction cancer (ToGA): a phase 3 open-label, randomised controlled trial. $Lancet. 2010;376(99742):687-697.$', null, '<b>Trastuzumab</b> 8 mg/kg IV, day 1 of the first cycle <b>THEN</b>  6 mg/kg IV, day 1<br><br> <b>Cisplatin</b> 80 mg/m<sup>2</sup> IV, day 1. <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14 <br> Repeat  every 21 days<br> <b>OR</b><br> <b>Fluorouracil</b> 800 mg/m<sup>2</sup>/d CIVI for 5 days of each 3-week cycle<br> Repeat for 6 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (31, 'Gemcitabine : Gemzar, various<br>Oxaliplatin : Eloxatin, Oxaliplatin, various', 'GEMOX', '<b>Hepatic Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Moderate', '<b>GEMOX</b>', '$Taieb J, et al. Cancer 2003;98:2664-2670.$', null, '<b>Gemcitabine</b>1,000 mg/m<sup>2</sup> IV on day 1<br><b>Oxaliplatin</b>100 mg/m<sup>2</sup> IV on day 2<br>Repeat cycle every 2 weeks.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (32, 'Oxaliplatin : Eloxatin, Oxaliplatin, various', 'FOLFOX4', '<b>Hepatic Impairment</b><ul><li>Leucovorin:<br>No dose reduction</li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Leucovorin:<br>No dose reduction</li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Moderate', '<b>FOLFOX4', '$Qin S, et al. J Clin Oncol 2010;28:303S (abstract 408).$', null, '<b>Oxaliplatin</b>85 mg/m<sup>2</sup> IV on day 1<br><b>5-Fluorouracil</b>400 mg/m<sup>2</sup> IV bolus, followed by 600 mg/m<sup>2</sup> IV continuous infusio for 22 hours on days 1 and 2<br><b>Leucovorin</b>200 mg/m<sup>2</sup> IV on days 1 and 2 as a 2-hour infusion before 5-fluorouracil<br>Repeat cycle every 2 weeks.', null);
