insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (48, 'Fluorouracil : 5-FU<br>Oxaliplatin : Eloxatin, Oxaliplatin, various', 'FOLFIRINOX', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li><li>Irinotecan:<br>No dose reduction </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Moderate', '<b>FOLFIRINOX</b>', 'Conroy T, Desseigne F, Ychou M, et al. FOLFIRINOX versus gemcitabine for metastatic pancreatic cancer. $N Engl J Med. 2011;364(19):1817-1825.$', null, '<b>Oxaliplatin</b> 85 mg/m<sup>2</sup> IV over 2 hours <b>FOLLOWED BY Leucovorin calcium</b> 400 mg/m<sup>2</sup> IV over 2 hours with the addition, after 30 minutes <b>Irinotecan</b> 180 mg/m<sup>2</sup> IV over 90 minutes; <b>IMMEDIATELY FOLLOWED BY</b><br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup> IV bolus, <b>THEN</b><br> <b>Fluorouracil</b> 2,400 mg/m<sup>2</sup> IV over 46 hours<br> Repeat cycle every 2 weeks', null);
