insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (189, 'Docetaxel : Taxotere<br>Prednisone : various', 'Docetaxel/Prednisone', '<b>Hepatic Impairment</b><ul><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Docetaxel:<br>No dose reduction </li></ul>', 'Low', '<b>Docetaxel/Prednisone</b>', 'Tannock IF, de Wit R, Berry WR, et al. Docetaxel plus prednisone or mitoxantrone plus prednisone for advanced prostate cancer. $N Engl J Med. 2004;351(15):1502-1512.$', null, '<b>Docetaxel</b> 75 mg/m<sup>2</sup> IV over 1 hour, day 1<br> <b>Prednisone</b> 5 mg PO bid, days 1-21<br> Repeat every 21 days', null);
