insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (91, 'Crizotinib : Xalkori', 'Crizotinib', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Moderate-High', '<b>Crizotinib</b>', 'Kwak EL, Bang YJ, Camidge DR, et al. Anaplastic Lymphoma Kinase inhabitation in non-small-cell lung cancer. $N engl J Med.2010;363(18):1693-1703.$<br> Xalkori [packages insert]. $New York, NY: Pfizer labs;2011.$', null, '<b>Crizotinib</b> 250 mg PO bid in 28-day cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (92, 'Docetaxel : Taxotere', 'Docetaxel', '<b>Hepatic Impairment</b><ul><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Docetaxel:<br>No dose reduction </li></ul>', 'Low', '<b>Docetaxel</b>', 'Shepherd FA, Dancey J, Ramlau R, et al. Prospective randomized trial of docetaxel versus best supportive care in patients with non-small-cell lung cancer previously treated with platinum-based chemotherapy. $J Clin Oncol. 2000;18(10):2095-2103.$<br> Fossella FV, DeVore R, Kerr RN, et al. Randomized phase III trial of docetaxel versus vinorelbine or ifosfamide in patients with advanced non-small-lung cancer previously treated with platinum-containing chemotherapy regimens. $J Clin Oncol. 2000;18(12):2354-2362.$', null, '<b>Docetaxel</b> 75mg/m<sup>2</sup> IV over 1hour.<br> Repeat every 21 days', null);
