insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (94, 'Gemcitabine : Gemzar, various', 'Gemcitabine', '<b>Hepatic Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Low', '<b>Gemcitabine</b>', 'Crino L, Mosconi AM, Scaglotti G, et al.              Gemcitabine as second-line treatment for advanced non-small-cell lung cancer: a phase II trial. $J Clin Oncol. 1999;17(7):2081-2085.$<br> Ricci S, Anonuzzo A, Galli L, et al. Gemcitabine monotherapy in elderly paients with advanced non-smell-cell lung cancer: a multicenter phase II study. $Lung Cancer.2000;27(2):75-80.$', null, '<b>Gemcitabine</b> 1000 mg/m<sup>2</sup>/d IV over 30 minutes, day 1,8,15<br> <br>  Repeat every 28 days.', null);
