insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (97, 'Carboplatin : Paraplatin', 'Carboplatin/Irinotecan', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Irinotecan:<br>No dose reduction </li></ul>', 'Moderate', '<b>Carboplatin/Irinotecan</b>', 'Hermes A, Bergman B, Bremnes R, et al           Irinotecan plus carboplatin in extensive versus oral etoposide plus carboplatin in extensive small-cell lung cancer: a randomized phase III trial. $J Clin Oncol. 2008:26(26):4261-4267.$', null, '<b>Carboplatin</b> AUC of 5 IV, day 1<br> <b>Irinotecan</b> 175mg/m<sup>2</sup> IV, day 1<br> Repeat every 21days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (98, 'Cisplatin : CDDP', 'Cisplatin + CPT-11', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Irinotecan:<br>No dose reduction </li></ul>', 'High', '<b>Cisplatin + CPT-11</b>', 'Noda K, Nishiwaki Y, Kawahara M, et al.                     Irinotecan plus cisplatin compared with etoposide plus cisplatin for extensive small-cell lung cancer. $N Engl J Med. 2002;346(2):85-91.$', null, '<b>Cisplatin</b> 60mg/m<sup>2</sup>, day 1<br> <b>Irinotecan</b> 60mg/m<sup>2</sup>/d, days 1, 8, 15<br> Repeat every 28days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (190, 'Flutamide : various', 'FL', '<b>Hepatic Impairment</b><ul><li>Leuprolide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'N/A', '<b>FL</b>', 'Sarosdy MF, Schellhammer PF, Johnson R, Carroll K, Kolvenbag GJCM. Does prolonged combined androgen blockade have survival benefits over short-term combined androgen blockade therapy? $Urology. 2000;55(3):391-396.$', null, '<b>Flutamide</b> 250 mg PO tid <b>WITH</b><br> <b>Leuprolide acetate depot</b> 7.5 mg IM<br> Repeat every 28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (99, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'CAV', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>CAV</b>', '$Roth  BJ, et al. J Clin Oncol 1992;10:282-291.$', null, '<b>Cyclophosphamide</b>  1,000 mg/m<sup>2</sup>IV on day 1.  <br>                                              <b>Doxorubicin</b> 40 mg/m<sup>2</sup>IV on day 1.<br>                                                                <b>Vincristine</b> 1 mg/m<sup>2</sup>IV on day 1. (maximum, 2 mg) <br>Repeat cycle every 21 days.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (100, 'Carboplatin : Paraplatin<br>Paclitaxel : Taxol', 'Carboplatin + Paclitaxel', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Carboplatin + Paclitaxel</b>', '$Neubauer M, et al. J Clin Oncol2004;22:1872-1877$', null, '<b>Carboplatin</b>AUC of 2, IV on days1,8, and 15.<br><b>Paclitaxel</b> 80 mg/m<sup>2</sup> IV on days 1,8, and 15. Repeat cycle every 28 days.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (101, 'Carboplatin : Paraplatin<br>Etoposide : VP-16', 'EC', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Moderate', '<b>EC</b>', 'Viren M, Liippo K, Ojala a, et al. Carboplatin and etoposide in extensive small-cell lung cancer. $Acta Oncal. 1994;33(8):921-924.$', null, '<b>Etoposide</b> 100mg/m<sup>2</sup>/d IV, days 1-3<br><b>Carboplatin</b> 450mg/m<sup>2</sup> IV, day 1<br> Repeat every 28 days for 6 cycles', null);
