insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (103, 'Etoposide : VP-16', 'Etoposide', '<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Low', '<b>Etoposide</b>', 'Johnson DH. Recent developments in chemotherapy treatment of small-cell lung cancer. $Semin Oncol. 1993;20(4):315-325.$', null, '<b>Etoposide</b> 160 mg/m<sup>2</sup>/d PO, days 1-5<br> Repeat every 28 days<br> <b>OR</b><br> <b>Etoposide</b> 50 mg PO bid for 14 days<br> Repeat every 21 days', null);
