insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (106, 'Topotecan : Hycamtin', 'Topotecan PO', '<b>Hepatic Impairment</b><ul><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'Low', '<b>Topotecan PO</b>', 'Eckardt JR, von Pawel J, Pujol JL, et al. Phase III study of oral compared with intravenous topotecan as second-line therapy in small-cell lung cancer. $J Clin Oncol. 2007;25(15):2086-2092.$ $Erratum in: J Clin Oncol. 2007;25(22):3387.$', null, '<b>Topotecan</b> 2.3 mg/m<sup>2</sup>/d PO, days 1-5<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (107, 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various', 'CAP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'High', '<b>CAP</b>', 'Loehrer PJ, et al.$J Clin Oncol 1994;12:1164-1168.$', null, '<b>Cyclophosphamide</b> 500 mg/m<sub>2</sub> IV on day 1<br><b>Doxorubicin</b> 500 mg/m<sub>2</sub> IV on day 1<br><b>Cisplatin</b> 500 mg/m<sub>2</sub> IV on day 1<br>Repeat cycle every 21 days.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (108, 'Cisplatin : CDDP<br>Etoposide : VP-16', 'Cisplatin + Etoposide', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>Cisplatin + Etoposide</b>', 'Giaccone G, et al.$J Clin Oncol 1996;14:814-820.$', null, '<b>Cisplatin</b> 60 mg/m<sub>2</sub> IV on day 1<br><b>Etoposide</b> 120 mg/m<sub>2</sub> IV on day 1 - 3<br>Repeat cycle every 21 days.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (109, 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'ADOC', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>ADOC</b>', 'Fornasiero A, et al.$Cancer 1991;68:30-33.$', null, '<b>Cisplatin</b> 50 mg/m<sub>2</sub> IV on day 1<br><b>Doxorubicin</b> 40 mg/m<sub>2</sub> IV on day 1<br><b>Vincristine</b> 0.6 mg/m<sub>2</sub> IV on day 3<br><b>Cyclophosphamide</b> 700 mg/m<sub>2</sub> IV on day 4<br>Repeat cycle every 28 days.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (110, 'Cisplatin : CDDP<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various', 'VIP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>VIP</b>', 'Loehrer PJ, et al.$Cancer 2001;91:2010-2015.$', null, '<b>Etoposide(VP-I6)</b> 75 mg/m<sub>2</sub> IV on days 1-4<br><b>Ifosfamide</b> 1200 mg/m<sub>2</sub> IV on days 1-4<br><b>Cisplatin</b> 20 mg/m<sub>2</sub> IV on days 1-4<br><b>Mesna</b> 240 mg/m<sub>2</sub> IV before first ifosfamide dose, then 4 and 8 hours later on days 1-4<br>Repeat cycle every 21 days for a total of 4 cycles.G-CSF support at 5 micro grams/kg/day on days 5-15', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (111, 'Gemcitabine : Gemzar, various<br>Topotecan : Hycamtin', 'Gemcitabine + Topotecan', '<b>Hepatic Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Gemcitabine:<br>No dose reduction </li><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'Moderate', '<b>Gemcitabine + Topotecan</b>', 'William WN, et al.$Am J Clin Oncol 2009;32:15-19.$', null, '<b>Gemcitabine</b>1000 mg/m<sub>2</sub> IV on days 1,8, and 15.<br><b>Topotecan</b>0.75-1.5 mg/m<sub>2</sub> IV on days 1,8, and 15.<br> Repeat cycle every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (112, 'Carboplatin : Paraplatin<br>Paclitaxel : Taxol', 'Carboplatin + Paclitaxel', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Carboplatin + Paclitaxel</b>', 'Lemma GL, et al.$ J Clin Oncol 2011;29:2060-2065.$', null, '<b>Carboplatin</b> AUC of 6, IV on day 1 <br><b>Paclitaxel</b>225 mg/m<sub>2</sub> IV on day 1<br> Repeat cycle every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (113, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various', 'AC', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'High', '<b>AC</b>', 'Fisher B,Brown AM,Dimitor NV,et al.Two months of doxorubicin-cyclophosphamide with and without interval reinduction therapy compared with 6 months of cyclophosphamide,methotrexade,and fluorouracil in positive-node breast cancer patients with tamoxifen-nonresponsive tumors:results from the National Surgical Adjuvant Breast and Bowel Project B-15. $J Clin Oncol. 1990;8(9):1483-1496$', null, '<b>Doxorubicin</b> 60mg/m<sup>2</sup> IV,day 1<br> <b>Cyclophosphamide</b> 600mg/m<sup>2</sup>  IV,day 1<br> Repeat every 21 days for 4 cycles', null);
