insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (126, 'Cyclophosphamide : Cytoxan<br>Epirubicin : Ellence, various', 'EC', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>EC</b>', 'Piccart MJ, Di Leo A, Beauduin M, et al. Phase III trial comparing two dose levels of epirubicin combined with cyclophosphamide with cyclophosphamide, methotrexate, and fluorouracil in node-positive breast cancer. $J Clin Oncol. 2001;19(12):3103-3110.$', null, '<b>Epirubicin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 830 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for up to 8 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (127, 'Capecitabine : Xeloda<br>Ixabepilone : Ixempra', 'Ixabepilone/Capecitabine', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Ixabepilone:<br>Monotherapy:  <br>                                                                                                 If AST and ALT < 2.5 upper normal limit, and bilirubin < 1 upper normal limit, use 40 mg/m2<br>                                                                         If AST or ALT < 10 upper normal limit, and bilirubin < 1 upper normal limit, use 32 mg/m2  <br>                                                                                     If AST and ALT < 10 upper normal limit, and bilirubin 1.5-3 upper normal limit, use 20-30 mg/m2 <br>                                                                  If AST or ALT > 10 upper normal limit, or bilirubin > 3 upper normal limit, do not use <br>                                                                                                                                                                                   In combination with capecitabine:  <br>                                                             If AST and ALT < 2.5 upper normal limit, and bilirubin < 1 upper normal limit, use 40 mg/m2 <br>                                                                        If AST or ALT > 2.5 upper normal limit, or bilirubin > 1 upper normal limit, do not use </li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Ixabepilone:<br>If CrCl > 30 mL/min, no dose reduction</li></ul>', 'Low', '<b>Ixabepilone/Capecitabine</b>', 'Thomas ES, Gomez HL, Li RK, et al. Ixabepilone plus capecitabine for metastatic breast cancer progressing after anthracycline and taxane treatment. $J Clin Oncol. 2007;25(33):5210-5217.$', null, '<b>Ixabepilone</b> 40 mg/m<sup>2</sup> IV over 3 hours, day 1<br> <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14<br> Repeat every 21 days for up to 8 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (128, 'Capecitabine : Xeloda<br>Lapatinib : Tykerb', 'Lapatinib/Capecitabine', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Lapatinib:<br>If Child-Pugh C, reduce dose to 750 mg/d </li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Lapatinib:<br>Not studied. Only 2% eliminated by kidneys </li></ul>', 'Low', '<b>Lapatinib/Capecitabine</b>', 'Geyer CE, Forster J, Lindquist D, et al. Lapatinib plus capecitabine for HER2-positive advanced breast cancer. $N Engl J Med. 2006;355(26):2733-2743.$', null, '<b>Lapatinib</b> 1,250 mg PO 1 hour before breakfast, daily<br> <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14<br> Repeat every 21 days', null);
