insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (181, 'Cisplatin : CDDP<br>Gemcitabine : Gemzar, various', 'Gemcitabine/Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'High', '<b>Gemcitabine/Cisplatin</b>', 'von der Maase H, Hansen SW, Roberts JT, et al. Gemcitabine and cisplatin versus methotrexate, vinblastine, doxorubicin, and ciplatin in advanced or metastatic bladder cancer: results of a large, randomized, mulinational, multicenter, phase III study. $J Clin Oncol. 2000;18(17):3068-3077.$', null, '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup>/d IV over 30-60 minutes, days 1,8,15<br> <b>Cisplatin</b> 70mg/m<sup>2</sup> IV, day 2<br> Repeat every 28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (261, '', 'Arsenic', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Moderate', '<b>Arsenic</b>', 'Trisenox [package insert]. $Frazer, PA: Cephalon; 2010.$', null, '<b>Arsenic trioxide</b> 0.15 mg/kg/d IV for 25 doses up to 5 weeks', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (182, 'Cisplatin : CDDP<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban', 'MVAC', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'High', '<b>MVAC</b>', 'von der Maase H, Hansen SW, Roberts JT, et al. Gemcitabine and cisplatin versus methotrexate, vinblastine, doxorubicin, and ciplatin in advanced or metastatic bladder cancer: results of a large, randomized, mulinational, multicenter, phase III study. $J Clin Oncol. 2000;18(17):3068-3077.$', null, '<b>Methotrexate</b> 30 mg/m<sup>2</sup>/d IV, days 1, 15, 22<br> <b>Vinblastine</b> 3 mg/m<sup>2</sup>/d IV, days 2, 15,22<br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV, day 2<br> <b>Cisplatin</b> 70 mg/m<sup>2</sup> IV, day 2<br> Repeat every 28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (183, 'Bleomycin : Blenoxane<br>Cisplatin : CDDP<br>Etoposide : VP-16', 'BEP', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>BEP</b>', 'Nichols CR, Catalano PJ, Crawford ED, et al. Randomized comparison of cisplatin and etoposide and either bleomycin or ifosfamide in treatment of advanced disseminated germ cell tumors: an Eastern Cooperative Oncology Group, jSouthwest Oncology Group, and Cancer and Leukemia Group B Study. $J Clin Oncol. 1998;16(4):1287-1293.$', null, '<b>Bleomycin</b> 30 units IV bolus, weekly for 12 weeks<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV over 30-60 minutes,days 1-5<br> <b>Cisplatin</b> 20 mg/m<sup>2</sup>/d IV over 30-60 minutes, days 1-5<br> Repeat every 21 days for 4 courses', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (184, 'Cisplatin : CDDP<br>Etoposide : VP-16', 'EP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>EP</b>', 'Motzer RJ, Sheinfeld J, Mazumdar M, et al. Etoposide and cisplatin adjuvant therapy for patients with pathologic stage II germ cell tumors. $J Clin Oncol. 1995;13(11):2700-2704.$', null, '<b>Etoposide</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-5<br> <b>Cisplatin</b> 20 mg/m<sup>2</sup>/d IV, days 1-5<br> Repeat after 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (185, 'Cisplatin : CDDP<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Paclitaxel : Taxol', 'TIP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'High', '<b>TIP</b>', 'Kondagunta GV, Bacik J, Donadio A, et al. Combination of paclitaxel, ifosfamide, and cisplatin is an effective second-line therapy for patients with relapsed testicular germ cell tumors. $J Oncol. 2005;23(27):6549-6555.$', null, '<b>Paclitaxel</b> 250 mg/m<sup>2</sup>/d CIVI over 24 hours, day 1<br> <b>Ifosfamide</b> 1,500 mg/m<sup>2</sup>/d IV over 1 hour, days 2-5<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d IV over 30 minutes, days 2-5<br> <b>Mesna</b> 500 mg/m<sup>2</sup> IV before ifosfamide, and at 4 and 8 hours after ifosfamide daily, days 2-5<br> <b>Peg-Filgrastim</b> 6 mcg SQ, days 6<br> Repeat every 21 days for 4 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (186, 'Cisplatin : CDDP<br>Etoposide : VP-16<br>Mesna : Mesnex, various<br>Vinblastine : Velban', 'VIP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Mesna:<br>No dose reduction</li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'High', '<b>VIP</b>', 'Loehrer PJ, Lauer R, Roth BJ, et al. Salvage therapy in recurrent germ-cell cancer: ifosfamide and cisplatin plus either vinblastine or etoposide. $Ann Intern Med. 1988;109(7):540-546.$', null, '<b>Vinblastine</b> 0.11 mg/kg/d IV, days 1, 2<br> <b>OR</b><br> <b>Etoposide</b> 75 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>AND</b><br> <b>Infosfamide</b> 1,2000 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 20mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Mesna</b> 400 mg IV 15 minutes prior to ifosfamide, <b>THEN</b> 1,200 mg/d CIVI, days 1-5<br> Repeat every 21 days for 4 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (187, 'Cabazitaxel : Jevtana<br>Prednisone : various', 'Cabazitaxel', '<b>Hepatic Impairment</b><ul><li>Cabazitaxel:<br></li></ul><b>Renal Impairment</b><ul><li>Cabazitaxel:<br>Caution should be used in patients with severe renal impairment (creatinine clearence of <30 ml/hr)</li></ul>', 'Low', '<b>Cabazitaxel</b>', 'de Bono JS, Oudard S, Ozguroglu M, et al. Prednisone plus cabazitaxel or mitoxantrone for metastatic castration-resistant prostate cancer progressing after docetaxel treatment: a randomised open-label trial. $Lancet. 2010;376(9747):1147-1154.$', null, '<b>Cabazitaxel</b> 25 mg/m<sup>2</sup> IV over 1 hour, day 1<br> <b>Prednisone</b> 10 mg PO daily<br> Repeat every 21 days', null);
