insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (195, 'Abaraterone : Zytiga', 'Abaraterone', '<b>Hepatic Impairment</b><ul><li>Abaraterone:<br>No dosage adjustment is necessary for patients with baseline mild hepatic impairment. <br>In patients with baseline moderate hepatic impairment (Child-Pugh Class B), reduce the recommended dose of ZYTIGA to 250 mg once daily. If elevations in ALT or AST >5X ULN or total bilirubin >3X ULN occur in patients with baseline moderate hepatic impairment, discontinue ZYTIGA treatment. <br> The safety of ZYTIGA in patients with baseline severe hepatic impairment has not been studied. These patients should not receive ZYTIGA. For patients who develop hepatotoxicity during treatment, interruption of treatment</li></ul><b>Renal Impairment</b><ul><li>Abaraterone:<br>No dose adjustment needed</li></ul>', 'Low', '<b>Abaraterone</b>', 'Ryan CJ, et al. "Abiraterone in Metastatic Prostate Cancer without Previous Chemotherapy". $N. Engl. J. Med. 368 (2): 138–48.$', null, '<b>Abaraterone</b>1000 mg PO once daily on empty stomach<br> <b>Prednisone</b> 5 mg PO twice daily', null);
