insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (204, 'Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various', 'AD', '<b>Hepatic Impairment</b><ul><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'High', '<b>AD</b>', 'Antman K, Crowley J, Balcerzak SP, et al. An intergroup phase III randomized study of doxorubicin and dacarbazine with or without ifosfamide and mesna in advanced soft tissue and bone sarcomas. $J Clin Oncol. 1993;11(7):1276-1285.$', null, '<b>Doxorubicin</b> 15 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Dacarbazine 250 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (205, 'Doxorubicin : Adriamycin, various<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various', 'DI', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Moderate', '<b>DI</b>', 'Santoro A, Tursz T, Mouridsen H, et al. Doxorubicin versus CYVADIC versus doxorubicin plus ifosfamide in first-line treatment of advanced soft tissue sarcomas: a randomized study of the European Organization for Research and Treatment of Cancer Soft Tissue and Bone Sarcoma Group. $J Clin Oncol. 1995;13(7):1537-1545.$', null, '<b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV bolus, day 1,<br> <b>FOLLOWED BY</b><br> <b>Ifosfamide</b> 5,000 mg/m<sup>2</sup> CIVI, day 1<br> <b>Mesna</b> 600 mg/m<sup>2</sup> IV bolus, before ifosfamide, FOLLOWED BY 2,500 mg/m<sup>2</sup> CIVI with ifosfamide, and 1,250 mg/m<sup>2</sup> IV over 12 hours following ifosfamide<br> Repeat every 21 days ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (206, 'Cyclophosphamide : Cytoxan<br>Dactinomycin : Actinomycin<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR', 'VAC-IE', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Dactinomycin:<br>If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'VAC-High<br> IE-Moderate', '<b>VAC-IE</b>', 'Grier HE, Krailo MD, Tarbell NJ, et al. Addition of ifosfamide and etoposide to standard chemotherapy for Ewing''s sarcoma and primitive neuroectodermal tumor of bone. $N Engl J Med. 2003;348(8):694-701.$', null, '<b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day 1<br> <b>FOLLOWED BY</b>  mesna<br> <b>Vincristine<sup></b> 2 mg/m<sup>2</sup> IV, day 1<br> <b>Doxorubicin</b> 75 mg/m<sup>2</sup> IV bolus, day 1<br> <b>Dactinomycin</b> 1.25 mg/m<sup>2</sup> IV, day 1 substitued for doxorubicin when cumulative doxorubicin dose reaches 375 mg/m<sup>2</sup><br> Alternating with each cycle<br> <b>Ifosfamide</b> 1,800 mg/m<sup>2</sup>/d IV, days 1-6 (given with mesna)<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV, days 1-5<br> Repeat cycle every 21 days for 17 total cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (207, 'Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various', 'MAID', '<b>Hepatic Impairment</b><ul><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'High', '<b>MAID</b>', 'Antman K, Crowley J, Balcerzak SP, et al. An intergroup phase III randomized study of doxorubicin and dacarbazine with or without ifosfamide and mesna in advanced soft tissue and bone sarcomas. $J Clin Oncol. 1993;11(7):1276-1285.$', null, '<b>Mesna</b> 2,500 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Doxorubicin</b> 15 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Ifosfamide</b> 2,500 mg/m<sup>2</sup>/d CIVI, days 1-3<br> <b>Dacarbazine</b> 250 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (208, 'Cisplatin : CDDP<br>Doxorubicin : Adriamycin, various<br>Ifosfamide : Ifex', 'Methotrexate/Cisplatin/Doxorubicin Ifosfamide', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Methotrexate:<br>If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25% <br>  If bilirubin > 5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Leucovorin:<br>No dose reduction</li><li>Methotrexate:<br>If CrCl 30-60 mL/min, reduce dose by 50%    <br>                                                                  If CrCl < 30 mL/min, do not use</li></ul>', 'High', '<b>Methotrexate/Cisplatin/Doxorubicin Ifosfamide</b>', 'Ferrrari S, Smeland S, Mercuri M, et al. Neoadjuvant chemotherapy with high-dose ifosfamide, high-dose methotrexate, cisplatin, and doxorubicin for patients with localized osteosarcoma of the extremity: a joint study by the Italian and Scandinavian Sarcoma Groups. $J Clin Oncol. 2005;23(34):8845-8852.$', null, '<b>Methotrexate</b> 12,000 mg/m<sup>2</sup> IV over 4 hours day 1<br> <b>WITH Leucovorin calcium</b> 8 mg/m<sup>2</sup> IV every 6 hours for 11 doses beginning 24 hours from the start of the Methotrexate infusion, days 1, 42 (preoperatively); days 133, 196, 238 (postoperatively)<br> <b>Cisplatin</b> 120 mg/m<sup>2</sup> CIVI over 48 hours, days 7-8, 49-50 (preoperatively); days 140-141, 203-204, 245-246 (postoperatively). <br> <b>Doxorubicin</b> 75 mg/m<sup>2</sup> CIVI over 24 hours, days 7, 49 (preoperatively); days 98, 161 (postoperatively). Doxorubicin was increased to 90 mg/m<sup>2</sup> IV over 24 hours for postoperative treatment courses<br> <b>Ifosfamide</b> 3,000 mg/m<sup>2</sup>/d CIVI for 5 days with equimolar doses of mesna, days 28-32, 70-74 (preooperatively); days 119-123, 182-186, 224-228 (postoperatively)', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (209, 'Doxorubicin : Adriamycin, various', 'Doxorubicin', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Moderate', '<b>Doxorubicin</b>', 'Nielsen OS, Dombernowsky P, Mouridsen H, et al. High-dose epirubicin is not alternative to standard-dose doxorubicin in the treatment of advanced soft tissue sarcomas: a study of the EORTC soft tissue and bone sarcoma group. $Br J Cancer. 1998;78(12):1634-1639.$', null, '<b>Doxorubicin</b> 75 mg/m<sup>2</sup> IV<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (210, '', 'Interferon alfa-2b', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Moderate', '<b>Interferon alfa-2b</b>', 'Groopman JE, et al. Recombinant alpha-2 interferon therapy for Kaposi''s sarcoma associated with the acquired immunodeficiency syndrome.$Ann Intern Med. 1984 May;100(5):671-6$', null, '<b>Interferon alfa-2b</b>  30 million IU/m<sup>2</sup> IM <b>OR</b> SQ 3 times per week<br>Repeat until disease progression ', null);
