insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (212, 'Paclitaxel : Taxol', 'Paclitaxel', '<b>Hepatic Impairment</b><ul><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Low', '<b>Paclitaxel</b>', 'Parkash S. Gill, et al. Paclitaxel Is Safe and Effective in the Treatment of Advanced AIDS-Related Kaposi''s Sarcoma.$http://jco.ascopubs.org/content/17/6/1876.full$', null, '<b>Paclitaxel</b>  135 mg/m<sup>2</sup> IV over 3 hours<br> Repeat every 21 days<br><b>OR</b><br> <b>Paclitaxel<sup>a</sup></b> 100 mg/m<sup>2</sup> IV over 3 hours<br>Repeat every 14 days   ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (213, '', 'Pegylated liposomal doxorubicin', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Pegylated liposomal doxorubicin</b>', 'Udhrain A, et al.Pegylated liposomal doxorubicin in the treatment of AIDS-related Kaposi’s sarcoma.$Int J Nanomedicine. 2007 September; 2(3): 345–352.$', null, '<b>Pegylated liposomal doxorubicin</b> 20 mg/m<sup>2</sup> IV every 3 weeks as tolerated', null);
