insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (216, 'Carboplatin : Paraplatin<br>Gemcitabine : Gemzar, various<br>paclitaxel : Taxol', 'Gemcitabine/Carboplatin/paclitaxel', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li><li>paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Gemcitabine:<br>No dose reduction </li><li>paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Gemcitabine/Carboplatin/paclitaxel</b>', 'Greco FA, Burris HA III, Litchy S, et al. Gemcitabine, carboplatin, and paclitaxel for patients with carcinoma of unknown primary site: a Minnie pearl cancer center research Network study. J Clin Oncol.2002;20(6):1651-1656.                ', null, '<b>Gemcitabine</b> 1,000mg/m<sup>2</sup>  1V, days 1,8<br><b>Carboplatin</b> AUC of 5 IV, day 1<br><b>Paclitaxel</b> 200 mg/m2 IV over 1 hour, day 1<br>Repeat cycle every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (217, 'Cisplatin : CDDP<br>Gemcitabine : Gemzar, various', 'Gemcitabine/Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'High', '<b>Gemcitabine/Cisplatin</b>', 'Culine S, Lortholary A, Voigt JJ, et al. Gemcitabine, carboplatin combintion with either gemcitabine or irinotecan in carcinomas of unknown primary site: results of a randomized phase II study- trial for the French study Group on Carcinomas of Unknown Primary (GEFCAPI 01). $JClin Oncol. 2003;21(18):3479-3482.$', null, '<b>Gemcitabine</b> 1,250 mg/m<sup>2</sup> IV, days 1 and 8<br><b>Cisplatin</b> 100mg/m<sup>2</sup> IV, day 1<br>Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (218, 'Docetaxel : Taxotere<br>Gemcitabine : Gemzar, various', 'Gemcitabine/Docetaxel', '<b>Hepatic Impairment</b><ul><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Docetaxel:<br>No dose reduction </li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Low', '<b>Gemcitabine/Docetaxel</b>', 'Pouessel D, Culine S, Becht C, et al. Gemcitabine and docetaxel as front-line chemotherapy in patients with carcinoma of an unknown primary site. $Cancer. 2004; 100(6): 1257-1261.$', null, '<b>Gemcitabine</b> 1,000mg/m<sup>2</sup> IV over 30 minutes, days 1 and 8<br><b>Docetaxel</b> 75 mg/m<sup>2</sup>  IV over 1 hour, day 8<br>Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (219, 'Capecitabine : Xeloda<br>Oxaliplatin : Eloxatin, Oxaliplatin, various', 'Oxaliplatin Capecitabine', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Moderate', '<b>Oxaliplatin Capecitabine</b>', 'Hainsworth JD, Spigel DR, Burris HA, et al. Oxaliplatin and capecitabine inn the treatment of patients with recurrent or refractory carcinoma of unknown primary site: A phase 2 trial of the Sarah Cannon Oncology Research Consortium. $Cancer. 2010; 116(10):2448-2454.$', null, '<b>Oxaliplatin</b> 130 mg/m<sup>2</sup>  IV, day 1<br><b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14<br>Repeat cycle every 21 days upto 6 cycles                                                 ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (220, 'Carboplatin : Paraplatin<br>Etoposide : VP-16<br>Paclitaxel : Taxol', 'Paclitaxel/Carboplatin/Etoposide', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Paclitaxel/Carboplatin/Etoposide</b>', 'Hainsworth JD, Spigel DR, Litchy S, Greco FA. Phase II trial of paclitaxel, carboplatin, and etoposide in advanced poorly differentiated neutroendocrine carcinoma: a Minnie Pearl Cancer Network Study. $J Clin Oncol. 2006;24(22):3548-3554.$', null, '<b>Paclitaxel</b>  200mg/m<sup>2</sup> IV over 1 hour, day 1,<br><b>FOLLOWED BY</b><br> <b>Carboplatin</b> dose targeted by Calvert equation to AUC of 6 mg/mL/min IV, day 1<br><b>Etoposide</b> 50 mg/d PO alternated with 100mg/d PO, days 1-10<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (243, '', 'Romidepsin', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Romidepsin</b>', 'Istodax [package insert]. $Summit , NJ: Celgene Corporation; 2011.$', null, '<b>Romidepsin</b> 14mg\m<sup>2</sup> IV on days 1,8,and 15.<br> Repeat every 28 days           ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (221, 'Bleomycin : Blenoxane<br>Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban', 'ABVD', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'High', '<b>ABVD</b>', 'Harker WG, Kushlan P, Rosenberg SA. Combination chemotherapy for advanced Hodgkin''s disease after failure of MOPP: ABVD and B-CAVe. $Ann Intern Med. 1984;  101(4):440-446$.<br> Longo DL, Glastein E, Duffey PL,et al. Alternating MOPP and ABVD chemotherapy plus mantle-field radiation therapy in patients with massive mediastinal Hodgikin''s disease. $J Clin Oncol. 1997; 15(11): 3338-3346$.     ', null, '<b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Bleomycin</b> 10 units/m<sup>2</sup>/d IV, days 1, 15<br> <b>Vinblastine</b> 6 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Dacarbazine</b> 375 mg/m<sup>2</sup>/d IV, days 1, 15<br> Repeat every 28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (222, 'Bleomycin : Blenoxane<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Procarbazine : Matulane<br>Vincristine : Oncovin, VCR', 'BEACOPP (Escalated)', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Procarbazine:<br>If CrCl < 30 mL/min, do not use</li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>BEACOPP (Escalated)</b>', 'Diehl V, Franklin J, Pfreundschuh M, et al. Standard and increased-dose BEACOPP chemotherapy compared with COPP-ABVD for advanced Hodgikin''s disease. $N Engl J Med. 2003;348(24):2386-2395$.', null, '<b>Bleomycin</b> 10 mg/m<sup>2</sup> IV, day8<br> <b>Etoposide</b> 200 mg/m<sup>2</sup> IV, days 1-3<br> <b>Doxorubicin</b> 35 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 8<br> <b>Procarbazine</b> 100 mg/m<sup>2</sup> PO, days 1-7<br> <b>Prednisone</b> 40 mg/m<sup>2</sup> IV, days 1-14<br> Repeat on day 22 for 8 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (223, 'Bleomycin : Blenoxane<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Vinblastine : Velban<br>Vincristine : Oncovin, VCR', 'Stanford V', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Mechlorethamine:<br>No dose reduction</li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vinblastine:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>Stanford V</b>', 'Bartlett NL, Rosenberg SA, Hoppe RT, Hancock SL, Horning SJ.Brief chemotherapy, Stanford V, and adjuvant radiotherapy for bulky or advanced-stage Hodgikin''s disease: a Preliminary report. $J Clin Oncol. 1995; 13(5):1080-1088$.', null, '<b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Vinblastine</b> 6 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Mechlorethamine</b> 6 mg/m<sup>2</sup> IV,day1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup>/d IV, days 8, 22<br> <b>Bleomycin</b> 5 units per m<sup>2</sup> per day  IV, days 8,22<br> <b>Etoposide</b> 60mg/m<sup>2</sup>/d IV, days 15,16<br> <b>Prednisone</b> 40 mg/m<sup>2</sup> PO every other day', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (224, 'Bendamustine : Treanda<br>Rituximab : Rituxan', 'BR', '<b>Hepatic Impairment</b><ul><li>Bendamustine:<br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin 1.5-3 upper normal limit, or bilirubin > 3 upper normal limit, do not use </li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Bendamustine:<br>If CrCl < 40 mL/min, do not use </li></ul>', 'Moderate', '<b>BR</b>', 'Rummel MK, Niederle N, Mascmeyere G, et al. Bendamustine plus rituximab is superior in respect of progression free survival and CR rate when compared too CHOP plus rituximab as first-line treatment of patients with advanced follicular, indolent, and matle cell lymphomas: Final results of a randomized phase III study of StiL (Study Group of Indolent Lymphomas, Germany). $Presented at :51st American Society of Hematology Annual Meeting; December 5-8, 2009; New Orleans, LA. Abstract 405.$', null, '<b>Rituximab</b> 375mg/m<sup>2</sup> IV, day 1<br> <b>Bendamustine</b> 90 mg/m<sup>2</sup> IV, days 1, 2<br> Repeat every 28 days for 6 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (225, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Prednisolone : Flo-pred, various<br>Vincristine : Oncovin, VCR', 'CHOP', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>CHOP</b>', 'Bezwoda W, Ristogi RB, Erazo Valla A, et al. Long-term results of a multicentre randomised, comparitive phase III trial of CHOP versus CNOP regimens in patients with intermediate-and high-grade non-Hodgikin''s lymphomas. $ Eur J Cancer. 1995;31A(6):903-911$.', null, '<b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 1<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 1<br> <b>Prednisolone</b> 50 mg/m<sup>2</sup>/d PO, days 1-5<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (240, '', 'Ibritumomab tiuxetan', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Ibritumomab tiuxetan</b>', 'Zevalin [package insert]. $San Deigo, CA: IDEC Pharmaceutical Coorporation; 2010.$<br> Wiseman GA, White CA, Sparks RB, et al. Biodistribution and dosimetry results from a phase III prospectively randomized controlled trial of Zevali radiommunotherapy for low-grade, follicular, or transofrmed B-cell non-Hodgikin''s lymphoma. $Crit Rev Oncol Hematol. 2001;39(1-2):181-194.$', null, '<b>Ibritumomab tiuxetan</b> is given in 2 steps:<br> Step 1: <b>Rituximab</b> 250 mg/m<sup>2</sup> infusion <b>FOLLOWED WITHIN 4 HOURS BY</b> 5.0 mCi (1.6 mg total antibody dose) of <b>In- 111 ibrituximomab tiuxetan</b> administered as a 10-minute IV push<br> Step 2: (7-9 days after step 1): <b>Rituximab</b> 250 mg/m<sup>2</sup> infusion <b>FOLLOWED WITHIN 4 HOURS BY</b><br> <b>Y-90 ibritumomab tiuxetan</b> 0.4 mCi/kg (maximum, 32 mCi) admistered as a 10-minute IV push               ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (226, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Prednisone : various<br>Rituximab : Rituxan<br>Vincristine : Oncovin, VCR', 'R - CHOP', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>R - CHOP</b>', 'Coiffier B, Leapage E, Briere J, et al. CHOP chemotherapy plus rituximab compared with CHOP alone in elderly patients with diffuse large B-cell lymphoma. $N Engl J Med. 2002;346(4):235-242.$<br> Czuczman MS, Grillo-Lopez AJ, White CA, et al. Treatment of patients with low-grade B-cell lymphoma with the combination of chimeric anti-CD20 monoclonal antibody and CHOP chemotherapy. $J Clin Oncol. 1999; 17(1):268-276$.', null, '<b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 1<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 1 upto a maximum dose of 2mg<br> <b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO, days 1-5<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (227, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR', 'CODOX-M/IVAC', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>CODOX-M/IVAC</b>', 'Mead GM, Sydes MR, Walewski J, et al. An international evaluation of CODOX-M and CODOX-M alternating with IVAC in adult Burkitt''s lymphoma: results of United Kingdom Lymphoma Group LY06 Study. $Ann Oncol. 2002, 13(8): 1254-1274.$', null, '<u>Cycles 1 AND 3 (CODOX-M)</u><br> <b>Cyclophosphamide</b> 800 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 600 mg/m<sup>2</sup>/d IV, days 2-5<br> <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV up to a maximum dose of 2 mg, days 1,8 for cycle 1, days 1,8, 15 for cycle 2<br> <b>Methotrexate</b> 1,200 mg/m<sup>2</sup> IV over 1 hour, day 10,          <b>THEN</b> 240 mg/m<sup>2</sup>/h CIVI for the next 23 hours<br> <b>Leucovorin calcium </b>192 mg/m<sup>2</sup> IV, starting at hour 36  <b>THEN</b> 12 mg/m<sup>2</sup> every 6 hours until Methotrexate level is <5*10<sup>-8</sup> M<br> <b>Filgrastim</b>5 mcg/kg/d SQ until ANC >1,000/mm<sup>3</sup><br> <b>Methotrexate</b> 12mg IT, day 15<br> <b>Leucovorin calcium</b> 15 mg PO 24 hours after Methotrexate, day 16<br>                                                              <u>CNS PROPHYLAXIS</u><br> <b>Cytarabine</b> 70 mg IT, days 1,3,5<br> <b>Methotrexate</b> 12 mg IT, day 12 Cycles 2 AND 4 (IVAC)<br> <b>Ifosfamide</b> 1,500mg/m<sup>2</sup> IV over 1 hour, days 1-5<br> <b>Mesna</b> 360 mg/m<sup>2</sup> IV over 1 hour <b>THEN</b> 360 mg/m<sup>2</sup> IV every 3 hours for a maximum of 7 doses  per 24 hour period, days 1-5<br> <b>Cytarabine</b>  2,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours, days 1 and 2<br> <b>Methotrexate</b> 12 mg IT, day 5<br> <b>Leucovorin calcium</b> 15 mg PO 24 hours after Methotrexate, day 6<br> <b>Filgrastim</b> 5 mcg/kg/d SQ until ANC > 1,000/mm<sup>3</sup>                                                  ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (228, 'Prednisone : various<br>Rituximab : Rituxan<br>Vincristine : Oncovin, VCR', 'R - CVP', '<b>Hepatic Impairment</b><ul><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>R - CVP</b>', 'Marcus R, Imrie K, Belch A, et al. CVP chemotherapy plus rituximab compared with CVP as first-line treatment for advanced follicular lymphoma. $Blood, 105(4): 1417-1423.$<br> Marcus R, Imrie K, Solal-Celigny P, et al. Phase III study of R-CVP compared with cyclophosphamide, vincristine and prednisone alone in patients with previously utreated advanced follicular lymphoma. $J Clin Oncol. 2008;26(28):4578-4589.$', null, '<b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> up to a maximum dose of 2 mg IV, day 1<br> <b>Prednisone</b> 40 mg PO, days 1-5<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for a maximum off 8 cycles ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (229, 'Cisplatin : CDDP<br>Cytarabine : Ara-C<br>Dexamethasone : various', 'DHAP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'High', '<b>DHAP</b>', 'Velasquez WS, Cabanillas F, Salvador P, et al. Effective salvage therapy for lymphoma with cisplatin in combination with high-dose Ara-C and dexamethasone (DHAP). $Blood. 1988;71(1):117-122.$', null, '<b>Dexamethasone</b> 40 mg/d PO or IV over 24 hours, days 1-4<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> CIVI, day 1<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours for 2 doses, day 2<br> Repeat every 21-28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (230, 'Cisplatin : CDDP<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Methylprednisolone : Depo-Medrol, Solu-Medrol, various', 'ESHAP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>ESHAP</b>', 'Velasquez WS, McLaughlin P, Tucker S, et al. ESHAP- an effective chemotherapy regimen in refractory and relapsing lymphoma: a 4-year follow-up study. $J Clin Oncol. 1994; 12(6): 1169-1176.$<br> Rodriguez MA, Cabanillas FC, Velasquez W, et al. Results of a salvage treatment program for relapsing lymphoma: MINE consolidated with ESHAP. $J Clin Oncol. 1995; 13(7): 1734-1741.$', null, '<b>Etoposide</b> 40 mg/m<sup>2</sup>/d IV, days 1-4<br> <b>Methylprednisolone</b> 500 mg/d IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 2-3 hours,             day 5<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>OR</b><br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d IV, days 1-4<br> <b>Methylprednisolone</b> 500 mg/d IV, days 1-4<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 2 hours, day 5, immediately following completion of cisplatin<br> Repeat every 21-28 days        ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (241, 'Pralatrexate : Folotyn', 'Pralatrexate', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Pralatrexate</b>', 'Folotyn [package insert]. $Westminster, CO: Allos Therapeutics Inc.; 2012.$', null, '<b>Pralatrexate</b> 30 mg/m<sup>2</sup> IV, weekly for 6 weeks<br> Repeat every 7 weeks', null);
