insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (231, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Vincristine : Oncovin, VCR', 'Hyper-CVAD/MTX-Ara-C', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>Hyper-CVAD/MTX-Ara-C</b>', 'Khouri IF, Romaguera J, Kantrajian H,et al. Hyper-CVAD and high -dose methotrexate/ cytarabine followed by stem-cell tans-plantation: an active regimen for aggressive mantle-cell lymphoma. $J Clin Oncol. 1998; 16(12): 3803-3809$.', null, '<u>COURSE 1</u><br> <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> over 3 hours every 12 hours  for 6 doses , days 1-3<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> over 48 hours after cyclophosphamide, days 4-5<br> <b>Vincristine</b> 2 mg administered 12 hours  after last cyclophosphamide dose, repeated on day 11<br> <b>Dexamethasone</b> 40 mg/d IV or PO,days 1-4,11-14<br> <b>Filgrastim</b> 5 mcg/kg IV or SQ beginning 24 hours after doxorubicin is complete.<br> <u>COURSE 2 <br>(BEGIN AFTER CLINICAL AND HEMATOLOGIC RECOVERY FROM COURSE 1)</u><br> <b>Methotrexate</b> 200 mg/m<sup>2</sup> IV bolus, day 1<br> <b>FOLLOWED BY</b><br> <b>Methotrexate</b> 800mg/m<sup>2</sup>over 24 hours day1 <b>Followed by</b><br><b>Cytarabine</b>3000 mg/m<sup>2</sup> IV every 12 hours for 4 doses, days 2-3 (dose reduced to 1,000 mg/m<sup>2</sup> in patients >60 years or with serum creatinine >1.5 mg/dL)<br> <b>Leucovorin calcium</b> 50 mg PO 24 hours after end of Methotrexate infusion<br> <b>FOLLOWED 6 HOURS LATER BY</b><br> <b>Leucovorin calcium</b> 15 mg PO every 6 hours for 8 doses (dose adjusted for Methotrexate concentration at 24 and 48 hours after completion of Methotrexate infusion)<br> <b>Filgrastim</b> 5mcg/kg IV or SQ begining 24 hours after cytarabine.<br> Repeat courses every 21 days.            ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (232, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Rituximab : Rituxan<br>Vincristine : Oncovin, VCR', 'R-EPOCH', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>R-EPOCH</b>', 'Jermann M, jost M, Taverna C, et al. Rituximab EPOCH, an effective salvage therapy for relapsed, refractory or transformed B-cell lymphomas: results of a phase II study. $Ann Oncol. 2004; 15(3):511-516$.', null, '<b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> <b>Etoposide</b> 65 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Doxorubicin</b> 15 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Vincristine</b> 0.5 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 5<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-14<br> Repeat every 21 days for 4 -6 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (233, 'Carboplatin : Paraplatin<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Rituximab : Rituxan', 'RICE', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Moderate', '<b>RICE</b>', 'Kewalramani T, Zelenetz AD, Nimer SD, Nimer SD, et al. Rituximab and ICE as second-line therapy before autologous stem cell transplantation for relapsed or primary refractory diffuse large B-cell lymphoma. $Blood. 2004; 103(10):3684-3688.$', null, '<b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1 every 14 days<br> <b>Ifosfamide</b> 5,000 mg/m<sup>2</sup> with mesna 5,000 mg/m<sup>2</sup> CIVI over 24 hours, day 4<br> <b>Carboplatin</b> AUC of 5 (maximum 800 mg) IV, day 4<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d CIVI, days 3-5<br> Repeat every 14 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (234, 'Bendamustine : Treanda', 'Bendamustine', '<b>Hepatic Impairment</b><ul><li>Bendamustine:<br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin 1.5-3 upper normal limit, or bilirubin > 3 upper normal limit, do not use </li></ul><b>Renal Impairment</b><ul><li>Bendamustine:<br>If CrCl < 40 mL/min, do not use </li></ul>', 'Moderate', '<b>Bendamustine</b>', 'Kahl BS, Bartlett NL, Leonard JP, et al. Bendamustine is effective therapy in patients with rituximab-refractory, indolent B-cell non-Hodgikin lymphoma: results from a multicenter study. $Cancer. 2010,116(1):106-114 $.', null, '<b>Bendamustine</b> 120 mg/m<sup>2</sup> IV days 1 and 2<br> Repeat every 21 days', null);
