insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (236, 'Bortezomib : Velcade', 'Bortezomib', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Low', '<b>Bortezomib</b>', 'Goy A, Bernstein SH, Kahl BS, et al. Bortezomib in patients with relapsed or refractory mantle cell lymphoma: updated time to event analyses of the multicenter Phase 2. PINNACLE study. $Ann Oncol. 2009;20(3):520-525.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup>/d IV bolus, days 1,4, 8, 11<br> Repeat every 21 days for up to 17 cycles.', null);
