insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (239, 'Fludarabine : Fludara', 'Fludarabine', '<b>Hepatic Impairment</b><ul><li>Fludarabine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Fludarabine:<br>If CrCl 30-70 mL/min, reduce dose by 20%      <br>                                                                  If CrCl < 30 mL/min, do not use </li></ul>', 'Low', '<b>Fludarabine</b>', 'Klasa RJ,Meyer RM, Shustic C, et al. Randomized phase III study of fludarabine phosphate versus cyclophosphamide, vincristine, and prednisione in patients with recurrent low-grade Non-Hodgikin''s lymphoma previously treated with an alkylating agent or alkylatic containing regimen. $J Clin Oncol. 2002;20(24):4649-4654.$', null, '<b>Fludarabine</b> 25 mg/m<sup>2</sup>/d IV over 10 minutes, days 1-5<br> Repeat every 28 days for up to 8 courses', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (247, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16', 'CDE', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Moderate', '<b>CDE</b>', 'Sparano JA,Lee S, Chen MG, et al. Phase II trial of   infusional, cyclophosphamide, doxorubicin, and etoposide in patients with HIV-associated non-Hodgkins lymphoma: an Eastern Cooperative Oncology Group Trial (E1491). $J Clin Oncol. 2004; 22(8): 1491-1500.$', null, '<b>Cyclophosphamide</b> 200mg/m<sup>2</sup>/d CIVI for 4 days<br><b>Doxorubicin</b> 12.5 mg/m<sup>2</sup>/d CIVI for 4 days<br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d CIVI for 4 days<br> Repeat every 28 days for a maximum of 8 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (248, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'CHOP', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>CHOP</b>', 'Weiss R, Mitrou P, Arasteh K, et.al. Acquired immunodeficiency syndrome-related lymphoma: Simultaneous treatment with combined cyclophosphamide, doxorubicin, Vincristine, and prednisone chemotherapy and highly active antiretroviral therapy is safe and improves survival--results of the German Multicenter Trial. $Cancer. 2006; 106(7): 1560-1568.$ ', null, '<b>Cyclophosphamide</b> 750mg/m<sup>2</sup> IV, day 1<br> Doxorubicin 50 mg/m<sup>2</sup> IV, day 1<br><b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 1<br> <b>Prednisone</b> 100mg/m<sup>2</sup> PO, days 1-5<br> Repeat every 21 days.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (249, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR', 'CODOX-M/IVAC', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>CODOX-M/IVAC</b>', 'Wang ES, Strauss DJ, Teruya-Feldstein J, et al. Intensive chemotherapy with cyclophosphosphamide, doxorucibicin, high-dose methotrecate/ ifosfamide, etoposide, and high-dose cytarabine (CODOX-M/IVAC)for human immunodeficiency virus-associated Burkitt lymphoma. $Cancer. 2003;98(6): 1196-1205.$', null, '<u>CYCLES 1 and 3 (CODOX-M)</u><br>  <b>Cyclophosphamide</b> 800 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 200 mg/m<sup>2</sup>/d IV, days 2-5<br>  <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 1, 8 for cycle 1,days 1, 8, 15 for cycle 2<br> <b>Methotrexate</b> 1,200 mg/m<sup>2</sup> IV over 1 h, day 10, <b>THEN</b> 240 mg/m2/h CIVI for the next 23 hours<br> <b>Leucovorin calcium</b> 50 mg IV q6h, begining 36 hours from the start of Methotrexate until Methotrexate level <0.05 mcM<br> <b>Filgrastim</b> beginning 24 h from the start of leucovorin until ANC>1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Cytarabine</b> 70 mg IT, days 1, 3<br> <b>Methotrexate<b> 12 mg IT, day 15<br> <u>CYCLES 2 AND 4 (IVAC)</u><br> <b>Ifosofamide</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Mesna</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Etoposide</b> 60 mg/m<sup>2</sup> IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV q 12h, days 1 and 2<br> <b>Filgrastim</b> beginning 24 h after completion of chemotherapy until ANC >1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Methotrexate</b> 12 mg IT, day 15<br> Additional IT Treatment for CNS disease- positive patients:<br> Cycle 1: cytarabine 70 mg IT, days 1,3,5, and Methotrexate 12 mg IT, days 15,17<br> Cycle 3: cytarabine 70 mg IT, days 1,3, and Methotrexate 12 mg IT, day 15                                                                                                                                                               ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (250, 'Cyclophosphamide : Cytoxan<br>Etoposide : VP-16<br>Prednisone : various<br>Vincristine : Oncovin, VCR', 'EPOCH Dose-adjusted ', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>EPOCH Dose-adjusted </b>', 'Little RF, Pittaluga S, Grant N, et al. Highly effective treatment of acquired immunodeficiency syndrome-related lymphoma with dose-adjusted EPOCH: impact of antiretroviral therapy suspension and tumor biology. $BLOOD.2003;101(12):4653-4659.$', null, '<b>Etoposide</b> 50 mg/m<sup>2</sup>/d CIVI, days 1-4 (96 hours)<br> <b>Doxorubicin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4 (96 hours)<br> <b>Vincristine</b> 0.4 mg/m<sup>2</sup>/d /CIVI, days 1-4 (96 hours)<br> <b>Cyclophosphamide</b> <br>Cycle1: <br>375 mg/m<sup>2</sup> IV, day 5, if CD4+ cells >= 100/mm<sup>3</sup>; 187 mg/m<sup>2</sup> IV, day 5, if CD4+ cells<100/mm<sup>3</sup><br> After cycle 1: increase dose by 187 mg if nadir ANC > 500/mvL or platelets <25,000/mcL<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-5<br> <b>Filgrastim</b> 5 mcg/kg/d SQ, day 6, and until ANC >5,000/mcl<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (258, 'Cytarabine : Ara-C', '7 + 3 with hi-dose daunorubicin <br>(<60 years)', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li></ul>', 'Moderate', '<b>7 + 3 with hi-dose daunorubicin <br>(<60 years)</b>', 'Fernandez HF, Sun Z, Pao X, et al. Anthracycline dose intensification in acute myeloid leukemia. $N Engl J Med. 2009;361(13):1249-1259.$', null, '<b>Cytarabine</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-7<br> <b>WITH</b><br> <b>Daunorubicin</b> 90 mg/m<sup>2</sup>/d IV, days 1-3', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (259, '', 'Arsenic', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Moderate', '<b>Arsenic</b>', 'Trisenox [package insert]. $Frazer, PA: Cephalon; 2010.$', null, '<b>Arsenic trioxide</b> 0.15 mg/kg IV, daily until remission, not to exceed 60 doses', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (251, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Prednisone : various<br>Thioguanine : 6-TG<br>Vincristine : Oncovin, VCR', 'CALGB 8811', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Thioguanine:<br>If bilirubin > 5 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>CALGB 8811</b>', 'Larson RA, Dodge RK, Burns CP, et al. A five-drug remission induction regimen with intensive consolidation for adults with acute lymphoblastic leukemia: Cancer and Leukemia Group B study 8811. $Blood. 1995;85(8):2025-2037.$', null, '<u>INDUCTION (COURSE 1:4 WEEKS)</u><br> <b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day1 (800 mg/m<sup>2</sup> for patients >=60 years)<br> <b>Daunorubicin</b> 45 mg/m<sup>2</sup> IV, days 1-3 (30 mg/m<sup>2</sup> for patients>=60 years)<br> <b>Vincristine</b> 2 mg IV, days 1, 8, 15, 22<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-21 (days 1-7 for patients >=60 years)<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> SQ, days 5, 8, 11, 15, 18, 22<br> <u>EARLY INTENSIFICATION (COURSE 2:4 WEEKS, REPEAT ONCE)</u><br> <b>Methotrexate</b> 15 mg IT, day 1<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 1<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Cytarabine</b> 75 mg/m<sup>2</sup>/d SQ, days 1-4, 8-11<br> <b>Vincristine</b> 2 mg IV, days 15, 22<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> SQ, days 15, 18, 22, 25<br> <u>CNS PROPHYLAXIS AND INTERIM MAINTENANCE (COURSE 3:12 WEEKS)</u><br> <b>Cranial RT</b> 2,400 cGy, days 1-12<br> <b>Methotrexate</b> 15 mg IT, days 1, 8, 15, 22, 29<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-70<br> <b>Methotrexate</b> 20 mg/m<sup>2</sup> PO, days 36, 43, 50, 57, 64<br> <u>LATE INTENSIFICATION (COURSE 4:8 WEEKS)</u><br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV, days 1, 8, 15<br> <b>Vincristine</b> 2 mg IV, days 1, 8, 15<br> <b>Dexamethasone</b> 10 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 29<br> <b>Thioguanine</b> 60 mg/m<sup>2</sup>/d PO, days 29-42<br> <b>Cytarabine</b> 75 mg/m<sup>2</sup>/d SQ, days 29-32, 36-39<br> <u>MAINTENANCE (COURSE 5:4 WEEKS, FOR 2 YEARS FROM DIAGNOSIS)</u><br> <b>Vincristine</b> 2 mg IV, day 1 of every 4 weeks<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-5 of every 4 weeks<br> <b>Methotrexate</b> 20 mg/m<sup>2</sup> PO, days 1, 8, 15, 22<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-28', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (252, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Mesna : Mesnex, various<br>Methylprednisolone : Depo-Medrol, Solu-Medrol, various<br>Vincristine : Oncovin, VCR', 'Hyper-CVAD', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>Hyper-CVAD</b>', 'Kantarjian HM, O''Brien S, Smith TL, et al. Results of treatment with Hyper-CVAD, a dose-intensive regimen, in adult acute lymphocytic leukemia. $J Clin Oncol. 2000;18(3):547-561.$', null, '<u>ODD CYCLES (1, 3, 5, 7)</u><br>                            <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> IV over 3 hours every 12 hours, days 1-3<br> <b>Mesna</b> 600 mg/m<sup>2</sup>/d CIVI, days 1-3, ending 6 hours after cyclophosphamide completion<br> <b>Vincristine</b> 2 mg IV, days 4, 11<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV, day 4<br> <b>Dexamethasone</b> 40 mg/d, days 1-4, 11-14<br> <b>Filgrastim</b> 5 mcg/kg bid SQ beginning 24 hours after doxorubicin completion <br> <b>OR</b><br><b>PEG-Filgrastim</b>6 mg SQ once 24 hours after doxorubicin completion<br> <u>EVEN CYCLES (2, 4, 6, 8)</u><br> <b>Methotrexate</b> 200 mg/m<sup>2</sup> IV over 2 hours, day 1,<br> <b>FOLLOWED BY</b>  800 mg/m<sup>2</sup> IV over 24 hours<br> <b>Leucovorin calcium</b> 15 mg IV every 6 hours for 8 doses, beginning 24 hours after Methotrexate completion; adjust per Methotrexate level<br> <b>Cytarabine</b> 3 g/m<sup>2</sup> IV over 2 hours every 12 hours for 4 doses, days 2, 3<br> <b>Methylprednisolone</b> 50 mg IV bid, days 1-3<br> <b>Filgrastim</b> 6 mg/kg SQ beginning 24 hours after cytarabine completion<br> <b>OR</b><br><b>PEG-Filgrastim</b>6 mg SQ once 24 hours after doxorubicin completion<br> <u>CNS PROPHYLAXIS</u><br> <b>Methotrexate</b> 12 mg IT, day 2 of each cycle<br> <b>Cytarabine</b> 100 mg IT, day 8 of each cycle<br>4-16 cycles based on risk profile', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (253, '', 'Nelarabine', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Nelarabine</b>', 'Arranon [package insert]. $Research Triangle Park, NC: GlaxoSmithKline;2011.$', null, '<b>Nelarabine</b> 1,500 mg/m<sup>2</sup>/d IV, days 1, 3 and 5', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (254, '', 'ATRA/Arsenic', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Moderate', '<b>ATRA/Arsenic</b>', 'Estey E, Garcia-Manero G, Ferrajoli A, et al. Use of all-trans-retinoic acid plus arsenic trioxide as an alternative to chemotherapy in untreated acute promyelocytic leukemia. $Blood. 2006;107(9):3469-3473.$', null, '<b>All-trans-retinoic acid (ATRA)</b> 45 mg/m<sup>2</sup> PO in 2 divided doses daily<br> <b>Arsenic trioxide</b> 0.15 mg/kg/d IV over 1 hour, daily beginning day 10 and continued until <5% blasts and absence of promyelocytes', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (255, 'Idarubicin : Idamycin', 'ATRA/Idarubicin', '<b>Hepatic Impairment</b><ul><li>Idarubicin:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 25%<br> If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 50% <br> If bilirubin > 5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Idarubicin:<br>If CrCl < 10 mL/min, reduce dose by 25% </li></ul>', 'Moderate', '<b>ATRA/Idarubicin</b>', 'Sanz MA, Montesinos P, Vellenga E, et al. Risk-adapted treatment of acute promyelocytic leukemia with all-trans retinoic acid and anthracycline monochemotherapy: long-term outcomeon the LPA99 multicenter study by the PETHEMA Group. $Blood. 2008;112(8):3130-3134.$', null, '<b>All-trans-retinoic acid</b> (ATRA) 45 mg/m<sup>2</sup> PO, daily<br> <b>Idarubicin</b> 12 mg/m<sup>2</sup> IV, on days 2, 4, 6, 8', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (256, 'Cytarabine : Ara-C<br>Etoposide : VP-16<br>Idarubicin : Idamycin<br>Mitoxantrone : Novantrone', 'Idarubicin/Cytarabine/Etoposide', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Idarubicin:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 25%<br> If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 50% <br> If bilirubin > 5 mg/dL, do not use </li><li>Mitoxantrone:<br>If bilirubin > 3 mg/dL, reduce dose by 25%</li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Idarubicin:<br>If CrCl < 10 mL/min, reduce dose by 25% </li><li>Mitoxantrone:<br>No dose reduction</li></ul>', 'Moderate', '<b>Idarubicin/Cytarabine/Etoposide</b>', 'Mandelli F, Vignetti M, Suciu S, et al. Daunorubicin versus mitoxantrone versus idarubicin as induction and consolidation chemotherapy for adults with acute myeloid leukemia: the EORTC and GIMEMA group study AML-10. $J Clin Oncol. 2009;32(10):5397-5403.$', null, '<b>Cytarabine</b> 25 mg/m<sup>2</sup> IV <b>FOLLOWED BY</b> 100 mg/m<sup>2</sup> CIVI, days 1-10<br> <b>Etoposide</b> 100 mg/m<sup>2</sup> IV, days 1-5<br> <b>Daunorubicin</b> 50 mg/m<sup>2</sup> IV, days 1, 3, 5<br> <b>OR</b><br> <b>Mitoxantrone</b> 12 mg/m<sup>2</sup> IV, days 1, 3, 5<br> <b>OR</b><br> <b>Idarubicin</b> 10 mg/m<sup>2</sup> IV, days 1, 3, 5', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (257, 'Cytarabine : Ara-C<br>Idarubicin : Idamycin<br>Mitoxantrone : Novantrone', '7 + 3', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Idarubicin:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 25%<br> If bilirubin 3.1-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 50% <br> If bilirubin > 5 mg/dL, do not use </li><li>Mitoxantrone:<br>If bilirubin > 3 mg/dL, reduce dose by 25%</li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Idarubicin:<br>If CrCl < 10 mL/min, reduce dose by 25% </li><li>Mitoxantrone:<br>No dose reduction</li></ul>', 'Moderate', '<b>7 + 3</b>', 'Presler H, Davis RB, Kirshener J, et al. Comparison of three remission induction regimens and two post-induction strategies for the treatment of acute nonlymphocytic leukemia: a Cancer and Leukemia Group B study. $Blood. 1987;69(5):1441-1449.$<br> Wiernik PH, BAnks PL, Case DC Jr, et al. Cytarabine plus idarubicin or daunorubicin as induction and consolidation therapy for previously untreated patients with acute myeloid leukemia. $Blood. 1992;79(2):313-319.$<br> Wahlin A, Hornsten P, Hedenus M, Malm C. Mitoxantrone and cytarabine versus daunorubicin and cytarabine in previously untreated patients with acute myeloid leukemia. $Cancer Chemother Pharmacol. 1991;28(6):480-483.$', null, '<b>Cytarabine</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-7<br> <b>WITH</b><br> <b>Daunorubicin</b> 45 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>OR</b><br> <b>Idarubicin</b> 13 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>OR</b><br> <b>Mitoxantrone</b> 12 mg/m<sup>2</sup>/d IV, days 1-3', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (262, 'Cytarabine : Ara-C', 'HiDAC', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul></ul>', 'Moderate', '<b>HiDAC</b>', 'Mayer RJ, Davis RB, Schiffer CA, et al. Intensive postremission chemotherapy in adults with acute myeloid deukemia. Cancer and Leukemia Group B. $N Engl J Med. 1994;331(14):896-903.$', null, 'For patients <60 years of age:<br> <b>Cytarabine</b> 3,000 mg/m<sup>2</sup> CIVI over 3 hours, every 12 hours, days 1, 3, 5<br> Repeat every 28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (263, 'Cytarabine : Ara-C', 'Cytarabine', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Cytarabine</b>', 'Stone RM, Berg DT, George SL, et al. Postremission therapy in older patients with de novo acute myeloid leukemia: a randomized trial comparing mitoxantrone and intermediate-dose cytarabine with standard-dose cytarabine. $Blood. 2001;98(3):548-553.$', null, 'For patients >=60 years of age:<br> <b>Cytarabine</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-5<br> Repeat every 28 days for 4 courses', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (264, 'Bendamustine : Treanda<br>Rituximab : Rituxan', 'BR', '<b>Hepatic Impairment</b><ul><li>Bendamustine:<br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin 1.5-3 upper normal limit, or bilirubin > 3 upper normal limit, do not use </li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Bendamustine:<br>If CrCl < 40 mL/min, do not use </li></ul>', 'Moderate', '<b>BR</b>', 'Fischer K, Cramer P, Stilgenbauer S, et al. Bendamustine combined with rituximab (BR) in first-line therapy of advanced CLL: A multicenter phase II trial of the German CLL Study Group (GCLLSG). $J Clin Oncol. 2011;29(26)3559-3566.$', null, '<b>Bendamustine</b> 90 mg/m<sup>2</sup> IV on days 1 and 2<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV for the first cycle and 500 mg/m<sup>2</sup> for subsequent cycles<br> Repeat every 28 days for up to 6 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (265, 'Cyclophosphamide : Cytoxan<br>Fludarabine : Fludara', 'FC', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Fludarabine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fludarabine:<br>If CrCl 30-70 mL/min, reduce dose by 20%      <br>                                                                  If CrCl < 30 mL/min, do not use </li></ul>', 'Moderate', '<b>FC</b>', 'Catovsky D, Richards S, Oscier D, et al. Assesmentof fludarabine plus cyclo-phosphamide for patients with chronic lymphocytic leukemia (the LRF CLL4 trial): a randomized ocntrolled trial. $Lancet. 2007; 370(9583):230-239.$', null, '<b>Fludarabine</b> 25 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cyclophosphamide</b> 250 mg/m<sup>2</sup>/d IV, days 1-3<br> Repeat every 28 days for 6 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (266, 'Cyclophosphamide : Cytoxan<br>Fludarabine : Fludara<br>Rituximab : Rituxan', 'FCR', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Fludarabine:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fludarabine:<br>If CrCl 30-70 mL/min, reduce dose by 20%      <br>                                                                  If CrCl < 30 mL/min, do not use </li></ul>', 'Moderate', '<b>FCR</b>', 'Robak T, Dmoszynska A, Solal-Celigny P, et al. Rituximab plus fludarabine and cyclophosphamide prolongs progression free survival compared with fludarabine and cyclophosphamide alone in previously treated chronic lymphocytic leukemia. $J Clin Oncol. 2010;28(10):1756-1765.$', null, '<b>Fludarabine</b> 25 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cyclophosphamide</b> 250 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1 cycle 1<br> <b>FOLLOWED BY</b><br> <b>Rituximab</b> 500 mg/m<sup>2</sup> IV, day 1 cycles 2-6<br> Repeat every 28 days for 6 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (267, 'Fludarabine : Fludara<br>Rituximab : Rituxan', 'FR', '<b>Hepatic Impairment</b><ul><li>Fludarabine:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Fludarabine:<br>If CrCl 30-70 mL/min, reduce dose by 20%      <br>                                                                  If CrCl < 30 mL/min, do not use </li></ul>', 'Low', '<b>FR</b>', 'Byrd JC, Peterson BL, Morrison VA, et al. Randomized phase 2 study of fludarabine with concurrent versus sequential treatment with rituximab in symptomatic, untreated patients with B-cell chronic lympocytic leukemia: results from Cancer and Leukemia Group B 9712 (CALGB 9712). $Blood. 2003;101(1):6-14$', null, '<b>Fludarabine</b> 25 mg/m<sup>2</sup>/d IV over 20-30 minutes, days 1-5<br> Repeat every 28 days for a total of 6 cycles<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV<br> Repeat every 7 days for 4 doses', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (268, 'Cyclophosphamide : Cytoxan<br>Pentostatin : Nipent<br>Rituximab : Rituxan', 'PCR', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Pentostatin:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Moderate', '<b>PCR</b>', 'Kay NE, Geyer SM, Call TG, et al. Combination chemoimmunotherapy with pentostatin, cyclophosphamide, and rituximab shows significant clinical activity with low accompanying toxicity in previously untreated B chronic lymphocytic leukemia. $Blood. 2007;109(2):405-411.$', null, '<b>Pentostatin</b> 2 mg/m<sup>2</sup> IV,day 1<br> <b>Cyclophosphamide</b> 600 mg/m<sup>2</sup> IV, day 1<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for 6 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (269, 'Alemtuzumab : Campath', 'Alemtuzumab', '<b>Hepatic Impairment</b><ul><li>Alemtuzumab:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Alemtuzumab:<br>Unknown</li></ul>', 'Low', '<b>Alemtuzumab</b>', 'Campath [package insert]. $Wayne, NJ: Bayer HealthCare Pharmaceuticals: 2009.$', null, '<b>Alemtuzumab</b> 30 mg per day IV, 3 times per week, upto a maximum of 23 weeks', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (270, 'Bendamustine : Treanda', 'Bendamustine', '<b>Hepatic Impairment</b><ul><li>Bendamustine:<br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin 1.5-3 upper normal limit, or bilirubin > 3 upper normal limit, do not use </li></ul><b>Renal Impairment</b><ul><li>Bendamustine:<br>If CrCl < 40 mL/min, do not use </li></ul>', 'Moderate', '<b>Bendamustine</b>', 'Knauf WU, Lissichov T, Aldaoud A, et al. Phase III randomized study of bendamustine compared with chlorambucil in previously treated patients with chronic lyphocytic leukemia. $J Clin Oncol. 2009;27(26):4378-4348.$', null, '<b>Bendamustine</b> 100 mg/m<sup>2</sup> IV over 30 min, days 1, 2<br> Repeat every 28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (271, 'Chlorambucil : Leukeran', 'Chlorambucil', '<b>Hepatic Impairment</b><ul><li>Chlorambucil:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Chlorambucil:<br>No dose reduction</li></ul>', 'Low', '<b>Chlorambucil</b>', 'Eichhorst BF, Busch R, Stilgenbauer S, et al. First line therapy with fludarabine compared with chlorambucil does not result in a major benefit for elderly patients with advanced chronic lymphocytic leukemia. $Blood. 2009;114:3382-3391.$', null, '<b>Chlorambucil</b> 0.4 mg/kg PO <br> Repeat therapy every 14 days, increase upto  0.8 mg/kg  if  tolerated', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (272, 'Fludarabine : Fludara', 'Fludarabine', '<b>Hepatic Impairment</b><ul><li>Fludarabine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Fludarabine:<br>If CrCl 30-70 mL/min, reduce dose by 20%      <br>                                                                  If CrCl < 30 mL/min, do not use </li></ul>', 'Low', '<b>Fludarabine</b>', 'Eichhorst BF, Busch R, Stilgenbauer S, et al. First line therapy with fludarabine compared with chlorambucil does not result in a major benefit for elderly patients with advanced chronic lymphocytic leukemia. $Blood. 2009;114:3382-3391.$', null, '<b>Fludarabine</b> 25 mg/m<sup>2</sup>/d IV over 30 minutes , days 1-5<br> Repeat every 28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (273, 'Ofatumumab : Arzerra', 'Ofatumumab', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Ofatumumab</b>', 'Arzerra [package insert]. <br> Wierda WG, Kipps TJ, Mayer J, et al. Ofatumumab as single agent CD20 immunotherapy in fluda-rabine-refractory chronic lymphocytic leukemia. $J Clin Oncol. 2010;28:1749-1755.$', null, '<b>Previously untreated CLL:</b><br>300 mg IV on Day 1 followed by 1,000 mg IV on Day 8 (Cycle 1)<br>1,000 mg IV on Day 1 subsequent 28-day cycles for a minimum of 3 cycles until best response or maximum of 12 cycles.<br><b>Refractory CLL:</b><br><b>Ofatumumab</b> 300 mg IV, initial dose,<br> <b>FOLLOWED 1 week later BY</b><br> <b>Ofatumumab</b> 2,000 mg IV, weekly dose for 7 weeks<br> Repeat every 28 days for 4 doses', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (274, 'Dasatinib : Sprycel', 'Dasatinib', '<b>Hepatic Impairment</b><ul><li>Dasatinib:<br>Unknown. Metabolism mainly hepatic </li></ul><b>Renal Impairment</b><ul><li>Dasatinib:<br>Unknown. < 4% excreted by kidney </li></ul>', 'Low', '<b>Dasatinib</b>', 'Kantarijan H, Shah NP, Hochhaus A, et al. Dasatinib versus imatinib in newly diagnosed chronic-phase chronic myeloid deukemia. $N Engl J Med. 2010;362(24):2260-2270.$', null, '<b>Dasatinib</b> 100 mg PO, daily (chronic phase)<br> <b>OR</b><br> <b>Dasatinib</b> 140 mg PO, daily (accelerated or blast phase)', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (275, 'Imatinib : Gleevec', 'Imatinib', '<b>Hepatic Impairment</b><ul><li>Imatinib:<br>If bilirubin < 3 upper normal limit, no dose reduction <br>                            If bilirubin > 3 upper normal limit, use with caution </li></ul><b>Renal Impairment</b><ul><li>Imatinib:<br>No dose reduction </li></ul>', 'Low', '<b>Imatinib</b>', 'Druker BJ, et al. Efficacy and safety of a specific inhibitor of the BCR-ABL tyrosine kinase in chronic myeloid leukemia.$N Engl J Med. 2001 Apr 5;344(14):1031-7.$', null, '<b>Imatinib mesylate</b> 400 mg PO daily <b>OR</b> 600 mg PO daily(accelarated phase / blast crisis) ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (276, 'Nilotinib : Tasigna', 'Nilotinib', '<b>Hepatic Impairment</b><ul><li>Nilotinib:<br>Unknown </li></ul><b>Renal Impairment</b><ul><li>Nilotinib:<br>Not tested. Not renally excreted </li></ul>', 'Low', '<b>Nilotinib</b>', 'Kantarjian HM, et al. Nilotinib versus imatinib for the treatment of patients with newly diagnosed chronic phase, Philadelphia chromosome-positive, chronic myeloid leukaemia: 24-month minimum follow-up of the phase 3 randomised ENESTnd trial.$Lancet Oncol. 2011 Sep;12(9):841-51. doi: 10.1016/S1470-2045(11)70201-7. Epub 2011 Aug 17$', null, '<b>Nilotinib</b> 300 mg PO bid  (newly diagnosed)<br> <b>OR</b><br> <b>Nilotinib</b> 400 mg PO bid (resistant or intolerant chronic or accelerated phase)', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (277, 'Cladribine : 2-CdA', 'Cladribine', '<b>Hepatic Impairment</b><ul><li>Cladribine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Cladribine</b>', 'Saven A, et al. $Blood 1992;79:1111-1120.$', null, '<b>Cladribine:</b>0.09 mg/kg/day IV continuous infusion on days 1 to 7 <br>Administer one cycle', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (278, 'Pentostatin : Nipent', 'Pentostatin', '<b>Hepatic Impairment</b><ul><li>Pentostatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Pentostatin</b>', 'Cassileth PA, et al.$J Clin Oncol. 1991;9:243-246.$', null, '<b>Pentostatin:</b> 4 mg/m<sup>2</sup> IV on day 1 <br>Repeat cycle every 14 days for 6 cycles ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (279, '', 'Interferon alfa-2a', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Interferon alfa-2a</b>', 'Ratain MJ, et al. $Blood 1985;65:644-648.$', null, '<b>Interferon alfa-2a:</b> 3 million IU SC or IM, 3 times per week<br>Continue treatment for up to 1 to 1.5 years.', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (280, 'Bortezomib : Velcade', 'Bortezomib/Doxil', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Low', '<b>Bortezomib/Doxil</b>', 'Doxil [package insert]. $Raritan, NJ: Centocor Ortho Biotech Products, LP; 2010.$<br> Orlowski RZ, Nagler A, Sonneveld P, et al. Randomized phase III study of pegylated liposomal doxorubicin plus bortezomib compared with bortezomib alone in relapsed or refractory multiple myeloma: combination therapy improves time to progression. $J clin Oncol. 2007;25(25):3892.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11<br> <b>Pegylated liposomal doxorubicin</b> 30 mg/m<sup>2</sup> IV over 1 hour, day 4<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (281, 'Bortezomib : Velcade<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various', 'Bortezomib/Doxorubicin/Dexamethasone', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Moderate', '<b>Bortezomib/Doxorubicin/Dexamethasone</b>', 'Sonneveld P, van der Holt B, Schmidt-Wolf IGH, et al. First analysis of HOVON-65/GMMG-HD4 randomized phase III trial comparing bortezomib, adriamycin, dexamethasone (PAD) vs VAD as induction treatment prior to high dose melphalan (HDM) in patients with newly diagnosed multiple myeloma (MM). $Blood. 2008;112(11):Abstract 653.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11<br> <b>Doxorubicin</b> 9 mg/m<sup>2</sup> IV, bolus days 1-4<br> <b>Dexamethasone</b> 40 mg PO days 1-4, 9-12, 17-20', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (282, 'Bortezomib : Velcade<br>Thalidomide : Thalomid', 'Bortezomib/Thalidomide/Dexamethasone (VTD)', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Low', '<b>Bortezomib/Thalidomide/Dexamethasone (VTD)</b>', 'Cazo M, Tacchetti P, Patriarca F, et al. Bortezomib with thalidomide plus dexamethasone compared with thalidomide plus dexamethasone as induction therapy before, and consolidation therapy after, double autologous stem-cell transplantation in newly diagnosed multiple myeloma: a randomised phase 3 study. $Lancet. 2010;18;376(9758):2075-2085.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV bolus, days 1, 4, 8, and 11<br> <b>Thalidomide</b> 100 mg PO daily for 14 days and 200 mg daily thereafter<br> <b>Dexamethasone</b> 40 mg PO daily on days 1, 2, 4, 5, 8, 9, 11, and 12; total of 320 mg per cycle', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (283, 'Bortezomib : Velcade<br>Cyclophosphamide : Cytoxan<br>Dexamethasone : various', 'CyBorD', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Moderate-High', '<b>CyBorD</b>', 'Khan ML, Reeder CB, Kumar SK, et al. A comparison af lenalidomide/dexamethasone versus cyclophosphamide/lenalidomide/dexamethasone versus cyclophosphamide/bortezomib/dexamethansone in newly diagnosed multiple myeloma. $Br J Haematol. 2011 [Epub ahead of print].$', null, '<b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> PO days 1, 8, 15, 22<br> <b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV on days 1, 4, 8, and 11<br> <b>Dexamethasone</b> 40 mg PO days 1-4, 9-12, and 17-20<br> Repeat every 28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (284, 'Cisplatin : CDDP<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Thalidomide : Thalomid', 'DT-PACE', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>DT-PACE</b>', 'Lee CK, Barlogie B, Munshi N, et al. DTPACE: an effective, novel combination chemotherapy with thalidomide for previously treated patients with myeloma. $J Clin Oncol. 2003;21(14):2732-2739.$', null, '<b>Dexamethasone</b> 40 mg PO, days 1-4<br> <b> Thalidomide</b> 400 mg/d PO<br> <b>Cisplatin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Doxorubicin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Etoposide</b> 40 mg/m<sup>2</sup>/d CIVI, days 1-4', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (285, 'Dexamethasone : various<br>Lenalidomide : Revlimid', 'Lenalidomide/Dexamethasone', '<b>Hepatic Impairment</b><ul><li>Lenalidomide:<br>Unknown </li></ul><b>Renal Impairment</b><ul><li>Lenalidomide:<br>Substantially excreted by kidney. Trials excluded patients with renal insufficiency </li></ul>', 'Low', '<b>Lenalidomide/Dexamethasone</b>', 'Dimopoulos M, Spencer A, Attal M, et al. Lenalidomide plus dexamethasone for relapsed or refractory multiple myeloma. $N Engl J Med. 2007;357(21):2123-2132.$', null, '<b>Lenalidomide</b> 25 mg/d PO, days 1-21<br> <b>Dexamethasone</b> 40 mg/d PO, days 1-4, 9-12, 17-20<br> Repeat every 28 days for 4 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (286, 'Melphalan : Alkeran<br>Prednisone : various', 'Melphalan/Prednisone (MP)', '<b>Hepatic Impairment</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction  <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Low', '<b>Melphalan/Prednisone (MP)</b>', ' $Southwest Oncology Group Study. Arch Intern Med 1975;135:147-152$', null, '<b>Melphalan</b> 8 - 10 mg per m<sup>2</sup>  on days 1 - 4<br> <b>Prednisone</b> 60 mg/m<sup>2</sup> days 1 - 4<br> Repeat cycle every 42 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (287, 'Melphalan : Alkeran<br>Prednisone : various', 'Melphalan/Prednisone/Talidomide (MPT)', '<b>Hepatic Impairment</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction  <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Low', '<b>Melphalan/Prednisone/Talidomide (MPT)</b>', ' $Palumbo A, et al. Lancet 2006;28:367-835.$', null, '<b>Melphalan</b> 0.25 mg/kg PO, daily for 5 days<br> <b>Prednisone</b> 1 mg/kg PO, daily for 5 days<br> Repeat every 28 days for 8 cycles<br> <b>Thalidomide 50 - 100 mg PO, daily ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (288, 'Bortezomib : Velcade<br>Dexamethasone : various<br>Lenalidomide : Revlimid', 'RVD', '<b>Hepatic Impairment</b><ul><li>Lenalidomide:<br>Unknown </li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Lenalidomide:<br>Substantially excreted by kidney. Trials excluded patients with renal insufficiency </li></ul>', 'Low', '<b>RVD</b>', 'Richardson PG, Weller E, Lonial S, et al. Lenalidomide, bortezomib, and dexamethasone combination therapy in patients with newly diagnosed multiple myeloma. $Blood. 2010;116(5):679-686.$', null, '<b>Lenalidomide</b> 25 mg/d PO, days 1-14<br> <b> Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11<br> <b>Dexamethasone</b> 20 mg/d PO days 1, 2, 4, 5, 8, 9, 11, 12 (cycles 1-4)<br> Repeat every 21 days for 8 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (289, 'Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'VAD', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>VAD</b>', 'Segeren CM, Sonneveld P, Holt V, et al. Vincristine, doxorubicin and dexamethasone (VAD) administered as rapid intravenous infusion for first-line treatment in untreated multiple myeloma. $Br J Haematol. 1999;105(1):127-130.$', null, '<b>Vincristine</b> 0.4 mg/d CIVI, days 1-4<br> <b>Doxorubicin</b> 9 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Dexamethasone</b> 40 mg/d PO, days 1-4, 9-12, 17-20<br> Repeat every 25 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (290, 'Bortezomib : Velcade<br>Melphalan : Alkeran<br>Prednisone : various', 'VMP', '<b>Hepatic Impairment</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction  <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Low', '<b>VMP</b>', 'San Miguel JF, Schalg R, Khuageva NK, et al. MMY-3002 a phase 3 study comparing bortezomib-melphalan-prednisone (VMP) with melphalan-prednisone (MP) in newly diagnosed multiple myeloma. $Blood. 2007;110(11 pt 1): Abstract 78.$<br> San Miguel JF, Schlag R, Khuageva NK, et al. Bortezomib plus melphalan and prednisone for initial treatment of multiple myeloma. $N Engl J Med. 2008;359(9):906-917.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11, 22, 25, 29, 32 cycles 1-4<br> <b>FOLLOWED BY</b><br> <b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 8, 22, 29 cycles 5-9<br> <b>Melphalan</b> 9 mg/m<sup>2</sup> PO, days 1-4<br> <b>Prednisone</b> 60 mg/m<sup>2</sup> PO, days 1-4<br> Repeat every 6 weeks for 9 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (291, 'Bortezomib : Velcade', 'Bortezomib', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Low', '<b>Bortezomib</b>', 'Richardson PG, Barlogie B, Berenson J, et al. A phase 2 study of bortezomib in relapsed, refractory myeloma. $N Engl J Med. 2003;348(26):2609-2617.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup>/d IV bolus, days 1, 4, 8, 11<br> Repeat every 21 days ', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (292, 'Dexamethasone : various', 'Dexamethasone', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'N/A', '<b>Dexamethasone</b>', 'Alexanian R, Dimopoulos MA, Delasalle K, Barlogie B. Primary dexamethasone treatment of multiple myeloma. $Blood. 1992;80(4):887-890.$', null, '<b>Dexamethasone</b> 20 mg/m<sup>2</sup>/d PO, days 1-4, 9-12, 17-20<br> Repeat after 14-day rest period', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (293, 'Thalidomide : Thalomid', 'Thalidomide', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Thalidomide</b>', 'Singhal S, Mehta J, Desikan R, et al. Antitumor activity of thalidomide in refractory multiple myeloma. $N Engl J Med. 1999;341(21):1565-1571.$ $ Erratum in N Engl J Med. 2000;342(5):364.$', null, '<b>Thalidomide</b> 200 mg/d PO at bedtime (increased dose every 2 weeks for 6 weeks up to a dose of 800 mg/d)', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (294, 'Carfilzomib : Kyprolis', 'Carfilzomib', '<b>Hepatic Impairment</b><ul><li>Carfilzomib:<br>The safety, efficacy and pharmacokinetics of Carfilzomib have not been evaluated in patients with baseline hepatic impairment.<br> Patients with the following laboratory values were excluded from the KYPROLIS clinical trials: ALT/AST = 3 × upper limit of normal (ULN) and bilirubin = 2 × ULN</li></ul><b>Renal Impairment</b><ul><li>Carfilzomib:<br>No dose adjustment recommended</li></ul>', 'Low', '<b>Carfilzomib</b>', 'Siegel DS, et al. A phase 2 study of single-agent carfilzomib (PX-171-003-A1) in patients with relapsed and refractory multiple myeloma.$Blood. 2012 Oct 4;120(14):2817-25. doi: 10.1182/blood-2012-05-425934. Epub 2012 Jul 25$', null, '<b>Carfilzomib</b> 20 mg/m<sup>2</sup>, Days 1,2,8,9,15, and 16, followed by a 12 day rest period<br> Repeat cycle every 28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (295, 'Pomalidamide : Pomalyst', 'Pomalidamide', '<b>Hepatic Impairment</b><ul><li>Pomalidamide:<br>Avoid POMALYST in patients with serum bilirubin greater than 2.0 mg/dL and AST/ALTgreater than 3.0 x ULN.</li></ul><b>Renal Impairment</b><ul><li>Pomalidamide:<br>Avoid Pomalidamide in patients with a serum creatinine greater than 3.0 mg/dL.</li></ul>', 'Low', '<b>Pomalidamide</b>', 'Leleu X, et al. Pomalidomide plus low-dose dexamethasone is active and well tolerated in bortezomib and lenalidomide-refractory multiple myeloma: Intergroupe Francophone du Myélome 2009-02.$Blood. 2013 Mar 14;121(11):1968-75. doi: 10.1182/blood-2012-09-452375. Epub 2013 Jan 14$', null, '<b>Pomalidamide</b>4 mg PO daily, from Day 1-21 <br>Repeat cycle every 28 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (296, 'Cisplatin : CDDP<br>Etoposide : VP-16', 'CDDP/VP-16', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>CDDP/VP-16</b>', 'Kovnar EH, Kellie SJ, Horowitz ME, et al. Preirradiation cisplatin and etoposide in the treatment of high-risk medulloblastoma and other malignant embryonal tumors of the central nervous system: a phase II study. $J Clin Oncol. 1990;8(2):330-336.$', null, '<b>Cisplatin</b> 90 mg/m<sup>2</sup> IV, day 1<br> <b>Etoposide</b> 150 mg/m<sup>2</sup>/d IV, days 3, 4<br> Repeat every 21 days for 4 courses', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (297, 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Etoposide : VP-16<br>Vincristine : Oncovin, VCR', 'COPE or "Baby Brain I"', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>COPE or "Baby Brain I"</b>', 'Duffner id, Horowitz ME, Krischer JP, et al. Postoperative chemotherapy and delayed radiation in children less than 3 years of age with malignant brain tumors. $N Engl J Med. 1993;328(24):1725-1731.', null, '<u>CYCLE A</u><br> <b>Vincristine</b> 0.065 mg/kg/d (maximum, 1.5 mg) IV, days 1, 8<br> <b>Cyclophosphamide</b> 65 mg/kg/d IV over 30 minutes, day 1<br> <u>CYCLE B</u><br> <b>Cisplatin</b> 4 mg/kg/d IV over 6 hours, day 1<br> <b>Etoposide</b> 6.5 mg/kg/d IV over 1 hour, days 3, 4<br> Regimens given in alternating 28-day cycles in the sequence AABAAB', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (298, 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Vincristine : Oncovin, VCR', 'Vincristine/Cyclophosphamide/Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', 'Vincristine/Cyclophosphamide/Cisplatin', 'Douglas JG, Barker JL, Ellenbogen RG, et al. Concurrent chemotherapy and reduced-dose cranial spinal irradiation followed by conformal posterior fossa tumor bed boost for average-risk medulloblastoma: efficacy and patterns of failure. $Int J Radiat Oncol Biol Phys. 2004;58(4):1161-1164.$<br> National Cancer Institute. Phase III randomized study of adjuvant lomustine/cisplatin/vincristine vs cyclophosphamide/cisplatin/vincristine following craniospinal radiotherapy in children with newly diagnosed, average-risk medulloblastoma. $http://cancer.gov.clinicaltrials.CCG-A9961. Accessed January 27, 2012.$', null, '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 1<br> Repeat every 6 weeks for 8 cycles', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (328, '', '6-MP/ MTX', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Moderate', '<b>6-MP/ MTX</b>', 'Schrappe M, Reiter A, Ludwig WD, et al. Improved outcome in childhood acute lymphoblastic leukemia despite reduced use of anthracyclines and cranial radiotherapy: results of trial ALL-BFM 90. German-Austrian-Swiss ALL-BFM Study Group. $Blood. 2000;95(11):3310-3322.$', null, '<b>Mercaptopurine</b> 25 mg/m<sup>2</sup>/d PO, days 1-56<br> <b>Methotrexate</b> 5,000 mg/m<sup>2</sup> IV over 24 hours, days 8, 22, 36, 50<br> <b>Methotrexate</b> 12 mg/d IT (adjust dose for children<3 years), days 8, 22, 36, 50', null);
