insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (309, 'Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various', 'IFoVP', '<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>IFoVP</b>', 'Miser JS, Kinsella TJ, Triche TJ, et al. Ifosfamide with mesna uroprotection and etoposide: an effective regimen in the treatment of recurrent sarcomas and other tumors of children and young adults. $J Clin Oncol. 1987;5(8):1191-1198.$', null, '<b>Ifosfamide</b> 1,800 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV over 1 hour, days 1-5<br> <b>Mesna</b> 2,880 mg/m<sup>2</sup>/d IV, days 1-5<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (329, '', 'MTX/6-MP', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>MTX/6-MP</b>', 'Camitta B, Leventhal B, Lauer S, et al. Intermediate-dose intravenous methotrexate and mercaptopurine therapy for non-T, non-B acute lymphocytic leukemia of chldhood: a Pediatric Oncology Group study. $J Clin Oncol. 1989;7(10):1539-1544.$', null, '<b>Methotrexate</b> 20 mg/m<sup>2</sup> IM weekly, weeks 25-130<br> <b>Mercaptopurine</b> 50 mg/m<sup>2</sup>/d PO, weeks 25-130', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (310, 'Cisplatin : CDDP<br>Doxorubicin : Adriamycin, various', 'MTX-CDDP/Adr', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Leucovorin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Leucovorin:<br>No dose reduction</li></ul>', 'High', '<b>MTX-CDDP/Adr</b>', 'Weiner MA, Harris MB, Lewis M, et al. Neoadjuvant high-dose methotrexate, cisplatin, and doxorubicin for the management of patients with nonmetastatic osteosarcoma. $Cancer Treat Rep. 1986;70(12):1431-1432.$', null, '<b>Methotrexate</b> 12,000 mg/m<sup>2</sup> IV weekly for 2 weeks; escalate dose by 1,000 mg/m<sup>2</sup> in subsequent courses until peak level of 10-8 M is reached (reduce dose to 8,000 mg/m<sup>2</sup> IV for patients >12 years)<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup> IV every 3 hours for 8 doses beginning 16 hours after completion of methotrexate, <b>THEN</b> every 6 hours PO for 8 doses<br> <b>ALTERNATING WITH</b><br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 15 of cycles 1-7,<br> <b>THEN</b> 120 mg/m<sup>2</sup> for cycles 8-10<br> <b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 15-17 of cycles 1-7', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (311, 'Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various', 'VP/high-dose Ifos', '<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>VP/high-dose Ifos</b>', 'Goorin AM, Harris MB, Bernstein M, et al. Phase II/III trial of etoposide and high-dose ifosfamide in newly diagnosed metastatic osteosarcoma: a pediatric oncology group trial. $J Clin Oncol. 2002;20(2):426-433.', null, '<b>Etoposide</b> 100 mg/m<sup>2</sup> days 1-5<br> <b>Ifosfamide</b> 3,500 mg/m<sup>2</sup> days 1-5<br> <b>Mesna</b> 700 mg/m<sup>2</sup> over 4 hours, <b>THEN</b> mesna 700 mg/m<sup>2</sup> at hour 4, 7, 10, 13, days 1-5<br> <b>GCF</b> 5 mcg/kg/d begin day 6<br> Repeat in 3 weeks for a total of 2 courses', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (312, 'Carboplatin : Paraplatin<br>Etoposide : VP-16<br>Ifosfamide : Ifex', 'ICE', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>ICE</b>', 'Cairo MS. The use of ifosfamide, carboplatin, and etoposide in children with solid tumors. $Semin Oncol. 1995;22(3 suppl 7):23-27.$', null, '<b>Ifosfamide</b> 1,800 mg/m<sup>2</sup>/d IV over 1 hour, days 0-4<br> <b>Carboplatin</b> 400 mg/m<sup>2</sup>/d IV over 1 hour, days 0, 1<br> <b>Etoposide 100 mg/m<sup>2</sup>/d IV over 1 hour, days 0-4', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (313, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'VCR/Cyclophosphamide', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>VCR/Cyclophosphamide</b>', 'Grier HE, Krailo MD, Tarbell NJ, et al. Addition of ifosfamide and etoposide to standard chemotherapy for Ewing''s sarcoma and primitive neuroectodermal tumor of bone. $N Engl J Med. 2003;348(8):694-701.$', null, '<b>Vincristine</b> 2 mg/m<sup>2</sup> IV, day 1<br> <b>Doxorubicin</b> 75 mg/m<sup>2</sup> IV bolus, day 1<br> <b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (314, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Vincristine : Oncovin, VCR', 'P6', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>P6</b>', 'Kolb EA, Kushner BH, Gorlick R, et al. Long-term event-free survival after intensive chemotherapy for Ewing''s family of tumors in children and young adults. $J Clin Oncol. 2003;21(18):3423-3430.$', null, '<u>CYCLES 1, 2, 3 AND 6</u><br> <b>Cyclophosphamide</b> 2,100 mg/m<sup>2</sup>/d IV, days 1, 2<br> <b>Doxorubicin</b> 25 mg/m<sup>2</sup> CIVI, days 1-3<br> <b>Vincristine</b> 0.67 mg/m2 (maximum, 2 mg/cycle0 CIVI, days 1-3<br><br> <u>CYCLES 4, 5 AND 7</u><br> <b>Ifosfamide</b> 1,800 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV, days 1-5', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (330, 'Cytarabine : Ara-C', 'CA', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul></ul>', 'high', '<b>CA</b>', 'Woods WG, Ruymann FB, Lampkin BC, et al. The role of timing of high-dose cytosine arabinoside intensification and of maintenance therapy in the treatment of children with acute nonlymphocytic leukemia. $Cancer. 2006;66(6):1106-1113.$', null, '<b>Cytarabine</b> 3,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours for 4 doses<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> IM 3 hours after last dose of cytarabine<br> Repeat 28 days after initial cycle or after recovery of blood counts, whichever is later', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (315, 'Cyclophosphamide : Cytoxan<br>Topotecan : Hycamtin', 'Topo/CTX', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'High', '<b>Topo/CTX</b>', 'Saylors RL, Stewart CF, Zamboni WC, et al. Phase I study of topotecan in combination with cyclophosphamide in pediatric patients with malignant solid tumors: a Pediatric Oncolgy Group study. $J Clin Oncol. 1998;16(3):945-952.$', null, '<b>Cyclophosphamide</b> 250 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>FOLLOWED BY</b><br> <b>Topotecan</b> 0.6-0.75 mg/m<sup>2</sup>/d IV as an escalating dose, days 1-5', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (316, 'Cyclophosphamide : Cytoxan<br>Dactinomycin : Actinomycin<br>Vincristine : Oncovin, VCR', 'VAC', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Dactinomycin:<br>If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>VAC</b>', 'Crist WM, Anderson JR, Meza JL, et al. Intergroup rhabdomyosarcoma study-IV: results for patients with nonmetastatic disease. $J Clin Oncol. 2001;19(12):3091-3102.$', null, '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV weekly for 3 weeks<br> <b>Dactinomycin</b> 0.015 mg/kg/d (maximum, 0.5 mg/kg/d) IV for 5 days<br> <b>Cyclophosphamide</b> 2,200 mg/m<sup>2</sup> IV, day 1', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (317, 'Bleomycin : Blenoxane<br>Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban', 'ABVD', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'High', '<b>ABVD</b>', 'Fryer CJ, Hutchinson RJ, Krailo M, et al. Efficacy and toxicity of 12 courses of ABVD therapy followed by low-dose regional radiation in advanced Hodgkin''s disease in children: a report from the Children''s Cancer Study Group. $J Clin Oncol. 1990;8(12):1971-1980.$', null, '<b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Bleomycin</b> 10 units/m<sup>2</sup>/d IV, days 1, 15<br> <b>Vinblastine</b> 6 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Dacarbazine</b> 375 mg/m<sup>2</sup>/d IV, days 1, 15<br> Repeat every 14 days for 12 courses', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (318, 'Bleomycin : Blenoxane<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Procarbazine : Matulane<br>Vincristine : Oncovin, VCR', 'BEACOPP', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Procarbazine:<br>If CrCl < 30 mL/min, do not use</li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>BEACOPP</b>', 'Kelly KM, Hutchinson RJ, Sposto R, et al. Feasibility of upfront dose-intensive chemotherapy in children with advanced-stage Hodgkin''s lymphoma: preliminary results from the Children''s Cancer Group Study Ccg-59704. $Ann Oncol. 2002;13(suppl 1):107-111.', null, '<b>Bleomycin</b> 10 units/m<sup>2</sup> IV, day 7<br> <b>Etoposide</b> 200 mg/m<sup>2</sup>/d IV, days 0-2<br> <b>Doxorubicin</b> 35 mg/m<sup>2</sup> IV, day 0<br> <b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day 0<br> <b>Vincristine</b> 2 mg/m<sup>2</sup> IV, day 7<br> <b>Procarbazine</b> 100 mg/m<sup>2</sup>/d PO, days 0-6<br> <b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO, days 0-13<br> Repeat every 21 days', null);
