insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (331, 'Cytarabine : Ara-C', 'DA', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li></ul>', 'Moderate', '<b>DA</b>', 'Woods WG, Ruymann FB, Lampkin BC, et al. The role of timing of high-dose cytosine arabinoside intensification and of maintenance therapy in the treatment of children with acute nonlymphocytic leukemia. $Cancer. 2006;66(6):1106-1113.$', null, '<b>Daunorubicin</b> 45 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cytarabine</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-7', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (320, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'SNCCL/B-ALL', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>SNCCL/B-ALL</b>', 'Bowman WP, Shuster JJ, Cook B, et al. Improved survival for children with B-cell acute lymphoblastic leukemia and stage IV small noncleaved-cell lymphoma: a Pediatric Oncology Group study. $J Clin Oncol. 1996;14(4):1252-1261.$', null, '<u>INITIAL PHASE</u><br> <b>Cytarabine</b> 50 mg/m<sup>2</sup>/d (maximum, 50 mg/d) IT, days 1, 2, 3, 11<br> <b>Methotrexate</b> 12 mg/m<sup>2</sup>/d (maximum, 12 mg/d) IT, days 4, 11<br> <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> IV push every 12 hours for 6 doses, days 1-3<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> IV, day 4<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup>/d IV, days 4, 11<br> <b>ALTERNATING WITH</b><br><br> <u>SECONDARY PHASE</u><br> <b>Cytarabine</b> 50 mg/m<sup>2</sup> (maximum, 50 mg) IT, day 1<br> <b>Methotrexate</b> 12 mg/m<sup>2</sup> (maximum, 12 mg) IT, day 1<br> <b>Methotrexate</b> 200 mg/m<sup>2</sup> IV bolus,<br> <b>THEN</b> 800 mg/m<sup>2</sup> CIVI<br> <b>Cytarabine</b> 3,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours for 4 doses, days 2, 3<br> <b>Leucovorin calcium</b> 30 mg/m<sup>2</sup> IV at hour 42, then 3 mg/m<sup>2</sup> IV at hours 54, 66, 78', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (321, 'Dexamethasone : various<br>Prednisone : various<br>Vincristine : Oncovin, VCR', 'VCR/Asparaginase/Dex/MTX', '<b>Hepatic Impairment</b><ul><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate for MTX agent IT, minimal for others', '<b>VCR/Asparaginase/Dex/MTX</b>', 'Bostrom BC, Sensel MR, Sather HN, et al. Dexamethasone versus prednisone and daily oral versus weekly intravenous mercaptopurine for patients with standard-risk acute lymphoblastic leukemia: a report from the Children''s Cancer Group. $Blood 2003;101(10):3809-3817.$', null, '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 0,7, 14, 21<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> IM, 3 times weekly for 9 doses, starting on days 2-4<br> <b>Dexamethasone</b> 6 mg/m<sup>2</sup>/d PO in 3 equal doses (was substituted for prednisone)<br> <b>Methotrexate</b> 8, 10, or 12 mg IT, age-adjusted, days 0, 14 (also days 7, 21 for patients with CNS disease at diagnosis)<br> <b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (322, 'Cytarabine : Ara-C<br>Prednisone : various<br>Vincristine : Oncovin, VCR', 'VCR/Prednisone/Pegaspargase/Cytarabine/MTX', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate for MTX agent IT, minimal for others', '<b>VCR/Prednisone/Pegaspargase/Cytarabine/MTX</b>', 'Avramis VI, Sencer S, Periclou AP, et al. A randomized comparison of native Escherichia coli asparaginase and polyethylene glycol conjugated asparaginase for treatment of children with newly diagnosed standard-risk acute lymphoblastic leukemia: a Children''s Cancer Group Study. $Blood. 2002;99(6):1986-1994.$ $Erratum in: Blood. 2002; 100(6):1531.$', null, '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 0, 7, 14, 21<br> <b>Prednisone</b> 40 mg/m<sup>2</sup> PO, days 0-28, then 10-day taper<br> <b>Pegaspargase</b> 2,500 units/m<sup>2</sup> IM, day 3<br> <b>Cytarabine</b> 30, 50, or 70 mg IT, age-adjusted, day 0<br> <b>Methotrexate</b> 8, 10, <b>OR</b> 12 mg IT, age-adjusted, days 7, 28', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (323, '', 'Nelarabine', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Nelarabine</b>', 'Arranon [package insert]. $Research Triangle Park, NC: GlaxoSmithKline; 2011.$', null, '<b>Nelarabine</b> 650 mg/m<sup>2</sup>/d IV, over 1 hour for 5 consecutive days<br> Repeat every 21 days', null);
