insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (326, 'Cytarabine : Ara-C<br>Hydrocortisone : various', 'TIT (CNS Prophylaxis)', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul></ul>', 'Moderate', '<b>TIT (CNS Prophylaxis)</b>', 'Lauer SJ, Camitta BM, Leventhal BG, et al. Intensive alternating drug pairs for treatment of hgh-risk chidhood acute lymphoblastic leukemia. Apediatric Oncology Group pilot study. $Cancer. 1993;71(9):2854-2861.$', null, '<u>Regimen <%0B>Age 1y <%0B>Age 2y <%0B>Age>=3y</u><br> <b>Methotrexate</b> <%0B>10 mg <%0B>12.5 mg <%0B>15 mg<br> <b>Cytarabine</b> <%0B>20 mg <%0B>25 mg <%0B>30 mg<br> <b>Hydrocortisone</b> <%0B>10 mg <%0B>12.5 mg <%0B>15 mg<br> <b>Administer</b> IT on day 1, then at weeks 1, 2, 3, 6, 11, 16, 21, 26, 31, then every 12 weeks', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (327, '', 'IDMTX', '<b>Hepatic Impairment</b><ul><li>Leucovorin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Leucovorin:<br>No dose reduction</li></ul>', 'Week 1, high; week 2, minimal', '<b>IDMTX</b>', 'Mahoney DH, Shuster JJ, Nitschke R, et al. Intensification with intermediate-dose intravenous methotrexate is effectie therapy for children with lower-risk B-precursor acute lymphoblastic leukemia: a Pediatric Oncology Group study. $J Clin Oncol. 2000;18(6):1285-1294.$', null, '<u>WEEK 1</u><br> <b>Methotrexate</b> 1,000 mg/m<sup>2</sup> IV over 24 hours<br> <b>FOLLOWED BY</b><br> <b>Mercaptopurine</b> 1,000 mg/m<sup>2</sup> IV over 6 hours<br> <b>THEN</b> (48 hours after the start of Methotrexate)<br> <b>Leucovorin calcium</b> 5 mg/m<sup>2</sup> IV every 6 hours for at least 5 doses<br> <u>WEEK 2</u><br> <b>Methotrexate</b> 20 mg/m<sup>2</sup> IM, day 1<br> <b>Mercaptopurine</b> 50 mg/m<sup>2</sup>/d PO, days 1-7<br> Repeat 2-week cycle times 12', null);
insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (332, 'Cytarabine : Ara-C<br>Thioguanine : 6-TG', 'DAT', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Thioguanine:<br>If bilirubin > 5 mg/dL, do not use</li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li></ul>', 'Moderate', '<b>DAT</b>', 'Ravindranath Y, Steuber CP, Krischer J, et al. High-dose cytarabine for intensification of early therapy of childhood acute myeloid leukemia: a Pediatric Oncology Group study. $J Clin Oncol. 1991;9(4):572-580.$', null, '<b>Daunorubicin</b> 45 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cytarabine</b> 100 mg/m<sup>2</sup>/d IV, days 1-7<br> <b>Thioguanine</b> 100 mg/m<sup>2</sup>/d PO, days 1-7', null);
