insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (334, 'Azacitidine : Vidaza<br>Cytarabine : Ara-C<br>Etoposide : VP-16', 'HI-C DAZE', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>HI-C DAZE</b>', 'Hurwitz CA, Mounce KG, Grier HE. Treatment of patients with acute myelogenous leukemia: review of clinical trials of the past decade. $J Pediatr Hematol Oncol. 1995;17(3):185-197.$', null, '<b>Daunorubicin</b> 30 mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cytarabine</b> 3,000 mg/m<sup>2</sup>/d IV every 12 hours, days 1-4<br> <b>Etoposide</b> 200 mg/m<sup>2</sup>/d IV, days 1-3, 6-8<br> <b>Azacitidine</b> 150 mg/m<sup>2</sup>/d IV, days 3-5, 8-10', null);
