insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (378, 'Methotrexate (Otrexup; Rasuvo; Rheumatrex [DSC]; Trexall; Xatmep). Vinblastine (Vinblastine), Doxorubicin (Adriamycin), (Cisplatin (Platinum)', 'dose-dense MVAC (DDMVAC)', '<table cellspacing="0">
	<tbody>
		<tr>
			<td><strong>Myelotoxicity</strong></td>
			<td colspan="3">
			<ul>
				<li>Delay treatment cycle until the WBC count is &gt;3000/mm<sup>3</sup>&nbsp;and platelet count is &gt;90,000 mm<sup>3</sup>. Methotrexate and doxorubicin doses should be reduced by 33% in patients who have a nadir WBC &lt;2000/mm<sup>3</sup>.</li>
			</ul>
			</td>
		</tr>
