insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (544, 'Paclitaxel: Taxol<br>Carboplatin: Paraplatin', 'Paclitaxel/Carboplatin', '<p><strong>Hepatic Impairment</strong></p>

