insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (451, 'Carboplatin: Paraplatin<br>Paclitaxel: Taxol', 'Carboplatin/Paclitaxel', '<p><strong>Hepatic Impairment</strong></p>

