insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (583, 'Rucaparib: Rubraca', 'Rucaparib', '<h4>Hepatic impairment</h4>

