insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (585, 'Bleomycin: Blenoxane<br>Etoposide: VP-16<br>Cisplatin: CDDP', 'BEP (germ cell)', '<p><strong>Hepatic Impairment</strong></p>

