insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (710, '', 'Gemcitabine + Erlotinib', '', '', '', '', 'METASTATIC', '<p><strong>Gemcitabine:&nbsp;</strong>1,000 mg/m<sup>2</sup>&nbsp;IV weekly for 7 weeks, then 1-week rest; subsequent cycles 1,000 mg/m<sup>2</sup>&nbsp;IV weekly for 3 weeks with 1-week rest</p>

