insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (714, '', 'Olaparib', '', '', '', '', 'METASTATIC', '<p><strong>Olaparib:&nbsp;</strong>300 mg PO twice daily</p>

