insert into regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (716, '', 'Capecitabine', '', '', '', '', 'METASTATIC', '<p><strong>Capecitabine:&nbsp;</strong>1,250 mg/m<sup>2</sup>&nbsp;PO twice daily on days 1-14</p>

