insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1, 'Bevacizumab : Avastin', 'Bevacizumab/Irinotecan', '<b>Hepatic Impairment</b><ul><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Irinotecan:<br>No dose reduction </li></ul>', 'Moderate', '<b>Bevacizumab/Irinotecan</b>', 'Vredenburgh JJ, Desjardins A, Herndon JE, et al. Bevacizumab plus irinotecan in recurrent glioblastoma multiforme. $J Clin Oncol. 2007;25(30):4722-4279.$', null, '<b>Bevacizumab</b> 15 mg/kg IV<br> Repeat Every 21 Days<br> <b>Irinotecan</b> 350 mg/m<sup>2</sup> with enzyme-inducing antiepileptic drugs, days 1, 8, 22, 29<br> <b>OR</b><br> <b>Irinotecan</b> 125 mg/m<sup>2</sup> with non-enzyme-including antiepileptic drugs, IV, days 1, 8, 22, 29<br> Repeat every 6 weeks', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (2, 'Lomustine : CCNU<br>Procarbazine : Matulane<br>Vincristine : Oncovin, VCR', 'PCV', '<b>Hepatic Impairment</b><ul><li>Lomustine:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Lomustine:<br>If CrCl < 60 mL/min, do not use</li><li>Procarbazine:<br>If CrCl < 30 mL/min, do not use</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate-High', '<b>PCV</b>', 'Levin VA, Uhm JH, Jaeckle KA, et al. Phase III randomized study of postradiotherapy chemotherapy with alpha-difluromethylornithine- procarbazine, N-(2-chloroethyl)-N'' -cyclohexyl- N-nitrosuera, vincristine (DFMO-PCV) versus PCV for glioblastoma multiforme. $Clin Cancer Res.2000;6(10):3878-3884.$', null, '<b>Procarbazine</b> 60 mg/m<sup>2</sup>/d PO, days 8-21<br> <b>Lomustine</b> 110 mg/m<sup>2</sup> PO, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, days 8, 29<br> Repeat every 6 weeks upto 7 cycles ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (3, 'Temozolomide : Temodar', 'Temozolomide+RT', '<b>Hepatic Impairment</b><ul><li>Temozolomide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Moderate for IV<br> <=75 mg/m<sup>2</sup> PO is minimal-low;<br> >75 mg/m<sup>2</sup> PO is moderate-high   ', '<b>Temozolomide+RT</b>', 'Stupp R, et al.  Radiotherapy plus Concomitant and Adjuvant Temozolomide for Glioblastoma.$N Engl J Med 2005; 352:987-996$', null, '<b>Temozolomide</b> 75 mg/m<sup>2</sup> daily along with radiation<br> <b>FOLLOWED 28 DAYS LATER BY</b><br><b>Temozolomide<sup>e</sup></b> 150 mg/m<sup>2</sup> daily for 5 days<br>Repeat every 28 days.', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (4, 'Carmustine : BCNU', 'BCNU (Carmustine)', '<b>Hepatic Impairment</b><ul><li>Carmustine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carmustine:<br>If CrCl < 60 mL/min, do not use</li></ul>', 'High', '<b>BCNU (Carmustine)</b>', 'Selker RG, Sharipo WR, Burger p, et al. The Brain tumor cooperative group NIH Trial 87-01: a randomized comparision of surgery, external radiotherapy, and carmustine versus surgery, interstital radiotherapy, and carmustine. $Neurosurgery.2002;51(2):343-355; discussion 355-357.$', null, '<b>Carmustine</b> 200 mg/m<sup>2</sup> IV over 30-60 minutes<br> Repeat every 8 weeks ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (5, 'Bevacizumab : Avastin', 'Bevacizumab', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Bevacizumab</b>', 'Kreisl TN, Kim L, Moore K, et al. Phase II trial of single-agent bevacizumab plus irinotecan at tumor progression in recuurent glioblastoma. $J Clin Oncol. 2009;27(5):740-745.$                                                           Raizer JJ, Grimm S, et al. Aphase 2 trial of single-agent bevacizumab plus irinotecan at tumor progression in recurrent high- grade schedule for patients with recurrent high- grade gliomas. $Cancer.2010;116(22):597-5305.$', null, '<b>Bevacizumab</b> 10 mg/kg IV<br> Repeat every 14 days on a 28-day cycle<br><b>OR</b><br><b>Bevacizumab</b> 15 mg/kg IV<br> Repeat every 21 days ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (6, 'Temozolomide : Temodar', 'Temozolomide', '<b>Hepatic Impairment</b><ul><li>Temozolomide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Moderate for IV<br> >75 mg/m<sup>2</sup> PO is moderate-high   ', '<b>Temozolomide</b>', 'Gállego Pérez-Larraya J, et al.Temozolomide in elderly patients with newly diagnosed glioblastoma and poor performance status: an ANOCEF phase II trial.$J Clin Oncol. 2011 Aug 1;29(22):3050-5. doi: 10.1200/JCO.2011.34.8086. Epub 2011 Jun 27$', null, '<b>Temozolomide</b> 150 mg/m<sup>2</sup> every day for 5 days per 28-day cycle<br> ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (7, 'Carboplatin : Paraplatin<br>Cetuximab : Erbitux<br>Fluorouracil : 5-FU', 'Carboplatin/Fluorouracil/Cetuximab', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Moderate', '<b>Carboplatin/Fluorouracil/Cetuximab</b>', 'Vermorken JB, Mesia R, Rivera F, et al. Platinum-based chemotherapy plus cetuximab in head and neck cancer. $N Engl J Med. 2008;359(11):1116-1127.$', null, '<b>Carboplatin</b> AUC of 5, day 1<br> <b>Fluorouracil</b> 1,000 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days for a maximum of 6 cycles<br> <b>Cetuximab</b> 400 mg/m<sup>2</sup> IV load over 4 hours, day 1, cycle 1 <b>THEN</b> 250 mg/m<sup>2</sup> IV over 1 hour, every week.', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (9, 'Cetuximab : Erbitux<br>Cisplatin : CDDP<br>Fluorouracil : 5-FU', 'Cisplatin/Fluorouracil/Cetuximab', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'High', '<b>Cisplatin/Fluorouracil/Cetuximab</b>', 'Vermorken JB, Mesia R, Rivera F, et al. Platinum-based chemotherapy plus cetuximab in head and neck cancer. $N Engl J Med. 2008;359(11):1116-1127.$', null, '<b>Cisplatin</b> 100 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 1,000 mg/m<sup>2</sup>/d CIVI, days 1-4<br> Repeat every 21 days for a maximuum of 6 cycles<br> <b>Cetuximab</b> 400 mg/m<sup>2</sup> IV load over 4 hours, day 1, cycle 1. <b>THEN</b> 250 mg/m<sup>2</sup> IV over 1 hour, every week', null);
