insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (22, 'Paclitaxel : Taxol', 'Paclitaxel', '<b>Hepatic Impairment</b><ul><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Paclitaxel</b>', '$Ajani JA, et al. Semin Oncol 1995;22 (Suppl 6):35-40.$', null, '<b>Paclitaxel</b>250 mg/m<sup>2</sup> IV over 24 hours on day 1<br>Repeat cycle every 21 days. G-CSF support is recommended.', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (23, 'Cisplatin : CDDP<br>Docetaxel : Taxotere<br>Fluorouracil : 5-FU', 'DCF', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'High', '<b>DCF</b>', 'Van Custem E, Moiseyenko VM, Tjulandin S, et al. Phase III study of docetaxel and cisplatin plus florouracil compared with cisplatin and flouorouracil as first-line therapy for advanced gastric cancer: a report of the V325 Study Group. $J Clin Oncol. 2006;24(31):4991-4997$', null, '<b>Docetaxel</b> 75mg/m<sup>2</sup> IV over 1 hour, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV over 1-3 hours, day 1<br> <b>Fluorouracil</b> 750 mg/m<sup>2</sup>/d CIVI, days 1-5<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (24, 'Cisplatin : CDDP<br>Docetaxel : Taxotere', 'Docetaxel/Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Docetaxel:<br>No dose reduction </li></ul>', 'High', '<b>Docetaxel/Cisplatin</b>', 'Roth AD, Maibach R, Martinelli G, et al. Docetaxel (Taxotere)-cisplatin (TC): an effective drug combination in gastric carcinoma. $Ann Oncol. 2000;11(3):301-306$', null, '<b>Docetaxel</b> 85 mg/m<sup>2</sup> IV over 1 hour, day 1,<br> <b>FOLLOWED BY</b><br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV over 1 hour, day 1<br> Repeat every 21 days for a maximum of 8 cycles', null);
