insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (188, 'Docetaxel : Taxotere<br>Estramustine : Estracyte', 'Docetaxel/Estramustine', '<b>Hepatic Impairment</b><ul><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Estramustine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Docetaxel:<br>No dose reduction </li></ul>', 'Moderate-High', '<b>Docetaxel/Estramustine</b>', 'Petrylak DP, Tangen CM, Hussain MA, et al. Docetaxel and estramustine compared with mitoxantrone and prednisone for advanced refractory prostate cancer. $N Engl J Med. 2004;351(15):1513-1520.$', null, '<b>Docetaxel</b> 60 mg/m<sup>2</sup> IV, day 2<br><b>Estramustine</b> 280 mg PO tid, days 1-5<br> Repeat every 21 days until progression or to a maximum of 12 cycles ', null);
