insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (169, 'Cisplatin : CDDP<br>Fluorouracil : 5-FU', 'Cisplatin/Fluorouracil', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'High', '<b>Cisplatin/Fluorouracil</b>', 'Morris M, Eifel PJ, Lu J, et al. Pelvic radiation with concurrent chemoterapy compared with pelvic and para-aortic radiation for high-risk cervical cancer. $N Engl J Med. 1999;340(15):1137-1143.$<br> Whitney CW, Sausse W, Bundy BN, et al. Randomized commparison of fluorouuracil plus cisplatin versus hydroxyurea as an adjunct to radiation therapy in stage IIB-IVA carcinoma of the cervix with negative para-aortic lymph nodes: a Gynecologic Oncology Group and Southwest Oncology Group Study. $J Clin Oncol. 1999;17(5):1339-1348.$', null, '<b>Cisplatin</b> 75 mg/m<sup>2</sup> IV over 4 hours, day 1, given within 16 hours of the first dose of RT<br> <b>Fluorouracil</b> 4,000 mg/m<sup>2</sup> CIVI over 96 hours, days 2-5<br> Repeat every 21 days for 3 cycles<br> <b>OR</b><br> <b>Cisplatin</b> 50 mg/m<sup>2</sup>/d IV, days 1, 29, given 4 hours before the first dose of RT<br> <b>Fluorouracil</b> 1,000 mg/m<sup>2</sup>/d IV, days 2-5, 30-33', null);
