insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (170, 'Cisplatin : CDDP<br>Paclitaxel : Taxol', 'C-P', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'High', '<b>C-P</b>', 'Monk BJ, Sill MW, McMeekin DS, et al. Phase III trial of four cisplatin-containing doublet commbinations in stage IVB recurrent or persistent cervical carcinoma: A Gynecologic Group Study. $J Clin Oncol. 2009;27(28):4649-4655.$', null, '<b>Cisplatin</b> 50 mg/m2 IV, day 1<br> <b>Paclitaxel</b> 135 mg/m<sup>2</sup> CIVI over 24 hours<br> Repeat every 21 days for 6 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (171, 'Cisplatin : CDDP<br>Topotecan : Hycamtin', 'Cisplatin/Topotecan', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'High', '<b>Cisplatin/Topotecan</b>', 'Long HJ, Bundy BN, Grendys EC, et al. Randomized Phase III trial of cisplatin with or without topotecan in carcinoma of the uterine cervix: a Gynecologic Oncology Group study. $J Clin Oncol. 2005;23(21):4626-4633.$', null, '<b>Cisplatin</b> 50 mg/m<sup>2</sup> IV, day 1<br> <b>Topotecan</b> 0.75 mg/m<sup>2</sup>/d IV, days 1-3<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (172, 'Cisplatin : CDDP', 'Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'High', '<b>Cisplatin</b>', 'Keys HM, Bundy BN, Stehman FB, et al. Cisplatin, radiation, and adjuvant hysterectomy compared with radiation and adjuvant hysterectomy compared with radiation and adjuvant hysterectomy for bulky stage IB cervical carcinoma. $N Engl J Med. 1999;340(15):1154-1161.$', null, '<b>Cisplatin</b> 40 mg/m<sup>2</sup> IV once per week during RT for up to 6', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (177, 'Sorafenib : Nexavar', 'Sorafenib', '<b>Hepatic Impairment</b><ul><li>Sorafenib:<br>If Child-Pugh A or B, no dose reduction     <br>                                                 If Child-Pugh C, reduce dose by 50% </li></ul><b>Renal Impairment</b><ul><li>Sorafenib:<br>No dose reduction </li></ul>', 'Low', '<b>Sorafenib</b>', 'Escudier B, Eisen T, Stadler WM, et al. Sorafenib in advanced clear-cell renal-cell carcinoma. $N Engl J Med. 2007;356(2):125-134.$', null, '<b>Sorafenib</b> 400 mg PO bid', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (178, 'Sunitinib : Sutent', 'Sunitinib', '<b>Hepatic Impairment</b><ul><li>Sunitinib:<br>If Child-Pugh A or B, no dose reduction  <br>                                                    If Child-Pugh C, reduce dose by 50% </li></ul><b>Renal Impairment</b><ul><li>Sunitinib:<br>If CrCl > 42 mL/min, no dose reduction </li></ul>', 'Low', '<b>Sunitinib</b>', 'Motzer RJ, Hutson TE, Tomczak P, et al. Sunitinib versus interferon alfa in metastatic renal-cell carcinoma. $N Engl J Med. 2007;356(2):115-124$', null, '<b>Sunitinib malate</b> 50 mg PO daily for 28 days on<br> <b>FOLLOWED BY</b> 14 days off', null);
