insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (180, 'Carboplatin : Paraplatin<br>Gemcitabine : Gemzar, various', 'Gemcitabine/Carboplatin', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Moderate', '<b>Gemcitabine/Carboplatin</b>', 'Bamias A, Moulopoulos LA, Koutras A, et al. The combination of gemcitabine and carboplatin as first-line treatment in patients with advanced urothelial carcinoma. $Cancer. 2006;106(2):297-303.$', null, '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup> IV over 30 minutes, days 1 and 8<br> <b>Carboplatin</b> AUC of 5 IV over 1 hour, day 1<br> Repeat cycle every 21 days upto 6 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (181, 'Cisplatin : CDDP<br>Gemcitabine : Gemzar, various', 'Gemcitabine/Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'High', '<b>Gemcitabine/Cisplatin</b>', 'von der Maase H, Hansen SW, Roberts JT, et al. Gemcitabine and cisplatin versus methotrexate, vinblastine, doxorubicin, and ciplatin in advanced or metastatic bladder cancer: results of a large, randomized, mulinational, multicenter, phase III study. $J Clin Oncol. 2000;18(17):3068-3077.$', null, '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup>/d IV over 30-60 minutes, days 1,8,15<br> <b>Cisplatin</b> 70mg/m<sup>2</sup> IV, day 2<br> Repeat every 28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (261, '', 'Arsenic', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Moderate', '<b>Arsenic</b>', 'Trisenox [package insert]. $Frazer, PA: Cephalon; 2010.$', null, '<b>Arsenic trioxide</b> 0.15 mg/kg/d IV for 25 doses up to 5 weeks', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (182, 'Cisplatin : CDDP<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban', 'MVAC', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'High', '<b>MVAC</b>', 'von der Maase H, Hansen SW, Roberts JT, et al. Gemcitabine and cisplatin versus methotrexate, vinblastine, doxorubicin, and ciplatin in advanced or metastatic bladder cancer: results of a large, randomized, mulinational, multicenter, phase III study. $J Clin Oncol. 2000;18(17):3068-3077.$', null, '<b>Methotrexate</b> 30 mg/m<sup>2</sup>/d IV, days 1, 15, 22<br> <b>Vinblastine</b> 3 mg/m<sup>2</sup>/d IV, days 2, 15,22<br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV, day 2<br> <b>Cisplatin</b> 70 mg/m<sup>2</sup> IV, day 2<br> Repeat every 28 days', null);
