insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (183, 'Bleomycin : Blenoxane<br>Cisplatin : CDDP<br>Etoposide : VP-16', 'BEP', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>BEP</b>', 'Nichols CR, Catalano PJ, Crawford ED, et al. Randomized comparison of cisplatin and etoposide and either bleomycin or ifosfamide in treatment of advanced disseminated germ cell tumors: an Eastern Cooperative Oncology Group, jSouthwest Oncology Group, and Cancer and Leukemia Group B Study. $J Clin Oncol. 1998;16(4):1287-1293.$', null, '<b>Bleomycin</b> 30 units IV bolus, weekly for 12 weeks<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV over 30-60 minutes,days 1-5<br> <b>Cisplatin</b> 20 mg/m<sup>2</sup>/d IV over 30-60 minutes, days 1-5<br> Repeat every 21 days for 4 courses', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (184, 'Cisplatin : CDDP<br>Etoposide : VP-16', 'EP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>EP</b>', 'Motzer RJ, Sheinfeld J, Mazumdar M, et al. Etoposide and cisplatin adjuvant therapy for patients with pathologic stage II germ cell tumors. $J Clin Oncol. 1995;13(11):2700-2704.$', null, '<b>Etoposide</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-5<br> <b>Cisplatin</b> 20 mg/m<sup>2</sup>/d IV, days 1-5<br> Repeat after 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (185, 'Cisplatin : CDDP<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Paclitaxel : Taxol', 'TIP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min