insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (212, 'Paclitaxel : Taxol', 'Paclitaxel', '<b>Hepatic Impairment</b><ul><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Low', '<b>Paclitaxel</b>', 'Parkash S. Gill, et al. Paclitaxel Is Safe and Effective in the Treatment of Advanced AIDS-Related Kaposi''s Sarcoma.$http://jco.ascopubs.