insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (242, 'Rituximab : Rituxan', 'Rituximab Maintenance', '<b>Hepatic Impairment</b><ul><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Rituximab Maintenance</b>', 'van Oers NH, Van Glabbeke M, Glurgea L, et al. Rituximab maintenance treatment of relapsed/residual follicular Non Hodgikin''s lymphoma: Long-term outcome or EROTC 20891 phase III randomized intergroup study. $ JClin Oncol. 2010;28(17):2853-2858.$', null, '<b>Rituximab</b> 375 mg/m<sup>2</sup>/d IV, day 1<br> Repeat every 90 days until relapse or maximum of 2 years', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (214, 'Carboplatin : Paraplatin<br>paclitaxel : Taxol', 'Carbo-Tax', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Carbo-Tax</b>', 'Briasoulis E, Kalofonos H, Bafaloukos D, et al. Carboplatin plus paclitaxel in unknown primary Carcinoma: a Phase II Hellenic Cooperative Oncology Group Study. $J Clin Oncol. 2000; 18(17):3101-3107.$                                                      ', null, '<b>Carboplatin</b> AUC of 6 mg/ml/min IV over 30 minutes.<br><b>FOLLOWED  BY</b><br><b>Paclitaxel</b> 200mg/m<sup>2</sup>  IV over 3 hours, day 1<br><b> Filgrastim </b> 300 mcg/d SQ, days 5-12<br><b>OR</b> <br><b>PEG-Filgrastim</b>6 mg SQ on day 2.<br>Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (215, 'Carboplatin : Paraplatin<br>Docetaxel : Taxotere', 'Docetaxel/Carboplatin', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Docetaxel:<br>No dose reduction </li></ul>', 'Moderate', '<b>Docetaxel/Carboplatin</b>', 'Pentheroudakis G, Briasoulis E, Kalofonos HP, et al. Docetaxel and carboplatin combination chemotherapy as outpatient palliative therapy in carcinoma of unknown primary: a multicentre Hellenic Cooperative Oncology Group Phase II study. $Acta Oncol. 2008;47(6): 1148-1155.$', null, '<b>Docetaxel</b> 75 mg/m<sup>2</sup> IV over 30 minutes, day1<br><b>Carboplatin</b> AUC of 5 mg/mL/min IV over 30 minutes, day 1<br>Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (216, 'Carboplatin : Paraplatin<br>Gemcitabine : Gemzar, various<br>paclitaxel : Taxol', 'Gemcitabine/Carboplatin/paclitaxel', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li><li>paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Gemcitabine:<br>No dose reduction </li><li>paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Gemcitabine/Carboplatin/paclitaxel</b>', 'Greco FA, Burris HA III, Litchy S, et al. Gemcitabine, carboplatin, and paclitaxel for patients with carcinoma of unknown primary site: a Minnie pearl cancer center research Network study. J Clin Oncol.2002;20(6):1651-1656.                ', null, '<b>Gemcitabine</b> 1,000mg/m<sup>2</sup>  1V, days 1,8<br><b>Carboplatin</b> AUC of 5 IV, day 1<br><b>Paclitaxel</b> 200 mg/m2 IV over 1 hour, day 1<br>Repeat cycle every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (217, 'Cisplatin : CDDP<br>Gemcitabine : Gemzar, various', 'Gemcitabine/Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'High', '<b>Gemcitabine/Cisplatin</b>', 'Culine S, Lortholary A, Voigt JJ, et al. Gemcitabine, carboplatin combintion with either gemcitabine or irinotecan in carcinomas of unknown primary site: results of a randomized phase II study- trial for the French study Group on Carcinomas of Unknown Primary (GEFCAPI 01). $JClin Oncol. 2003;21(18):3479-3482.$', null, '<b>Gemcitabine</b> 1,250 mg/m<sup>2</sup> IV, days 1 and 8<br><b>Cisplatin</b> 100mg/m<sup>2</sup> IV, day 1<br>Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (218, 'Docetaxel : Taxotere<br>Gemcitabine : Gemzar, various', 'Gemcitabine/Docetaxel', '<b>Hepatic Impairment</b><ul><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Docetaxel:<br>No dose reduction </li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Low', '<b>Gemcitabine/Docetaxel</b>', 'Pouessel D, Culine S, Becht C, et al. Gemcitabine and docetaxel as front-line chemotherapy in patients with carcinoma of an unknown primary site. $Cancer. 2004; 100(6): 1257-1261.$', null, '<b>Gemcitabine</b> 1,000mg/m<sup>2</sup> IV over 30 minutes, days 1 and 8<br><b>Docetaxel</b> 75 mg/m<sup>2</sup>  IV over 1 hour, day 8<br>Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (219, 'Capecitabine : Xeloda<br>Oxaliplatin : Eloxatin, Oxaliplatin, various', 'Oxaliplatin Capecitabine', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Moderate', '<b>Oxaliplatin Capecitabine</b>', 'Hainsworth JD, Spigel DR, Burris HA, et al. Oxaliplatin and capecitabine inn the treatment of patients with recurrent or refractory carcinoma of unknown primary site: A phase 2 trial of the Sarah Cannon Oncology Research Consortium. $Cancer. 2010; 116(10):2448-2454.$', null, '<b>Oxaliplatin</b> 130 mg/m<sup>2</sup>  IV, day 1<br><b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14<br>Repeat cycle every 21 days upto 6 cycles                                                 ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (220, 'Carboplatin : Paraplatin<br>Etoposide : VP-16<br>Paclitaxel : Taxol', 'Paclitaxel/Carboplatin/Etoposide', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Paclitaxel/Carboplatin/Etoposide</b>', 'Hainsworth JD, Spigel DR, Litchy S, Greco FA. Phase II trial of paclitaxel, carboplatin, and etoposide in advanced poorly differentiated neutroendocrine carcinoma: a Minnie Pearl Cancer Network Study. $J Clin Oncol. 2006;24(22):3548-3554.$', null, '<b>Paclitaxel</b>  200mg/m<sup>2</sup> IV over 1 hour, day 1,<br><b>FOLLOWED BY</b><br> <b>Carboplatin</b> dose targeted by Calvert equation to AUC of 6 mg/mL/min IV, day 1<br><b>Etoposide</b> 50 mg/d PO alternated with 100mg/d PO, days 1-10<br> Repeat every 21 days', null);
