insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (221, 'Bleomycin : Blenoxane<br>Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban', 'ABVD', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'High', '<b>ABVD</b>', 'Harker WG, Kushlan P, Rosenberg SA. Combination chemotherapy for advanced Hodgkin''s disease after failure of MOPP: ABVD and B-CAVe. $Ann Intern Med. 1984;  101(4):440-446$.<br> Longo DL, Glastein E, Duffey PL,et al. Alternating MOPP and ABVD chemotherapy plus mantle-field radiation therapy in patients with massive mediastinal Hodgikin''s disease. $J Clin Oncol. 1997; 15(11): 3338-3346$.     ', null, '<b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Bleomycin</b> 10 units/m<sup>2</sup>/d IV, days 1, 15<br> <b>Vinblastine</b> 6 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Dacarbazine</b> 375 mg/m<sup>2</sup>/d IV, days 1, 15<br> Repeat every 28 days', null);
