insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (228, 'Prednisone : various<br>Rituximab : Rituxan<br>Vincristine : Oncovin, VCR', 'R - CVP', '<b>Hepatic Impairment</b><ul><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>R - CVP</b>', 'Marcus R, Imrie K, Belch A, et al. CVP chemotherapy plus rituximab compared with CVP as first-line treatment for advanced follicular lymphoma. $Blood, 105(4): 1417-1423.$<br> Marcus R, Imrie K, Solal-Celigny P, et al. Phase III study of R-CVP compared with cyclophosphamide, vincristine and prednisone alone in patients with previously utreated advanced follicular lymphoma. $J Clin Oncol. 2008;26(28):4578-4589.$', null, '<b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> up to a maximum dose of 2 mg IV, day 1<br> <b>Prednisone</b> 40 mg PO, days 1-5<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for a maximum off 8 cycles ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (229, 'Cisplatin : CDDP<br>Cytarabine : Ara-C<br>Dexamethasone : various', 'DHAP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li></ul>', 'High', '<b>DHAP</b>', 'Velasquez WS, Cabanillas F, Salvador P, et al. Effective salvage therapy for lymphoma with cisplatin in combination with high-dose Ara-C and dexamethasone (DHAP). $Blood. 1988;71(1):117-122.$', null, '<b>Dexamethasone</b> 40 mg/d PO or IV over 24 hours, days 1-4<br> <b>Cisplatin</b> 100 mg/m<sup>2</sup> CIVI, day 1<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 3 hours every 12 hours for 2 doses, day 2<br> Repeat every 21-28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (230, 'Cisplatin : CDDP<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Methylprednisolone : Depo-Medrol, Solu-Medrol, various', 'ESHAP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>ESHAP</b>', 'Velasquez WS, McLaughlin P, Tucker S, et al. ESHAP- an effective chemotherapy regimen in refractory and relapsing lymphoma: a 4-year follow-up study. $J Clin Oncol. 1994; 12(6): 1169-1176.$<br> Rodriguez MA, Cabanillas FC, Velasquez W, et al. Results of a salvage treatment program for relapsing lymphoma: MINE consolidated with ESHAP. $J Clin Oncol. 1995; 13(7): 1734-1741.$', null, '<b>Etoposide</b> 40 mg/m<sup>2</sup>/d IV, days 1-4<br> <b>Methylprednisolone</b> 500 mg/d IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 2-3 hours,             day 5<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>OR</b><br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d IV, days 1-4<br> <b>Methylprednisolone</b> 500 mg/d IV, days 1-4<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV over 2 hours, day 5, immediately following completion of cisplatin<br> Repeat every 21-28 days        ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (241, 'Pralatrexate : Folotyn', 'Pralatrexate', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Pralatrexate</b>', 'Folotyn [package insert]. $Westminster, CO: Allos Therapeutics Inc.; 2012.$', null, '<b>Pralatrexate</b> 30 mg/m<sup>2</sup> IV, weekly for 6 weeks<br> Repeat every 7 weeks', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (246, 'Vorinostat : Zolinza', 'Vorinostat', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Vorinostat</b>', 'Zolinza [package insert].', null, '<b>Vorinostat</b> 400mg PO daily with food', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (231, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Vincristine : Oncovin, VCR', 'Hyper-CVAD/MTX-Ara-C', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>Hyper-CVAD/MTX-Ara-C</b>', 'Khouri IF, Romaguera J, Kantrajian H,et al. Hyper-CVAD and high -dose methotrexate/ cytarabine followed by stem-cell tans-plantation: an active regimen for aggressive mantle-cell lymphoma. $J Clin Oncol. 1998; 16(12): 3803-3809$.', null, '<u>COURSE 1</u><br> <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> over 3 hours every 12 hours  for 6 doses , days 1-3<br> <b>Doxorubicin</b> 50 mg/m<sup>2</sup> over 48 hours after cyclophosphamide, days 4-5<br> <b>Vincristine</b> 2 mg administered 12 hours  after last cyclophosphamide dose, repeated on day 11<br> <b>Dexamethasone</b> 40 mg/d IV or PO,days 1-4,11-14<br> <b>Filgrastim</b> 5 mcg/kg IV or SQ beginning 24 hours after doxorubicin is complete.<br> <u>COURSE 2 <br>(BEGIN AFTER CLINICAL AND HEMATOLOGIC RECOVERY FROM COURSE 1)</u><br> <b>Methotrexate</b> 200 mg/m<sup>2</sup> IV bolus, day 1<br> <b>FOLLOWED BY</b><br> <b>Methotrexate</b> 800mg/m<sup>2</sup>over 24 hours day1 <b>Followed by</b><br><b>Cytarabine</b>3000 mg/m<sup>2</sup> IV every 12 hours for 4 doses, days 2-3 (dose reduced to 1,000 mg/m<sup>2</sup> in patients >60 years or with serum creatinine >1.5 mg/dL)<br> <b>Leucovorin calcium</b> 50 mg PO 24 hours after end of Methotrexate infusion<br> <b>FOLLOWED 6 HOURS LATER BY</b><br> <b>Leucovorin calcium</b> 15 mg PO every 6 hours for 8 doses (dose adjusted for Methotrexate concentration at 24 and 48 hours after completion of Methotrexate infusion)<br> <b>Filgrastim</b> 5mcg/kg IV or SQ begining 24 hours after cytarabine.<br> Repeat courses every 21 days.            ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (232, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Rituximab : Rituxan<br>Vincristine : Oncovin, VCR', 'R-EPOCH', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>R-EPOCH</b>', 'Jermann M, jost M, Taverna C, et al. Rituximab EPOCH, an effective salvage therapy for relapsed, refractory or transformed B-cell lymphomas: results of a phase II study. $Ann Oncol. 2004; 15(3):511-516$.', null, '<b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> <b>Etoposide</b> 65 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Doxorubicin</b> 15 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Vincristine</b> 0.5 mg/m<sup>2</sup>/d CIVI, days 2-4<br> <b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 5<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-14<br> Repeat every 21 days for 4 -6 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (233, 'Carboplatin : Paraplatin<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Rituximab : Rituxan', 'RICE', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Rituximab:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Moderate', '<b>RICE</b>', 'Kewalramani T, Zelenetz AD, Nimer SD, Nimer SD, et al. Rituximab and ICE as second-line therapy before autologous stem cell transplantation for relapsed or primary refractory diffuse large B-cell lymphoma. $Blood. 2004; 103(10):3684-3688.$', null, '<b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1 every 14 days<br> <b>Ifosfamide</b> 5,000 mg/m<sup>2</sup> with mesna 5,000 mg/m<sup>2</sup> CIVI over 24 hours, day 4<br> <b>Carboplatin</b> AUC of 5 (maximum 800 mg) IV, day 4<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d CIVI, days 3-5<br> Repeat every 14 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (234, 'Bendamustine : Treanda', 'Bendamustine', '<b>Hepatic Impairment</b><ul><li>Bendamustine:<br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin 1.5-3 upper normal limit, or bilirubin > 3 upper normal limit, do not use </li></ul><b>Renal Impairment</b><ul><li>Bendamustine:<br>If CrCl < 40 mL/min, do not use </li></ul>', 'Moderate', '<b>Bendamustine</b>', 'Kahl BS, Bartlett NL, Leonard JP, et al. Bendamustine is effective therapy in patients with rituximab-refractory, indolent B-cell non-Hodgikin lymphoma: results from a multicenter study. $Cancer. 2010,116(1):106-114 $.', null, '<b>Bendamustine</b> 120 mg/m<sup>2</sup> IV days 1 and 2<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (235, 'Bexarotene : Targretin', 'Bexarotene', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Bexarotene</b>', 'Targretin [package insert]. $Woodcliff Lake, NJ: Eisai Inc.; 2011.$', null, '<b>Bexarotene</b> 300 mg/m<sup>2</sup>/d PO with a meal', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (236, 'Bortezomib : Velcade', 'Bortezomib', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Low', '<b>Bortezomib</b>', 'Goy A, Bernstein SH, Kahl BS, et al. Bortezomib in patients with relapsed or refractory mantle cell lymphoma: updated time to event analyses of the multicenter Phase 2. PINNACLE study. $Ann Oncol. 2009;20(3):520-525.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup>/d IV bolus, days 1,4, 8, 11<br> Repeat every 21 days for up to 17 cycles.', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (237, '', 'Brentuximab vedotin', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Brentuximab vedotin</b>', 'Advani RH, Shustov AR, Brice P, et al.                                     Brentuximab vedotin (SGN-35) in patients with relapsed or refractory systemic anaplastic large cell lymphoma: A Phase 2 study update. $Blood (ASH Annula Meeting Abstracts). 2011; 118:443.$<br> Adcetris [package insert]. $Bothell, WA: Seattle Genetics; 2011.$', null, '<b>Brentuximab</b> 1.8 mg/kg IV over 30 minutes,  day 1<br> Repeat every 21 days for upto 16 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (238, '', 'Denileukin diftitox', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Denileukin diftitox</b>', 'Olsen E, Duvic M Frankel A, et al. Pivotal Phase III trial of two dose levels of denileukin diftitox for the treatment of cutaneous T-cell lymphoma. $J Clin Oncol. 2001; 19(2):376-388.$', null, '<b>Denileukin diftitox</b> 9 <b>OR</b> 18 mcg/kg/d/IV over 15-60 minutes, days 1-5<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (239, 'Fludarabine : Fludara', 'Fludarabine', '<b>Hepatic Impairment</b><ul><li>Fludarabine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Fludarabine:<br>If CrCl 30-70 mL/min, reduce dose by 20%      <br>                                                                  If CrCl < 30 mL/min, do not use </li></ul>', 'Low', '<b>Fludarabine</b>', 'Klasa RJ,Meyer RM, Shustic C, et al. Randomized phase III study of fludarabine phosphate versus cyclophosphamide, vincristine, and prednisione in patients with recurrent low-grade Non-Hodgikin''s lymphoma previously treated with an alkylating agent or alkylatic containing regimen. $J Clin Oncol. 2002;20(24):4649-4654.$', null, '<b>Fludarabine</b> 25 mg/m<sup>2</sup>/d IV over 10 minutes, days 1-5<br> Repeat every 28 days for up to 8 courses', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (247, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16', 'CDE', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Moderate', '<b>CDE</b>', 'Sparano JA,Lee S, Chen MG, et al. Phase II trial of   infusional, cyclophosphamide, doxorubicin, and etoposide in patients with HIV-associated non-Hodgkins lymphoma: an Eastern Cooperative Oncology Group Trial (E1491). $J Clin Oncol. 2004; 22(8): 1491-1500.$', null, '<b>Cyclophosphamide</b> 200mg/m<sup>2</sup>/d CIVI for 4 days<br><b>Doxorubicin</b> 12.5 mg/m<sup>2</sup>/d CIVI for 4 days<br> <b>Etoposide</b> 60 mg/m<sup>2</sup>/d CIVI for 4 days<br> Repeat every 28 days for a maximum of 8 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (248, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'CHOP', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>CHOP</b>', 'Weiss R, Mitrou P, Arasteh K, et.al. Acquired immunodeficiency syndrome-related lymphoma: Simultaneous treatment with combined cyclophosphamide, doxorubicin, Vincristine, and prednisone chemotherapy and highly active antiretroviral therapy is safe and improves survival--results of the German Multicenter Trial. $Cancer. 2006; 106(7): 1560-1568.$ ', null, '<b>Cyclophosphamide</b> 750mg/m<sup>2</sup> IV, day 1<br> Doxorubicin 50 mg/m<sup>2</sup> IV, day 1<br><b>Vincristine</b> 1.4 mg/m<sup>2</sup> IV, day 1<br> <b>Prednisone</b> 100mg/m<sup>2</sup> PO, days 1-5<br> Repeat every 21 days.', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (249, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR', 'CODOX-M/IVAC', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>CODOX-M/IVAC</b>', 'Wang ES, Strauss DJ, Teruya-Feldstein J, et al. Intensive chemotherapy with cyclophosphosphamide, doxorucibicin, high-dose methotrecate/ ifosfamide, etoposide, and high-dose cytarabine (CODOX-M/IVAC)for human immunodeficiency virus-associated Burkitt lymphoma. $Cancer. 2003;98(6): 1196-1205.$', null, '<u>CYCLES 1 and 3 (CODOX-M)</u><br>  <b>Cyclophosphamide</b> 800 mg/m<sup>2</sup> IV, day 1<br> <b>Cyclophosphamide</b> 200 mg/m<sup>2</sup>/d IV, days 2-5<br>  <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 1, 8 for cycle 1,days 1, 8, 15 for cycle 2<br> <b>Methotrexate</b> 1,200 mg/m<sup>2</sup> IV over 1 h, day 10, <b>THEN</b> 240 mg/m2/h CIVI for the next 23 hours<br> <b>Leucovorin calcium</b> 50 mg IV q6h, begining 36 hours from the start of Methotrexate until Methotrexate level <0.05 mcM<br> <b>Filgrastim</b> beginning 24 h from the start of leucovorin until ANC>1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Cytarabine</b> 70 mg IT, days 1, 3<br> <b>Methotrexate<b> 12 mg IT, day 15<br> <u>CYCLES 2 AND 4 (IVAC)</u><br> <b>Ifosofamide</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Mesna</b> 1,500 mg/m<sup>2</sup> IV, days 1-5<br> <b>Etoposide</b> 60 mg/m<sup>2</sup> IV, days 1-5<br> <b>Cytarabine</b> 2,000 mg/m<sup>2</sup> IV q 12h, days 1 and 2<br> <b>Filgrastim</b> beginning 24 h after completion of chemotherapy until ANC >1,000<br> <u>CNS PROPHYLAXIS</u><br> <b>Methotrexate</b> 12 mg IT, day 15<br> Additional IT Treatment for CNS disease- positive patients:<br> Cycle 1: cytarabine 70 mg IT, days 1,3,5, and Methotrexate 12 mg IT, days 15,17<br> Cycle 3: cytarabine 70 mg IT, days 1,3, and Methotrexate 12 mg IT, day 15                                                                                                                                                               ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (250, 'Cyclophosphamide : Cytoxan<br>Etoposide : VP-16<br>Prednisone : various<br>Vincristine : Oncovin, VCR', 'EPOCH Dose-adjusted ', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>EPOCH Dose-adjusted </b>', 'Little RF, Pittaluga S, Grant N, et al. Highly effective treatment of acquired immunodeficiency syndrome-related lymphoma with dose-adjusted EPOCH: impact of antiretroviral therapy suspension and tumor biology. $BLOOD.2003;101(12):4653-4659.$', null, '<b>Etoposide</b> 50 mg/m<sup>2</sup>/d CIVI, days 1-4 (96 hours)<br> <b>Doxorubicin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4 (96 hours)<br> <b>Vincristine</b> 0.4 mg/m<sup>2</sup>/d /CIVI, days 1-4 (96 hours)<br> <b>Cyclophosphamide</b> <br>Cycle1: <br>375 mg/m<sup>2</sup> IV, day 5, if CD4+ cells >= 100/mm<sup>3</sup>; 187 mg/m<sup>2</sup> IV, day 5, if CD4+ cells<100/mm<sup>3</sup><br> After cycle 1: increase dose by 187 mg if nadir ANC > 500/mvL or platelets <25,000/mcL<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-5<br> <b>Filgrastim</b> 5 mcg/kg/d SQ, day 6, and until ANC >5,000/mcl<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (259, '', 'Arsenic', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Moderate', '<b>Arsenic</b>', 'Trisenox [package insert]. $Frazer, PA: Cephalon; 2010.$', null, '<b>Arsenic trioxide</b> 0.15 mg/kg IV, daily until remission, not to exceed 60 doses', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (251, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Prednisone : various<br>Thioguanine : 6-TG<br>Vincristine : Oncovin, VCR', 'CALGB 8811', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Daunorubicin:<br>If bilurubin 1.5-3 mg/dL, reduce dose by 25% <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 50%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Thioguanine:<br>If bilirubin > 5 mg/dL, do not use</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Daunorubicin:<br>If Cr > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>CALGB 8811</b>', 'Larson RA, Dodge RK, Burns CP, et al. A five-drug remission induction regimen with intensive consolidation for adults with acute lymphoblastic leukemia: Cancer and Leukemia Group B study 8811. $Blood. 1995;85(8):2025-2037.$', null, '<u>INDUCTION (COURSE 1:4 WEEKS)</u><br> <b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day1 (800 mg/m<sup>2</sup> for patients >=60 years)<br> <b>Daunorubicin</b> 45 mg/m<sup>2</sup> IV, days 1-3 (30 mg/m<sup>2</sup> for patients>=60 years)<br> <b>Vincristine</b> 2 mg IV, days 1, 8, 15, 22<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-21 (days 1-7 for patients >=60 years)<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> SQ, days 5, 8, 11, 15, 18, 22<br> <u>EARLY INTENSIFICATION (COURSE 2:4 WEEKS, REPEAT ONCE)</u><br> <b>Methotrexate</b> 15 mg IT, day 1<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 1<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Cytarabine</b> 75 mg/m<sup>2</sup>/d SQ, days 1-4, 8-11<br> <b>Vincristine</b> 2 mg IV, days 15, 22<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> SQ, days 15, 18, 22, 25<br> <u>CNS PROPHYLAXIS AND INTERIM MAINTENANCE (COURSE 3:12 WEEKS)</u><br> <b>Cranial RT</b> 2,400 cGy, days 1-12<br> <b>Methotrexate</b> 15 mg IT, days 1, 8, 15, 22, 29<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-70<br> <b>Methotrexate</b> 20 mg/m<sup>2</sup> PO, days 36, 43, 50, 57, 64<br> <u>LATE INTENSIFICATION (COURSE 4:8 WEEKS)</u><br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV, days 1, 8, 15<br> <b>Vincristine</b> 2 mg IV, days 1, 8, 15<br> <b>Dexamethasone</b> 10 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Cyclophosphamide</b> 1,000 mg/m<sup>2</sup> IV, day 29<br> <b>Thioguanine</b> 60 mg/m<sup>2</sup>/d PO, days 29-42<br> <b>Cytarabine</b> 75 mg/m<sup>2</sup>/d SQ, days 29-32, 36-39<br> <u>MAINTENANCE (COURSE 5:4 WEEKS, FOR 2 YEARS FROM DIAGNOSIS)</u><br> <b>Vincristine</b> 2 mg IV, day 1 of every 4 weeks<br> <b>Prednisone</b> 60 mg/m<sup>2</sup>/d PO, days 1-5 of every 4 weeks<br> <b>Methotrexate</b> 20 mg/m<sup>2</sup> PO, days 1, 8, 15, 22<br> <b>Mercaptopurine</b> 60 mg/m<sup>2</sup>/d PO, days 1-28', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (252, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Mesna : Mesnex, various<br>Methylprednisolone : Depo-Medrol, Solu-Medrol, various<br>Vincristine : Oncovin, VCR', 'Hyper-CVAD', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Leucovorin:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Leucovorin:<br>No dose reduction</li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>Hyper-CVAD</b>', 'Kantarjian HM, O''Brien S, Smith TL, et al. Results of treatment with Hyper-CVAD, a dose-intensive regimen, in adult acute lymphocytic leukemia. $J Clin Oncol. 2000;18(3):547-561.$', null, '<u>ODD CYCLES (1, 3, 5, 7)</u><br>                            <b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> IV over 3 hours every 12 hours, days 1-3<br> <b>Mesna</b> 600 mg/m<sup>2</sup>/d CIVI, days 1-3, ending 6