insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (273, 'Ofatumumab : Arzerra', 'Ofatumumab', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Ofatumumab</b>', 'Arzerra [package insert]. <br> Wierda WG, Kipps TJ, Mayer J, et al. Ofatumumab as single agent CD20 immunotherapy in fluda-rabine-refractory chronic lymphocytic leukemia. $J Clin Oncol. 2010;28:1749-1755.$', null, '<b>Previously untreated CLL:</b><br>300 mg IV on Day 1 followed by 1,000 mg IV on Day 8 (Cycle 1)<br>1,000 mg IV on Day 1 subsequent 28-day cycles for a minimum of 3 cycles until best response or maximum of 12 cycles.<br><b>Refractory CLL:</b><br><b>Ofatumumab</b> 300 mg IV, initial dose,<br> <b>FOLLOWED 1 week later BY</b><br> <b>Ofatumumab</b> 2,000 mg IV, weekly dose for 7 weeks<br> Repeat every 28 days for 4 doses', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (274, 'Dasatinib : Sprycel', 'Dasatinib', '<b>Hepatic Impairment</b><ul><li>Dasatinib:<br>Unknown. Metabolism mainly hepatic </li></ul><b>Renal Impairment</b><ul><li>Dasatinib:<br>Unknown. < 4% excreted by kidney </li></ul>', 'Low', '<b>Dasatinib</b>', 'Kantarijan H, Shah NP, Hochhaus A, et al. Dasatinib versus imatinib in newly diagnosed chronic-phase chronic myeloid deukemia. $N Engl J Med. 2010;362(24):2260-2270.$', null, '<b>Dasatinib</b> 100 mg PO, daily (chronic phase)<br> <b>OR</b><br> <b>Dasatinib</b> 140 mg PO, daily (accelerated or blast phase)', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (275, 'Imatinib : Gleevec', 'Imatinib', '<b>Hepatic Impairment</b><ul><li>Imatinib:<br>If bilirubin < 3 upper normal limit, no dose reduction <br>                            If bilirubin > 3 upper normal limit, use with caution </li></ul><b>Renal Impairment</b><ul><li>Imatinib:<br>No dose reduction </li></ul>', 'Low', '<b>Imatinib</b>', 'Druker BJ, et al. Efficacy and safety of a specific inhibitor of the BCR-ABL tyrosine kinase in chronic myeloid leukemia.$N Engl J Med. 2001 Apr 5;344(14):1031-7.$', null, '<b>Imatinib mesylate</b> 400 mg PO daily <b>OR</b> 600 mg PO daily(accelarated phase / blast crisis) ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (276, 'Nilotinib : Tasigna', 'Nilotinib', '<b>Hepatic Impairment</b><ul><li>Nilotinib:<br>Unknown </li></ul><b>Renal Impairment</b><ul><li>Nilotinib:<br>Not tested. Not renally excreted </li></ul>', 'Low', '<b>Nilotinib</b>', 'Kantarjian HM, et al. Nilotinib versus imatinib for the treatment of patients with newly diagnosed chronic phase, Philadelphia chromosome-positive, chronic myeloid leukaemia: 24-month minimum follow-up of the phase 3 randomised ENESTnd trial.$Lancet Oncol. 2011 Sep;12(9):841-51. doi: 10.1016/S1470-2045(11)70201-7. Epub 2011 Aug 17$', null, '<b>Nilotinib</b> 300 mg PO bid  (newly diagnosed)<br> <b>OR</b><br> <b>Nilotinib</b> 400 mg PO bid (resistant or intolerant chronic or accelerated phase)', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (277, 'Cladribine : 2-CdA', 'Cladribine', '<b>Hepatic Impairment</b><ul><li>Cladribine:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Cladribine</b>', 'Saven A, et al. $Blood 1992;79:1111-1120.$', null, '<b>Cladribine:</b>0.09 mg/kg/day IV continuous infusion on days 1 to 7 <br>Administer one cycle', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (278, 'Pentostatin : Nipent', 'Pentostatin', '<b>Hepatic Impairment</b><ul><li>Pentostatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Pentostatin</b>', 'Cassileth PA, et al.$J Clin Oncol. 1991;9:243-246.$', null, '<b>Pentostatin:</b> 4 mg/m<sup>2</sup> IV on day 1 <br>Repeat cycle every 14 days for 6 cycles ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (280, 'Bortezomib : Velcade', 'Bortezomib/Doxil', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Low', '<b>Bortezomib/Doxil</b>', 'Doxil [package insert]. $Raritan, NJ: Centocor Ortho Biotech Products, LP; 2010.$<br> Orlowski RZ, Nagler A, Sonneveld P, et al. Randomized phase III study of pegylated liposomal doxorubicin plus bortezomib compared with bortezomib alone in relapsed or refractory multiple myeloma: combination therapy improves time to progression. $J clin Oncol. 2007;25(25):3892.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11<br> <b>Pegylated liposomal doxorubicin</b> 30 mg/m<sup>2</sup> IV over 1 hour, day 4<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (281, 'Bortezomib : Velcade<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various', 'Bortezomib/Doxorubicin/Dexamethasone', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'Moderate', '<b>Bortezomib/Doxorubicin/Dexamethasone</b>', 'Sonneveld P, van der Holt B, Schmidt-Wolf IGH, et al. First analysis of HOVON-65/GMMG-HD4 randomized phase III trial comparing bortezomib, adriamycin, dexamethasone (PAD) vs VAD as induction treatment prior to high dose melphalan (HDM) in patients with newly diagnosed multiple myeloma (MM). $Blood. 2008;112(11):Abstract 653.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11<br> <b>Doxorubicin</b> 9 mg/m<sup>2</sup> IV, bolus days 1-4<br> <b>Dexamethasone</b> 40 mg PO days 1-4, 9-12, 17-20', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (282, 'Bortezomib : Velcade<br>Thalidomide : Thalomid', 'Bortezomib/Thalidomide/Dexamethasone (VTD)', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Low', '<b>Bortezomib/Thalidomide/Dexamethasone (VTD)</b>', 'Cazo M, Tacchetti P, Patriarca F, et al. Bortezomib with thalidomide plus dexamethasone compared with thalidomide plus dexamethasone as induction therapy before, and consolidation therapy after, double autologous stem-cell transplantation in newly diagnosed multiple myeloma: a randomised phase 3 study. $Lancet. 2010;18;376(9758):2075-2085.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV bolus, days 1, 4, 8, and 11<br> <b>Thalidomide</b> 100 mg PO daily for 14 days and 200 mg daily thereafter<br> <b>Dexamethasone</b> 40 mg PO daily on days 1, 2, 4, 5, 8, 9, 11, and 12; total of 320 mg per cycle', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (283, 'Bortezomib : Velcade<br>Cyclophosphamide : Cytoxan<br>Dexamethasone : various', 'CyBorD', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Moderate-High', '<b>CyBorD</b>', 'Khan ML, Reeder CB, Kumar SK, et al. A comparison af lenalidomide/dexamethasone versus cyclophosphamide/lenalidomide/dexamethasone versus cyclophosphamide/bortezomib/dexamethansone in newly diagnosed multiple myeloma. $Br J Haematol. 2011 [Epub ahead of print].$', null, '<b>Cyclophosphamide</b> 300 mg/m<sup>2</sup> PO days 1, 8, 15, 22<br> <b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV on days 1, 4, 8, and 11<br> <b>Dexamethasone</b> 40 mg PO days 1-4, 9-12, and 17-20<br> Repeat every 28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (342, 'Tagrisso', 'Osimertinib', '', 'Minimal', '<b>Osimertinib</b>', 'Mitsudomi T, Tsai C, Shepherd F, et al. AZD9291 in pre-treated T790M positive advanced non-small cell lung carcinoma:AURA 2 phase II study. Presented at 16th world conference on lung cancer:September 6-9; Denverm CO. Abstract 1406. $', null, '<b>Osimertinib</b> 80 mg  PO daily<br> ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (284, 'Cisplatin : CDDP<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Thalidomide : Thalomid', 'DT-PACE', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>DT-PACE</b>', 'Lee CK, Barlogie B, Munshi N, et al. DTPACE: an effective, novel combination chemotherapy with thalidomide for previously treated patients with myeloma. $J Clin Oncol. 2003;21(14):2732-2739.$', null, '<b>Dexamethasone</b> 40 mg PO, days 1-4<br> <b> Thalidomide</b> 400 mg/d PO<br> <b>Cisplatin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Doxorubicin</b> 10 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Etoposide</b> 40 mg/m<sup>2</sup>/d CIVI, days 1-4', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (286, 'Melphalan : Alkeran<br>Prednisone : various', 'Melphalan/Prednisone (MP)', '<b>Hepatic Impairment</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction  <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Low', '<b>Melphalan/Prednisone (MP)</b>', ' $Southwest Oncology Group Study. Arch Intern Med 1975;135:147-152$', null, '<b>Melphalan</b> 8 - 10 mg per m<sup>2</sup>  on days 1 - 4<br> <b>Prednisone</b> 60 mg/m<sup>2</sup> days 1 - 4<br> Repeat cycle every 42 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (287, 'Melphalan : Alkeran<br>Prednisone : various', 'Melphalan/Prednisone/Talidomide (MPT)', '<b>Hepatic Impairment</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction  <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Low', '<b>Melphalan/Prednisone/Talidomide (MPT)</b>', ' $Palumbo A, et al. Lancet 2006;28:367-835.$', null, '<b>Melphalan</b> 0.25 mg/kg PO, daily for 5 days<br> <b>Prednisone</b> 1 mg/kg PO, daily for 5 days<br> Repeat every 28 days for 8 cycles<br> <b>Thalidomide 50 - 100 mg PO, daily ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (288, 'Bortezomib : Velcade<br>Dexamethasone : various<br>Lenalidomide : Revlimid', 'RVD', '<b>Hepatic Impairment</b><ul><li>Lenalidomide:<br>Unknown </li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Lenalidomide:<br>Substantially excreted by kidney. Trials excluded patients with renal insufficiency </li></ul>', 'Low', '<b>RVD</b>', 'Richardson PG, Weller E, Lonial S, et al. Lenalidomide, bortezomib, and dexamethasone combination therapy in patients with newly diagnosed multiple myeloma. $Blood. 2010;116(5):679-686.$', null, '<b>Lenalidomide</b> 25 mg/d PO, days 1-14<br> <b> Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11<br> <b>Dexamethasone</b> 20 mg/d PO days 1, 2, 4, 5, 8, 9, 11, 12 (cycles 1-4)<br> Repeat every 21 days for 8 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (289, 'Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'VAD', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', '<b>VAD</b>', 'Segeren CM, Sonneveld P, Holt V, et al. Vincristine, doxorubicin and dexamethasone (VAD) administered as rapid intravenous infusion for first-line treatment in untreated multiple myeloma. $Br J Haematol. 1999;105(1):127-130.$', null, '<b>Vincristine</b> 0.4 mg/d CIVI, days 1-4<br> <b>Doxorubicin</b> 9 mg/m<sup>2</sup>/d CIVI, days 1-4<br> <b>Dexamethasone</b> 40 mg/d PO, days 1-4, 9-12, 17-20<br> Repeat every 25 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (290, 'Bortezomib : Velcade<br>Melphalan : Alkeran<br>Prednisone : various', 'VMP', '<b>Hepatic Impairment</b><ul><li>Melphalan:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li><li>Melphalan:<br>PO: if CrCl < 30 mL/min, consider dose reduction  <br> IV: if BUN > 30 mg/dL, reduce dose by 50%</li></ul>', 'Low', '<b>VMP</b>', 'San Miguel JF, Schalg R, Khuageva NK, et al. MMY-3002 a phase 3 study comparing bortezomib-melphalan-prednisone (VMP) with melphalan-prednisone (MP) in newly diagnosed multiple myeloma. $Blood. 2007;110(11 pt 1): Abstract 78.$<br> San Miguel JF, Schlag R, Khuageva NK, et al. Bortezomib plus melphalan and prednisone for initial treatment of multiple myeloma. $N Engl J Med. 2008;359(9):906-917.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 4, 8, 11, 22, 25, 29, 32 cycles 1-4<br> <b>FOLLOWED BY</b><br> <b>Bortezomib</b> 1.3 mg/m<sup>2</sup> IV, bolus days 1, 8, 22, 29 cycles 5-9<br> <b>Melphalan</b> 9 mg/m<sup>2</sup> PO, days 1-4<br> <b>Prednisone</b> 60 mg/m<sup>2</sup> PO, days 1-4<br> Repeat every 6 weeks for 9 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (291, 'Bortezomib : Velcade', 'Bortezomib', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul><li>Bortezomib:<br>No dose reduction </li></ul>', 'Low', '<b>Bortezomib</b>', 'Richardson PG, Barlogie B, Berenson J, et al. A phase 2 study of bortezomib in relapsed, refractory myeloma. $N Engl J Med. 2003;348(26):2609-2617.$', null, '<b>Bortezomib</b> 1.3 mg/m<sup>2</sup>/d IV bolus, days 1, 4, 8, 11<br> Repeat every 21 days ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (292, 'Dexamethasone : various', 'Dexamethasone', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'N/A', '<b>Dexamethasone</b>', 'Alexanian R, Dimopoulos MA, Delasalle K, Barlogie B. Primary dexamethasone treatment of multiple myeloma. $Blood. 1992;80(4):887-890.$', null, '<b>Dexamethasone</b> 20 mg/m<sup>2</sup>/d PO, days 1-4, 9-12, 17-20<br> Repeat after 14-day rest period', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (293, 'Thalidomide : Thalomid', 'Thalidomide', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'Low', '<b>Thalidomide</b>', 'Singhal S, Mehta J, Desikan R, et al. Antitumor activity of thalidomide in refractory multiple myeloma. $N Engl J Med. 1999;341(21):1565-1571.$ $ Erratum in N Engl J Med. 2000;342(5):364.$', null, '<b>Thalidomide</b> 200 mg/d PO at bedtime (increased dose every 2 weeks for 6 weeks up to a dose of 800 mg/d)', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (294, 'Carfilzomib : Kyprolis', 'Carfilzomib', '<b>Hepatic Impairment</b><ul><li>Carfilzomib:<br>The safety, efficacy and pharmacokinetics of Carfilzomib have not been evaluated in patients with baseline hepatic impairment.<br> Patients with the following laboratory values were excluded from the KYPROLIS clinical trials: ALT/AST = 3 × upper limit of normal (ULN) and bilirubin = 2 × ULN</li></ul><b>Renal Impairment</b><ul><li>Carfilzomib:<br>No dose adjustment recommended</li></ul>', 'Low', '<b>Carfilzomib</b>', 'Siegel DS, et al. A phase 2 study of single-agent carfilzomib (PX-171-003-A1) in patients with relapsed and refractory multiple myeloma.$Blood. 2012 Oct 4;120(14):2817-25. doi: 10.1182/blood-2012-05-425934. Epub 2012 Jul 25$', null, '<b>Carfilzomib</b> 20 mg/m<sup>2</sup>, Days 1,2,8,9,15, and 16, followed by a 12 day rest period<br> Repeat cycle every 28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (295, 'Pomalidamide : Pomalyst', 'Pomalidamide', '<b>Hepatic Impairment</b><ul><li>Pomalidamide:<br>Avoid POMALYST in patients with serum bilirubin greater than 2.0 mg/dL and AST/ALTgreater than 3.0 x ULN.</li></ul><b>Renal Impairment</b><ul><li>Pomalidamide:<br>Avoid Pomalidamide in patients with a serum creatinine greater than 3.0 mg/dL.</li></ul>', 'Low', '<b>Pomalidamide</b>', 'Leleu X, et al. Pomalidomide plus low-dose dexamethasone is active and well tolerated in bortezomib and lenalidomide-refractory multiple myeloma: Intergroupe Francophone du Myélome 2009-02.$Blood. 2013 Mar 14;121(11):1968-75. doi: 10.1182/blood-2012-09-452375. Epub 2013 Jan 14$', null, '<b>Pomalidamide</b>4 mg PO daily, from Day 1-21 <br>Repeat cycle every 28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (296, 'Cisplatin : CDDP<br>Etoposide : VP-16', 'CDDP/VP-16', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>CDDP/VP-16</b>', 'Kovnar EH, Kellie SJ, Horowitz ME, et al. Preirradiation cisplatin and etoposide in the treatment of high-risk medulloblastoma and other malignant embryonal tumors of the central nervous system: a phase II study. $J Clin Oncol. 1990;8(2):330-336.$', null, '<b>Cisplatin</b> 90 mg/m<sup>2</sup> IV, day 1<br> <b>Etoposide</b> 150 mg/m<sup>2</sup>/d IV, days 3, 4<br> Repeat every 21 days for 4 courses', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (297, 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Etoposide : VP-16<br>Vincristine : Oncovin, VCR', 'COPE or "Baby Brain I"', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>COPE or "Baby Brain I"</b>', 'Duffner id, Horowitz ME, Krischer JP, et al. Postoperative chemotherapy and delayed radiation in children less than 3 years of age with malignant brain tumors. $N Engl J Med. 1993;328(24):1725-1731.', null, '<u>CYCLE A</u><br> <b>Vincristine</b> 0.065 mg/kg/d (maximum, 1.5 mg) IV, days 1, 8<br> <b>Cyclophosphamide</b> 65 mg/kg/d IV over 30 minutes, day 1<br> <u>CYCLE B</u><br> <b>Cisplatin</b> 4 mg/kg/d IV over 6 hours, day 1<br> <b>Etoposide</b> 6.5 mg/kg/d IV over 1 hour, days 3, 4<br> Regimens given in alternating 28-day cycles in the sequence AABAAB', null);
