insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (310, 'Cisplatin : CDDP<br>Doxorubicin : Adriamycin, various', 'MTX-CDDP/Adr', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Leucovorin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Leucovorin:<br>No dose reduction</li></ul>', 'High', '<b>MTX-CDDP/Adr</b>', 'Weiner MA, Harris MB, Lewis M, et al. Neoadjuvant high-dose methotrexate, cisplatin, and doxorubicin for the management of patients with nonmetastatic osteosarcoma. $Cancer Treat Rep. 1986;70(12):1431-1432.$', null, '<b>Methotrexate</b> 12,000 mg/m<sup>2</sup> IV weekly for 2 weeks; escalate dose by 1,000 mg/m<sup>2</sup> in subsequent courses until peak level of 10-8 M is reached (reduce dose to 8,000 mg/m<sup>2</sup> IV for patients >12 years)<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup> IV every 3 hours for 8 doses beginning 16 hours after completion of methotrexate, <b>THEN</b> every 6 hours PO for 8 doses<br> <b>ALTERNATING WITH</b><br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 15 of cycles 1-7,<br> <b>THEN</b> 120 mg/m<sup>2</sup> for cycles 8-10<br> <b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 15-17 of cycles 1-7', null);
