insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (602, '', 'Oxaliplatin + 5-Fluorouracil + Leucovorin (FOLFOX6)', '', '', '', '', 'METASTATIC', '<p><strong>Oxaliplatin:&nbsp;</strong>100 mg/m<sup>2</sup>&nbsp;IV on day 1</p>

