insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (393, 'Enfortumab vedotin (Padcev) ', 'Enfortumab vedoitin monotherapy', '<table align="center" border="1" cellpadding="1" cellspacing="1" style="width:100%">
